    48â†’	floorPointsGeom.setAttribute('position', new THREE.Float32BufferAttribute(new Float32Array(maxLines * 3), 3));
    49â†’	const pointsMat = new THREE.PointsMaterial({
    50â†’		color: 0xffffff,
    51â†’		size: 2.2,
    52â†’		sizeAttenuation: true,
    53â†’		transparent: true,
    54â†’		opacity: 0.85,
    55â†’		blending: THREE.AdditiveBlending,
    56â†’		depthWrite: false,
    57â†’		depthTest: true
    58â†’	});
    59â†’	floorPoints = new THREE.Points(floorPointsGeom, pointsMat);
    60â†’	floorPoints.renderOrder = 11;
    61â†’	sceneHead.add(floorPoints);
    62â†’}
    63â†’
    64â†’export function updateProjectionLines(time, px = 0, py = 0) {
    65â†’    if (!projectionGeom || !projectionSamples || projectionSamples.length === 0) return;
    66â†’    const arr = projectionGeom.attributes.position.array;
    67â†’    const tmp = new THREE.Vector3();
    68â†’    const tmpWorld = new THREE.Vector3();
    69â†’    const dir = new THREE.Vector3();
    70â†’    const flow = new THREE.Vector2(px * 0.7, py * 0.7);
    71â†’    const baseLen = 20;
    72â†’    const amp = 35;
    73â†’    const speed = 0.8;
    74â†’	// gather unique floor endpoints keyed by snapped grid position
    75â†’	const unique = new Map();
    76â†’    for (let i = 0; i < projectionSamples.length; i++) {
    77â†’        const { mesh, local } = projectionSamples[i];
    78â†’        tmp.copy(local);
    79â†’        tmpWorld.copy(tmp);
    80â†’        mesh.localToWorld(tmpWorld);
    81â†’        const vx = tmpWorld.x;
    82â†’        const vy = tmpWorld.y;
    83â†’        const vz = tmpWorld.z;
    84â†’        const fx = vx;
    85â†’        const fz = vz;
    86â†’        const len = baseLen + amp * (0.5 + 0.5 * Math.sin(time * speed + i * 0.23));
    87â†’        dir.set(vx, 0, vz).normalize();
    88â†’        dir.x += flow.x * 0.6;
    89â†’        dir.z += flow.y * 0.6;
    90â†’        dir.normalize();
    91â†’        let ex = fx + dir.x * len;
    92â†’        let ez = fz + dir.z * len;
    93â†’        const snap = 20;
    94â†’        ex = Math.round(ex / snap) * snap;
    95â†’        ez = Math.round(ez / snap) * snap;
    96â†’        ex = Math.max(-200, Math.min(200, ex));
    97â†’        ez = Math.max(-200, Math.min(200, ez));
    98â†’        const idx = i * 6;
    99â†’        arr[idx + 0] = vx;
   100â†’        arr[idx + 1] = vy;
   101â†’        arr[idx + 2] = vz;
   102â†’        arr[idx + 3] = ex;
   103â†’        arr[idx + 4] = floorY;
   104â†’        arr[idx + 5] = ez;
   105â†’		const key = ex + ',' + ez;
   106â†’		if (!unique.has(key)) unique.set(key, { x: ex, z: ez });
   107â†’    }
   108â†’    projectionGeom.attributes.position.needsUpdate = true;
   109â†’
   110â†’	if (floorPointsGeom) {
   111â†’		const parr = floorPointsGeom.attributes.position.array;
   112â†’		let p = 0;
   113â†’		for (const v of unique.values()) {
   114â†’			parr[p++] = v.x;
   115â†’			parr[p++] = floorY;
