   120â†’		floorPointsGeom.attributes.position.needsUpdate = true;
   121â†’	}
   122â†’}
   123â†’
   124â†’export function loadModel(onComplete) {
   125â†’    const loader = new GLTFLoader();
   126â†’    loader.load('public/face.glb', (gltf) => {
   127â†’        const model = gltf.scene;
