   134â†’            for (const m of mats) {
   135â†’                if (!m) continue;
   136â†’                if (m.isMeshStandardMaterial || m.isMeshPhysicalMaterial) {
   137â†’                    m.color.set(0xf5f2ed);
   138â†’                    m.emissive.set(0xffffff);
   139â†’                    m.emissiveIntensity = 0.08;
   140â†’                    m.metalness = 0.08;
   141â†’                    m.roughness = 0.4;
   142â†’                    m.envMapIntensity = 0.08;
   143â†’                    if (envMapTexture) m.envMap = envMapTexture;
   144â†’                    m.transparent = false;
   145â†’                    m.opacity = 1;
   146â†’                    m.depthWrite = true;
   147â†’                    m.depthTest = true;
   148â†’                    m.alphaTest = 0.0;
   149â†’                    m.side = THREE.FrontSide;
   150â†’                    m.onBeforeCompile = (shader) => {
   151â†’                        shader.uniforms.uFresnelColor = { value: new THREE.Color(0xffffff) };
   152â†’                        shader.uniforms.uFresnelPower = { value: 1.8 };
   153â†’                        shader.uniforms.uFresnelIntensity = { value: 0.25 };
   154â†’                        shader.fragmentShader = shader.fragmentShader.replace(
