import * as THREE from 'three';
import { GLTFLoader } from 'three/addons/loaders/GLTFLoader.js';
import { camera, sceneHead, outlinePass, envMapTexture, floorY } from './scene.js';

export let headModel = null;
export let orbitRadius = 3;
export let modelSphereRadius = 0;
export let cameraBaseZ = 0;
export let cameraBaseY = 0;
export let cameraTargetY = 0;
let projectionLines = null;
let projectionGeom = null;
let projectionSamples = [];
let floorPointsGeom = null;
let floorPoints = null;

function initProjectionLines(root) {
    projectionSamples = [];
    root.traverse((obj) => {
        if (!obj.isMesh || !obj.geometry || !obj.geometry.attributes || !obj.geometry.attributes.position) return;
        const posAttr = obj.geometry.attributes.position;
        const count = posAttr.count;
        const stride = Math.max(10, Math.floor(count / 500));
        for (let i = 0; i < count; i += stride) {
            const lx = posAttr.getX(i);
            const ly = posAttr.getY(i);
            const lz = posAttr.getZ(i);
            projectionSamples.push({ mesh: obj, local: new THREE.Vector3(lx, ly, lz) });
        }
    });
    const maxLines = projectionSamples.length;
    projectionGeom = new THREE.BufferGeometry();
    projectionGeom.setAttribute('position', new THREE.Float32BufferAttribute(new Float32Array(maxLines * 2 * 3), 3));
    const material = new THREE.LineBasicMaterial({
        color: 0xffffff,
        transparent: true,
        opacity: 0.18,
        blending: THREE.AdditiveBlending,
        depthWrite: false,
        depthTest: true
    });
    projectionLines = new THREE.LineSegments(projectionGeom, material);
    projectionLines.renderOrder = 10;
    sceneHead.add(projectionLines);

	// floor intersection points (endpoints), preallocate to maxLines
	floorPointsGeom = new THREE.BufferGeometry();
	floorPointsGeom.setAttribute('position', new THREE.Float32BufferAttribute(new Float32Array(maxLines * 3), 3));
	const pointsMat = new THREE.PointsMaterial({
		color: 0xffffff,
		size: 2.2,
		sizeAttenuation: true,
		transparent: true,
		opacity: 0.85,
		blending: THREE.AdditiveBlending,
		depthWrite: false,
		depthTest: true
	});
	floorPoints = new THREE.Points(floorPointsGeom, pointsMat);
	floorPoints.renderOrder = 11;
	sceneHead.add(floorPoints);
}

export function updateProjectionLines(time, px = 0, py = 0) {
    if (!projectionGeom || !projectionSamples || projectionSamples.length === 0) return;
    const arr = projectionGeom.attributes.position.array;
    const tmp = new THREE.Vector3();
    const tmpWorld = new THREE.Vector3();
    const dir = new THREE.Vector3();
    const flow = new THREE.Vector2(px * 0.7, py * 0.7);
    const baseLen = 20;
    const amp = 35;
    const speed = 0.8;
	// gather unique floor endpoints keyed by snapped grid position
	const unique = new Map();
    for (let i = 0; i < projectionSamples.length; i++) {
        const { mesh, local } = projectionSamples[i];
        tmp.copy(local);
        tmpWorld.copy(tmp);
        mesh.localToWorld(tmpWorld);
        const vx = tmpWorld.x;
        const vy = tmpWorld.y;
        const vz = tmpWorld.z;
        const fx = vx;
        const fz = vz;
        const len = baseLen + amp * (0.5 + 0.5 * Math.sin(time * speed + i * 0.23));
        dir.set(vx, 0, vz).normalize();
        dir.x += flow.x * 0.6;
        dir.z += flow.y * 0.6;
        dir.normalize();
        let ex = fx + dir.x * len;
        let ez = fz + dir.z * len;
        const snap = 20;
        ex = Math.round(ex / snap) * snap;
        ez = Math.round(ez / snap) * snap;
        ex = Math.max(-200, Math.min(200, ex));
        ez = Math.max(-200, Math.min(200, ez));
        const idx = i * 6;
        arr[idx + 0] = vx;
        arr[idx + 1] = vy;
        arr[idx + 2] = vz;
        arr[idx + 3] = ex;
        arr[idx + 4] = floorY;
        arr[idx + 5] = ez;
		const key = ex + ',' + ez;
		if (!unique.has(key)) unique.set(key, { x: ex, z: ez });
    }
    projectionGeom.attributes.position.needsUpdate = true;

	if (floorPointsGeom) {
		const parr = floorPointsGeom.attributes.position.array;
		let p = 0;
		for (const v of unique.values()) {
			parr[p++] = v.x;
			parr[p++] = floorY;
			parr[p++] = v.z;
			if (p >= parr.length) break;
		}
		floorPointsGeom.setDrawRange(0, Math.floor(p / 3));
		floorPointsGeom.attributes.position.needsUpdate = true;
	}
}

export function loadModel(onComplete) {
    const loader = new GLTFLoader();
    loader.load('public/face.glb', (gltf) => {
        const model = gltf.scene;
        model.traverse((obj) => {
            if (obj.userData && obj.userData.__digitalOverlay) return;
            if (!obj.isMesh) return;
            obj.castShadow = true;
            obj.receiveShadow = true;
            const mats = Array.isArray(obj.material) ? obj.material : [obj.material];
            for (const m of mats) {
                if (!m) continue;
                if (m.isMeshStandardMaterial || m.isMeshPhysicalMaterial) {
                    m.color.set(0xf5f2ed);
                    m.emissive.set(0xffffff);
                    m.emissiveIntensity = 0.08;
                    m.metalness = 0.08;
                    m.roughness = 0.4;
                    m.envMapIntensity = 0.08;
                    if (envMapTexture) m.envMap = envMapTexture;
                    m.transparent = false;
                    m.opacity = 1;
                    m.depthWrite = true;
                    m.depthTest = true;
                    m.alphaTest = 0.0;
                    m.side = THREE.FrontSide;
                    m.onBeforeCompile = (shader) => {
                        shader.uniforms.uFresnelColor = { value: new THREE.Color(0xffffff) };
                        shader.uniforms.uFresnelPower = { value: 1.8 };
                        shader.uniforms.uFresnelIntensity = { value: 0.25 };
                        shader.fragmentShader = shader.fragmentShader.replace(
                            '#include <output_fragment>',
                            `
                            float fresnel = pow(1.0 - dot(normalize(vNormal), normalize(vViewPosition)), uFresnelPower);
                            vec3 fres = uFresnelColor * fresnel * uFresnelIntensity;
                            gl_FragColor = vec4( outgoingLight + fres, diffuseColor.a );
                            `
                        );
                    };
                    m.needsUpdate = true;
                }
            }

            if (obj.geometry && !obj.userData.__hasOverlay) {
                const overlayMaterial = new THREE.MeshBasicMaterial({
                    color: 0xffffff,
                    wireframe: true,
                    transparent: true,
                    opacity: 0.045,
                    depthWrite: false
                });
                const overlayMesh = new THREE.Mesh(obj.geometry, overlayMaterial);
                overlayMesh.renderOrder = (obj.renderOrder || 0) + 1;
                overlayMesh.userData.__digitalOverlay = true;
                obj.userData.__hasOverlay = true;
                obj.add(overlayMesh);
            }
        });

        model.scale.set(3, 3, 3);
        model.updateMatrixWorld(true);

        const box = new THREE.Box3().setFromObject(model);
        const center = box.getCenter(new THREE.Vector3());
        const size = box.getSize(new THREE.Vector3());
        const maxDim = Math.max(size.x, size.y, size.z);

        const headGroup = new THREE.Group();
        headGroup.position.set(0, 0, 0);

        model.position.x = -center.x;
        model.position.y = -center.y;
        model.position.z = -center.z;
        model.rotation.x = 0;
        model.updateMatrixWorld(true);

        headGroup.add(model);
        sceneHead.add(headGroup);
        headModel = headGroup;
        if (outlinePass) outlinePass.selectedObjects = [headGroup];

        const boxCentered = new THREE.Box3().setFromObject(model);
        const sphere = new THREE.Sphere();
        boxCentered.getBoundingSphere(sphere);
        modelSphereRadius = sphere.radius;

        const fovRad = camera.fov * Math.PI / 180;
        const distance = modelSphereRadius / Math.tan(fovRad / 2);
        const idealZ = distance * 1.2;

        orbitRadius = idealZ;
        camera.near = Math.max(0.01, maxDim / 100);
        camera.far = Math.max(500, maxDim * 50);

        applyResponsiveCamera();

        if (onComplete) onComplete();
    });
}

export function applyResponsiveCamera() {
    if (!camera || !orbitRadius) return;
    const isMobile = (window.innerWidth || 0) <= 800;
    const baseFovRad = camera.fov * Math.PI / 180;
    // Recompute baseline distance from current fov and sphere radius (fallback to orbitRadius)
    const baseDistance = modelSphereRadius > 0 ? (modelSphereRadius / Math.tan(baseFovRad / 2)) * 1.2 : orbitRadius;
    const zMult = isMobile ? 1.5 : 1.0; // closer on mobile
    const finalZ = baseDistance * zMult;
    const y = finalZ * (isMobile ? 0.44 : 0.15); // slightly lower camera to drop the face
    const targetY = isMobile ? (modelSphereRadius * 1.1) : 0; // look a bit higher so face sits lower
    cameraBaseZ = finalZ;
    cameraBaseY = y;
    cameraTargetY = targetY;
    camera.position.set(0, cameraBaseY, cameraBaseZ);
    camera.up.set(0, 1, 0);
    camera.lookAt(0, cameraTargetY, 0);
    camera.updateProjectionMatrix();
}

