import * as THREE from 'three';

export function getScoreColorRGB(score) {
    if (typeof score !== 'number' || score === null || score === undefined) {
        return 'rgb(37, 99, 235)';
    }
    const clamp = Math.max(1, Math.min(10, score));
    const stops = [
        { t: 1, c: [185, 28, 28] },
        { t: 2, c: [220, 53, 53] },
        { t: 3, c: [239, 88, 88] },
        { t: 4, c: [245, 130, 75] },
        { t: 5, c: [251, 175, 60] },
        { t: 6, c: [220, 200, 80] },
        { t: 7, c: [140, 215, 100] },
        { t: 8, c: [74, 222, 128] },
        { t: 9, c: [40, 205, 170] },
        { t: 10, c: [6, 182, 212] }
    ];
    let out = stops[stops.length - 1].c;
    for (let i = 0; i < stops.length - 1; i++) {
        const a = stops[i], b = stops[i + 1];
        if (clamp >= a.t && clamp <= b.t) {
            const f = (clamp - a.t) / (b.t - a.t);
            out = [
                Math.round(a.c[0] + f * (b.c[0] - a.c[0])),
                Math.round(a.c[1] + f * (b.c[1] - a.c[1])),
                Math.round(a.c[2] + f * (b.c[2] - a.c[2]))
            ];
            break;
        }
    }
    return `rgb(${out[0]}, ${out[1]}, ${out[2]})`;
}

const _tmpVec3 = new THREE.Vector3();

export function createVerticalGradientTexture(topHex, bottomHex) {
    const canvas = document.createElement('canvas');
    canvas.width = 1;
    canvas.height = 256;
    const ctx = canvas.getContext('2d');
    const grad = ctx.createLinearGradient(0, 0, 0, 256);
    grad.addColorStop(0, topHex);
    grad.addColorStop(1, bottomHex);
    ctx.fillStyle = grad;
    ctx.fillRect(0, 0, 1, 256);
    const tex = new THREE.CanvasTexture(canvas);
    tex.wrapS = THREE.ClampToEdgeWrapping;
    tex.wrapT = THREE.ClampToEdgeWrapping;
    tex.magFilter = THREE.LinearFilter;
    tex.minFilter = THREE.LinearMipmapLinearFilter;
    return tex;
}

export function worldToScreen(position, headModel, camera, w, h) {
    const vector = _tmpVec3.set(position.x, position.y, position.z);

    if (headModel) {
        vector.applyMatrix4(headModel.matrixWorld);
    }

    vector.project(camera);

    let width = w;
    let height = h;
    if (width == null || height == null) {
        const host = document.getElementById('head3d');
        const rect = host?.getBoundingClientRect();
        width = rect?.width || window.innerWidth * 0.5;
        height = rect?.height || window.innerHeight;
    }

    const x = (vector.x + 1) * width / 2;
    const y = -(vector.y - 1) * height / 2;

    return { x, y, z: vector.z };
}

