    if (!sceneRef.current) return;

    const {
      scene,
      camera,
      composer,
      headModel,
      roomGroup,
      outlinePass,
      bloomPass,
      rgbShiftPass,
      pointer,
      pointerTarget,
      cameraBaseZ,
      cameraBaseY,
      cameraTargetY,
      gridColorsAttr
