// Load 3D Model
const loader = new GLTFLoader();
loader.load('/face.glb', (gltf) => {
  const model = gltf.scene;

  console.log('Model loaded:', model);

  // Apply materials - handle both MeshStandardMaterial and MeshPhysicalMaterial
  model.traverse((obj) => {
    if ((obj as THREE.Mesh).isMesh) {
      const mesh = obj as THREE.Mesh;
      const materials = Array.isArray(mesh.material) ? mesh.material : [mesh.material];

      materials.forEach((m, idx) => {
        if (m) {
          // Create a new standard material for consistent appearance
          const newMat = new THREE.MeshStandardMaterial({
            color: 0xf5f2ed,
            emissive: 0xffffff,
            emissiveIntensity: 0.08,
            metalness: 0.08,
            roughness: 0.4,
            side: THREE.FrontSide
          });

          if (Array.isArray(mesh.material)) {
            mesh.material[idx] = newMat;
          } else {
            mesh.material = newMat;
          }
        }
      });

      // Add wireframe overlay
      if (mesh.geometry) {
        const overlayMaterial = new THREE.MeshBasicMaterial({
          color: 0xffffff,
          wireframe: true,
          transparent: true,
          opacity: 0.045,
          depthWrite: false
        });
        const overlayMesh = new THREE.Mesh(mesh.geometry, overlayMaterial);
        mesh.add(overlayMesh);
      }
    }
  });

  // First center the model at origin, then scale
  const initialBox = new THREE.Box3().setFromObject(model);
  const initialCenter = initialBox.getCenter(new THREE.Vector3());

  // Move model to origin first
  model.position.set(-initialCenter.x, -initialCenter.y, -initialCenter.z);
  model.updateMatrixWorld(true);

  // Now scale
  model.scale.set(3, 3, 3);
  model.updateMatrixWorld(true);

  // Recalculate bounding box after transformations
  const box = new THREE.Box3().setFromObject(model);
  const size = box.getSize(new THREE.Vector3());
  const center = box.getCenter(new THREE.Vector3());

  console.log('Model size after transform:', size);
  console.log('Model center after transform:', center);

  const headGroup = new THREE.Group();
  // Adjust position to center at origin
  model.position.x -= center.x;
  model.position.y -= center.y;
  model.position.z -= center.z;

  headGroup.add(model);
  scene.add(headGroup);

  if (sceneRef.current) {
    sceneRef.current.headModel = headGroup;
    sceneRef.current.outlinePass.selectedObjects = [headGroup];

    // Calculate camera position based on model size
    const maxDim = Math.max(size.x, size.y, size.z);
    const fovRad = camera.fov * Math.PI / 180;
    const distance = (maxDim / 2) / Math.tan(fovRad / 2);
    const idealZ = distance * 1.5;

    const isMobile = window.innerWidth <= 800;
    const zMult = isMobile ? 1.8 : 1.2;
    const finalZ = idealZ * zMult;
    const y = finalZ * (isMobile ? 0.3 : 0.12);
    const targetY = 0;

    console.log('Camera position:', { finalZ, y, targetY });

    sceneRef.current.cameraBaseZ = finalZ;
    sceneRef.current.cameraBaseY = y;
    sceneRef.current.cameraTargetY = targetY;

    camera.position.set(0, y, finalZ);
    camera.lookAt(0, targetY, 0);
    camera.updateProjectionMatrix();
  }

  setIsLoading(false);
},
(progress) => {
  console.log('Loading progress:', (progress.loaded / progress.total * 100).toFixed(1) + '%');
},
(error) => {
  console.error('Error loading model:', error);
  setIsLoading(false);
});

