     1→'use client';
     2→
     3→import { useEffect, useRef, useState } from 'react';
     4→import * as THREE from 'three';
     5→import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader.js';
     6→import { EffectComposer } from 'three/examples/jsm/postprocessing/EffectComposer.js';
     7→import { RenderPass } from 'three/examples/jsm/postprocessing/RenderPass.js';
     8→import { UnrealBloomPass } from 'three/examples/jsm/postprocessing/UnrealBloomPass.js';
     9→import { OutlinePass } from 'three/examples/jsm/postprocessing/OutlinePass.js';
    10→import { ShaderPass } from 'three/examples/jsm/postprocessing/ShaderPass.js';
    11→import { RGBShiftShader } from 'three/examples/jsm/shaders/RGBShiftShader.js';
    12→
    13→// Score color gradient function
    14→function getScoreColorRGB(score: number): string {
    15→  if (typeof score !== 'number') return 'rgb(37, 99, 235)';
    16→  const clamp = Math.max(1, Math.min(10, score));
    17→  const stops = [
    18→    { t: 1, c: [185, 28, 28] },
    19→    { t: 2, c: [220, 53, 53] },
    20→    { t: 3, c: [239, 88, 88] },
    21→    { t: 4, c: [245, 130, 75] },
    22→    { t: 5, c: [251, 175, 60] },
    23→    { t: 6, c: [220, 200, 80] },
    24→    { t: 7, c: [140, 215, 100] },
    25→    { t: 8, c: [74, 222, 128] },
    26→    { t: 9, c: [40, 205, 170] },
    27→    { t: 10, c: [6, 182, 212] }
    28→  ];
    29→  let out = stops[stops.length - 1].c;
    30→  for (let i = 0; i < stops.length - 1; i++) {
    31→    const a = stops[i], b = stops[i + 1];
    32→    if (clamp >= a.t && clamp <= b.t) {
    33→      const f = (clamp - a.t) / (b.t - a.t);
    34→      out = [
    35→        Math.round(a.c[0] + f * (b.c[0] - a.c[0])),
    36→        Math.round(a.c[1] + f * (b.c[1] - a.c[1])),
    37→        Math.round(a.c[2] + f * (b.c[2] - a.c[2]))
    38→      ];
    39→      break;
    40→    }
    41→  }
    42→  return `rgb(${out[0]}, ${out[1]}, ${out[2]})`;
    43→}
    44→
    45→// Create vertical gradient texture for walls
    46→function createVerticalGradientTexture(topHex: string, bottomHex: string): THREE.CanvasTexture {
    47→  const canvas = document.createElement('canvas');
    48→  canvas.width = 1;
    49→  canvas.height = 256;
    50→  const ctx = canvas.getContext('2d')!;
    51→  const grad = ctx.createLinearGradient(0, 0, 0, 256);
    52→  grad.addColorStop(0, topHex);
    53→  grad.addColorStop(1, bottomHex);
    54→  ctx.fillStyle = grad;
    55→  ctx.fillRect(0, 0, 1, 256);
    56→  const tex = new THREE.CanvasTexture(canvas);
    57→  tex.wrapS = THREE.ClampToEdgeWrapping;
    58→  tex.wrapT = THREE.ClampToEdgeWrapping;
    59→  return tex;
    60→}
    61→
    62→interface HeadSceneProps {
    63→  className?: string;
    64→}
    65→
