   285→      const model = gltf.scene;
   286→
   287→      console.log('GLTF loaded:', gltf);
   288→      console.log('Model scene:', model);
   289→
   290→      // Collect meshes first to avoid infinite recursion when adding overlays
   291→      const meshes: THREE.Mesh[] = [];
   292→      model.traverse((obj) => {
   293→        if ((obj as THREE.Mesh).isMesh) {
   294→          meshes.push(obj as THREE.Mesh);
   295→        }
   296→      });
   297→
   298→      console.log('Total meshes found:', meshes.length);
   299→
   300→      // Apply materials to collected meshes - matching original pale glowing aesthetic
   301→      meshes.forEach((mesh) => {
   302→        console.log('Processing mesh:', mesh.name, 'geometry vertices:', mesh.geometry?.attributes?.position?.count);
   303→
   304→        // Create material matching original: off-white with white emissive for glow
   305→        const skinMaterial = new THREE.MeshStandardMaterial({
   306→          color: 0xf5f2ed,           // Off-white (original)
   307→          emissive: 0xffffff,        // White emissive (original)
   308→          emissiveIntensity: 0.08,   // Subtle glow (original)
   309→          metalness: 0.08,           // Slight metalness (original)
   310→          roughness: 0.4,            // Lower roughness for more shine (original)
   311→          transparent: false,
   312→          opacity: 1,
   313→          depthWrite: true,
   314→          depthTest: true,
   315→          side: THREE.FrontSide
   316→        });
   317→
   318→        // Add Fresnel effect via shader modification for edge glow
   319→        skinMaterial.onBeforeCompile = (shader) => {
   320→          shader.uniforms.uFresnelColor = { value: new THREE.Color(0xffffff) };
   321→          shader.uniforms.uFresnelPower = { value: 1.8 };
   322→          shader.uniforms.uFresnelIntensity = { value: 0.25 };
   323→          shader.fragmentShader = shader.fragmentShader.replace(
   324→            '#include <output_fragment>',
   325→            `
   326→            float fresnel = pow(1.0 - dot(normalize(vNormal), normalize(vViewPosition)), uFresnelPower);
   327→            vec3 fres = uFresnelColor * fresnel * uFresnelIntensity;
   328→            gl_FragColor = vec4( outgoingLight + fres, diffuseColor.a );
   329→            `
   330→          );
   331→        };
   332→        skinMaterial.needsUpdate = true;
   333→
   334→        mesh.material = skinMaterial;
   335→        mesh.castShadow = true;
   336→        mesh.receiveShadow = true;
   337→
   338→        // Add wireframe overlay - white with subtle opacity (original)
   339→        if (mesh.geometry) {
   340→          const overlayMaterial = new THREE.MeshBasicMaterial({
   341→            color: 0xffffff,          // White wireframe (original)
   342→            wireframe: true,
   343→            transparent: true,
