   345→            depthWrite: false
   346→          });
   347→          const overlayMesh = new THREE.Mesh(mesh.geometry, overlayMaterial);
   348→          overlayMesh.renderOrder = (mesh.renderOrder || 0) + 1;
   349→          mesh.add(overlayMesh);
