   409→        const baseDistance = (modelSphereRadius / Math.tan(fovRad / 2)) * 1.2;
   410→
   411→        // Apply responsive camera settings (matching original applyResponsiveCamera)
   412→        const isMobile = window.innerWidth <= 800;
   413→        const zMult = isMobile ? 1.5 : 1.0;
   414→        const finalZ = baseDistance * zMult;
   415→        const finalY = finalZ * (isMobile ? 0.44 : 0.15);
   416→        const targetY = isMobile ? modelSphereRadius * 1.1 : 0;
   417→
   418→        console.log('Camera setup - sphereRadius:', modelSphereRadius, 'distance:', finalZ, 'y:', finalY);
   419→
