   442→
   443→  }, []);
   444→
   445→  // Animation loop
   446→  const animate = useCallback(() => {
   447→    if (!sceneRef.current) return;
   448→
   449→    const {
   450→      camera,
   451→      composer,
   452→      headModel,
   453→      roomGroup,
   454→      outlinePass,
   455→      bloomPass,
   456→      rgbShiftPass,
   457→      pointer,
   458→      pointerTarget,
   459→      cameraBaseZ,
   460→      cameraBaseY,
   461→      cameraTargetY,
   462→      gridColorsAttr
   463→    } = sceneRef.current;
   464→
   465→    const t = performance.now() * 0.001;
   466→
   467→    // Interpolate yaw for smooth transitions (matches original behavior)
   468→    sceneRef.current.currentYaw = THREE.MathUtils.lerp(
   469→      sceneRef.current.currentYaw,
   470→      sceneRef.current.targetYaw,
   471→      0.08
   472→    );
   473→
   474→    // Smooth pointer interpolation
   475→    pointer.x += (pointerTarget.x - pointer.x) * 0.08;
   476→    pointer.y += (pointerTarget.y - pointer.y) * 0.08;
   477→
   478→    const isMobile = window.innerWidth <= 800;
   479→
   480→    // Base pitch and yaw values (matches original animation.js)
   481→    const headBasePitch = isMobile ? -0.35 : -0.10;
   482→    const headBaseYaw = isMobile ? 0.0 : 0.0;
   483→    const roomBasePitch = isMobile ? -0.35 : -0.10;
   484→    const roomBaseYaw = isMobile ? -0.2 : 0.0;
   485→
   486→    // Animate head model (matches original: headBaseYaw - currentYaw + pointer.x * 0.1)
   487→    if (headModel) {
   488→      headModel.rotation.y = headBaseYaw - sceneRef.current.currentYaw + pointer.x * 0.1;
   489→      headModel.rotation.x = headBasePitch + pointer.y * 0.03;
   490→    }
   491→
   492→    // Animate room (matches original: roomBaseYaw + pointer.x * 0.08)
   493→    if (roomGroup) {
   494→      roomGroup.rotation.y = roomBaseYaw + pointer.x * 0.08;
   495→      roomGroup.rotation.x = roomBasePitch + pointer.y * 0.04;
   496→    }
   497→
   498→    // Camera sway
   499→    const sway = Math.max(0.02, Math.min(0.06, cameraBaseZ * 0.0004));
   500→    const lift = Math.max(0.015, Math.min(0.05, cameraBaseZ * 0.0003));
   501→    camera.position.set(
   502→      pointer.x * cameraBaseZ * sway,
   503→      cameraBaseY + pointer.y * cameraBaseZ * lift,
   504→      cameraBaseZ
   505→    );
   506→    camera.lookAt(0, cameraTargetY, 0);
   507→
   508→    // Animate score
   509→    sceneRef.current.currentScore += (sceneRef.current.targetScore - sceneRef.current.currentScore) * 0.08;
   510→    const scoreColor = getScoreColorRGB(sceneRef.current.currentScore);
   511→
   512→    // Update outline color
   513→    if (outlinePass) {
   514→      outlinePass.visibleEdgeColor.setStyle(scoreColor);
   515→      outlinePass.edgeStrength = 2.0 + 1.5 * (0.5 + 0.5 * Math.sin(t * 1.2));
   516→    }
   517→
   518→    // Update bloom
   519→    if (bloomPass) {
   520→      const targetBloom = 0.25 + Math.max(0, Math.min(1, (sceneRef.current.currentScore - 5) / 5)) * 0.7;
   521→      bloomPass.strength += (targetBloom - bloomPass.strength) * 0.05;
   522→      bloomPass.radius = 0.4 + 0.2 * (0.5 + 0.5 * Math.sin(t * 0.6));
   523→    }
   524→
   525→    // Update RGB shift
   526→    if (rgbShiftPass) {
   527→      rgbShiftPass.uniforms['amount'].value = 0.0012 + 0.0008 * (0.5 + 0.5 * Math.sin(t * 0.75));
   528→    }
   529→
   530→    // Update grid colors
   531→    if (gridColorsAttr) {
   532→      const rgbMatch = scoreColor.match(/\d+/g);
   533→      if (rgbMatch) {
   534→        const r = parseInt(rgbMatch[0]) / 255;
   535→        const g = parseInt(rgbMatch[1]) / 255;
   536→        const b = parseInt(rgbMatch[2]) / 255;
   537→        const arr = gridColorsAttr.array as Float32Array;
   538→        for (let i = 0; i < arr.length; i += 3) {
   539→          const wave = 0.3 + 0.7 * (0.5 + 0.5 * Math.sin(t * 0.5 + i * 0.01));
   540→          arr[i] = r * wave;
   541→          arr[i + 1] = g * wave;
   542→          arr[i + 2] = b * wave;
   543→        }
   544→        gridColorsAttr.needsUpdate = true;
   545→      }
   546→    }
   547→
   548→    composer.render();
   549→    animationRef.current = requestAnimationFrame(animate);
   550→  }, []);
   551→
   552→  // Handle pointer move
   553→  const handlePointerMove = useCallback((e: PointerEvent) => {
   554→    if (!containerRef.current || !sceneRef.current) return;
   555→    const rect = containerRef.current.getBoundingClientRect();
   556→    const nx = ((e.clientX - rect.left) / rect.width) * 2 - 1;
   557→    const ny = ((e.clientY - rect.top) / rect.height) * 2 - 1;
   558→    sceneRef.current.pointerTarget.x = Math.max(-1, Math.min(1, nx));
   559→    sceneRef.current.pointerTarget.y = Math.max(-1, Math.min(1, ny));
   560→  }, []);
   561→
   562→  const handlePointerLeave = useCallback(() => {
   563→    if (sceneRef.current) {
   564→      sceneRef.current.pointerTarget.x = 0;
   565→      sceneRef.current.pointerTarget.y = 0;
   566→    }
   567→  }, []);
   568→
   569→  // Handle resize
   570→  const handleResize = useCallback(() => {
   571→    if (!containerRef.current || !sceneRef.current) return;
   572→    const rect = containerRef.current.getBoundingClientRect();
   573→    const width = rect.width;
   574→    const height = rect.height;
   575→
   576→    sceneRef.current.camera.aspect = width / height;
   577→    sceneRef.current.camera.updateProjectionMatrix();
   578→    sceneRef.current.renderer.setSize(width, height);
   579→    sceneRef.current.composer.setSize(width, height);
   580→  }, []);
   581→
   582→  // Setup and cleanup
   583→  useEffect(() => {
   584→    initScene();
   585→
   586→    window.addEventListener('pointermove', handlePointerMove);
   587→    window.addEventListener('pointerleave', handlePointerLeave);
   588→    window.addEventListener('resize', handleResize);
   589→
   590→    animationRef.current = requestAnimationFrame(animate);
   591→
   592→    return () => {
   593→      window.removeEventListener('pointermove', handlePointerMove);
   594→      window.removeEventListener('pointerleave', handlePointerLeave);
   595→      window.removeEventListener('resize', handleResize);
   596→
   597→      if (animationRef.current) {
   598→        cancelAnimationFrame(animationRef.current);
   599→      }
   600→
   601→      if (sceneRef.current) {
   602→        sceneRef.current.renderer.dispose();
   603→        sceneRef.current.composer.dispose();
   604→      }
   605→    };
   606→  }, [initScene, animate, handlePointerMove, handlePointerLeave, handleResize]);
   607→
   608→  return (
   609→    <div
   610→      ref={containerRef}
   611→      className={`relative ${className}`}
   612→      style={{ width: '100%', height: '100%', minHeight: '100vh' }}
   613→    >
   614→      {isLoading && (
   615→        <div className="absolute inset-0 flex items-center justify-center bg-black/50 z-10">
   616→          <div className="w-8 h-8 border-2 border-blue-400 border-t-transparent rounded-full animate-spin" />
   617→        </div>
   618→      )}
   619→    </div>
   620→  );
   621→}
