   101→  const [isAnimating, setIsAnimating] = useState(false);
   102→
   103→  useEffect(() => {
   104→    const timer = setInterval(() => {
   105→      setIsAnimating(true);
   106→      setTimeout(() => {
   107→        setCurrentIndex((prev) => (prev + 1) % rotatingWords.length);
   108→        setIsAnimating(false);
   109→      }, 300);
   110→    }, 2500);
   111→
   112→    return () => clearInterval(timer);
   113→  }, []);
   114→
   115→  return (
   116→    <span
   117→      className="text-blue-400 inline-block"
   118→      style={{
   119→        transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
   120→        minWidth: '280px',
   121→        opacity: isAnimating ? 0 : 1,
   122→        transform: isAnimating ? 'translateY(10px)' : 'translateY(0)',
   123→      }}
   124→    >
   125→      {rotatingWords[currentIndex]}
   126→    </span>
   127→  );
   128→}
