   197→// ============================================================================
   198→// FLOATING SCORE CARD COMPONENT
   199→// ============================================================================
   200→function FloatingScoreCard() {
   201→  const [yOffset, setYOffset] = useState(0);
   202→  const [displayScore, setDisplayScore] = useState(0);
   203→
   204→  // Animate score counting up
   205→  useEffect(() => {
   206→    const targetScore = 4.27;
   207→    const duration = 2000;
   208→    const startTime = Date.now();
   209→
   210→    const animate = () => {
   211→      const elapsed = Date.now() - startTime;
   212→      const progress = Math.min(elapsed / duration, 1);
   213→      const eased = 1 - Math.pow(1 - progress, 3);
   214→      setDisplayScore(targetScore * eased);
   215→
   216→      if (progress < 1) {
   217→        requestAnimationFrame(animate);
   218→      }
   219→    };
   220→
   221→    const timer = setTimeout(() => {
   222→      requestAnimationFrame(animate);
   223→    }, 800);
   224→
   225→    return () => clearTimeout(timer);
   226→  }, []);
   227→
   228→  // Gentle floating animation
   229→  useEffect(() => {
   230→    let animationFrame: number;
   231→    let startTime: number;
   232→
   233→    const animate = (timestamp: number) => {
   234→      if (!startTime) startTime = timestamp;
   235→      const elapsed = (timestamp - startTime) / 1000;
   236→      setYOffset(Math.sin(elapsed * 0.6 + 1) * 6);
   237→      animationFrame = requestAnimationFrame(animate);
   238→    };
   239→
   240→    animationFrame = requestAnimationFrame(animate);
   241→    return () => cancelAnimationFrame(animationFrame);
   242→  }, []);
   243→
   244→  // Color interpolation based on score
   245→  const getScoreColor = (score: number) => {
   246→    if (score < 3) return 'rgb(239, 68, 68)';
   247→    if (score < 5) return 'rgb(247, 142, 71)';
   248→    if (score < 7) return 'rgb(234, 179, 8)';
   249→    return 'rgb(34, 197, 94)';
   250→  };
   251→
   252→  return (
   253→    <motion.div
   254→      initial={{ opacity: 0, x: 30 }}
   255→      animate={{ opacity: 1, x: 0 }}
   256→      transition={{ delay: 0.8, duration: 0.5 }}
   257→      className="absolute bottom-[30%] right-[5%] lg:right-[10%] translate-y-1/2 pointer-events-none z-20"
   258→    >
   259→      <div
   260→        className="rounded-xl bg-white/5 backdrop-blur-xl px-5 py-4 shadow-2xl border border-white/20 transition-all duration-300"
   261→        style={{ transform: `translateY(${yOffset}px)` }}
   262→      >
   263→        <div className="text-[10px] uppercase tracking-wider font-semibold text-gray-300 mb-1.5">
   264→          Overall Score
   265→        </div>
   266→        <div className="flex items-baseline gap-0.5">
   267→          <span
   268→            className="text-4xl font-bold tabular-nums transition-colors duration-300"
   269→            style={{ color: getScoreColor(displayScore) }}
   270→          >
   271→            {displayScore.toFixed(2)}
   272→          </span>
   273→          <span className="text-xl font-medium text-gray-400">/10</span>
   274→        </div>
   275→      </div>
   276→    </motion.div>
   277→  );
   278→}
