      <section className="py-20 px-6 bg-[#0d1117] relative">
        <div className="max-w-5xl mx-auto relative z-20">
          <motion.div
            variants={fadeIn}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
