     1→/**
     2→ * HeroAnimations.css
     3→ * Extracted from faceiqlabs.com HAR file
     4→ *
     5→ * IMPORTANT FINDING: The original site does NOT use a gradient shimmer animation
     6→ * on the "Harmony" text. Instead, it uses:
     7→ * 1. A simple blue color (text-blue-400 / #60a5fa)
     8→ * 2. A rotating word animation with fade/slide transitions
     9→ */
    10→
    11→/* =============================================
    12→   ROTATING TEXT ANIMATION
    13→   The h1 cycles through words: proportions, symmetry,
    14→   harmony, ratios, measurements, balance
    15→   ============================================= */
    16→
    17→.rotating-text {
    18→  display: inline-block;
    19→  color: #60a5fa; /* Tailwind text-blue-400 */
    20→  min-width: 280px;
    21→  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    22→}
    23→
    24→.rotating-text.fade-out {
    25→  opacity: 0;
    26→  transform: translateY(10px);
    27→}
    28→
    29→.rotating-text.fade-in {
    30→  opacity: 1;
    31→  transform: translateY(0);
    32→}
    33→
    34→/* =============================================
    35→   FADE-IN ANIMATION (Intersection Observer)
    36→   Applied to sections as they scroll into view
    37→   ============================================= */
    38→
    39→.fade-in {
    40→  opacity: 0;
    41→  transform: translateY(20px);
    42→  transition: opacity 0.6s ease, transform 0.6s ease;
    43→}
    44→
    45→.fade-in.visible {
    46→  opacity: 1;
    47→  transform: translateY(0);
    48→}
    49→
    50→/* =============================================
    51→   HERO SECTION CONTAINER
    52→   ============================================= */
    53→
    54→.hero-section {
    55→  position: relative;
    56→  overflow: hidden;
    57→  min-height: max(100vh, 720px);
    58→  height: 100vh;
    59→  background: #000000;
    60→}
    61→
    62→/* =============================================
    63→   3D HEAD CONTAINER
    64→   The head3d element hosts the Three.js canvas
    65→   ============================================= */
    66→
    67→.head3d-container {
    68→  position: absolute;
    69→  top: 0;
    70→  left: 0;
    71→  width: 100%;
    72→  height: 100%;
    73→  min-height: max(100vh, 720px);
    74→  z-index: 0;
    75→  pointer-events: none;
    76→  transform: translate3d(0, 0, 0);
    77→  will-change: transform;
    78→  contain: layout style paint;
    79→}
    80→
    81→.head3d-container > canvas {
    82→  position: absolute !important;
    83→  inset: 0 !important;
    84→  width: 100% !important;
    85→  height: 100% !important;
    86→  z-index: 0;
    87→  display: block;
    88→  transform: translate3d(0, 0, 0);
    89→  will-change: transform;
    90→  backface-visibility: hidden;
    91→}
    92→
    93→/* =============================================
    94→   VIGNETTE OVERLAY
    95→   Creates the gradient fade effect around the 3D head
    96→   ============================================= */
    97→
    98→.vignette-overlay {
    99→  position: absolute !important;
   100→  top: 0 !important;
   101→  bottom: 0 !important;
   102→  left: 0 !important;
   103→  width: 100% !important;
   104→  pointer-events: none !important;
   105→  z-index: 2 !important;
   106→  opacity: 0.8 !important;
   107→  transform: translate3d(0, 0, 0);
   108→  will-change: opacity;
   109→  background:
   110→    linear-gradient(to right, rgba(0, 0, 0, 0.65) 0%, rgba(0, 0, 0, 0.5) 20%, rgba(0, 0, 0, 0.35) 35%, transparent 55%),
   111→    linear-gradient(to top, #000000 0%, transparent 40%),
   112→    linear-gradient(to bottom, rgba(0, 0, 0, 0.2) 0%, transparent 30%),
   113→    radial-gradient(ellipse at 55% 50%, transparent 35%, rgba(0, 0, 0, 0.18) 60%, rgba(0, 0, 0, 0.35) 82%, rgba(0, 0, 0, 0.5) 100%);
   114→}
   115→
   116→/* =============================================
   117→   RESPONSIVE STYLES
   118→   ============================================= */
   119→
   120→/* Small mobile (< 400px) */
   121→@media (max-width: 400px) {
   122→  .hero-section {
   123→    height: 100vh !important;
   124→    min-height: max(100vh, 720px) !important;
   125→  }
   126→
   127→  .head3d-container {
   128→    width: 100vw !important;
   129→    height: 100vh !important;
   130→    min-height: max(100vh, 720px) !important;
   131→    left: -1.5rem !important;
   132→  }
   133→
   134→  .vignette-overlay {
   135→    opacity: 0.6 !important;
   136→  }
   137→
   138→  .hero-text {
   139→    position: absolute !important;
   140→    top: 2rem !important;
   141→    left: 0;
   142→    right: 0;
   143→    padding-left: 1.25rem !important;
   144→    padding-right: 1.25rem !important;
   145→    z-index: 30;
   146→  }
   147→
   148→  .hero-text h1 {
   149→    font-size: 1.75rem !important;
   150→    line-height: 1.2 !important;
   151→    margin-bottom: 0.75rem !important;
   152→  }
   153→
   154→  .hero-text p {
   155→    font-size: 0.875rem !important;
   156→    margin-bottom: 1rem !important;
   157→  }
   158→}
