    48→  canvas.width = 1;
    49→  canvas.height = 256;
    50→  const ctx = canvas.getContext('2d')!;
    51→  const grad = ctx.createLinearGradient(0, 0, 0, 256);
    52→  grad.addColorStop(0, topHex);
    53→  grad.addColorStop(1, bottomHex);
    54→  ctx.fillStyle = grad;
    55→  ctx.fillRect(0, 0, 1, 256);
    56→  const tex = new THREE.CanvasTexture(canvas);
    57→  tex.wrapS = THREE.ClampToEdgeWrapping;
    58→  tex.wrapT = THREE.ClampToEdgeWrapping;
    59→  return tex;
    60→}
