    89→function RotatingText() {
    90→  const [currentIndex, setCurrentIndex] = useState(0);
    91→  const [isAnimating, setIsAnimating] = useState(false);
    92→
    93→  useEffect(() => {
    94→    const timer = setInterval(() => {
