   120→// ============================================================================
   121→// FLOATING ADVICE CARD COMPONENT
   122→// ============================================================================
   123→function FloatingAdviceCard() {
   124→  const [currentIndex, setCurrentIndex] = useState(0);
   125→  const [isAnimating, setIsAnimating] = useState(false);
   126→  const [yOffset, setYOffset] = useState(0);
   127→
   128→  // Cycle through advice data
   129→  useEffect(() => {
   130→    const timer = setInterval(() => {
   131→      setIsAnimating(true);
   132→      setTimeout(() => {
   133→        setCurrentIndex((prev) => (prev + 1) % adviceData.length);
   134→        setIsAnimating(false);
   135→      }, 200);
   136→    }, 3000);
   137→
   138→    return () => clearInterval(timer);
   139→  }, []);
   140→
   141→  // Gentle floating animation
   142→  useEffect(() => {
   143→    let animationFrame: number;
   144→    let startTime: number;
   145→
   146→    const animate = (timestamp: number) => {
   147→      if (!startTime) startTime = timestamp;
   148→      const elapsed = (timestamp - startTime) / 1000;
   149→      setYOffset(Math.sin(elapsed * 0.8) * 5);
   150→      animationFrame = requestAnimationFrame(animate);
   151→    };
   152→
   153→    animationFrame = requestAnimationFrame(animate);
   154→    return () => cancelAnimationFrame(animationFrame);
   155→  }, []);
   156→
   157→  const advice = adviceData[currentIndex];
   158→
   159→  return (
   160→    <motion.div
   161→      initial={{ opacity: 0, x: -30 }}
   162→      animate={{ opacity: 1, x: 0 }}
   163→      transition={{ delay: 0.6, duration: 0.5 }}
   164→      className="absolute top-[26%] left-[15%] lg:left-[20%] -translate-x-1/2 -translate-y-1/2 pointer-events-none z-20"
   165→    >
   166→      <div
   167→        className="rounded-xl bg-white/5 backdrop-blur-xl px-4 py-3 shadow-2xl border border-white/20 transition-all duration-300"
   168→        style={{ transform: `translateY(${yOffset}px)` }}
   169→      >
   170→        <div
   171→          className="text-[10px] uppercase tracking-wider font-semibold text-gray-300 mb-1 transition-opacity duration-200"
   172→          style={{ opacity: isAnimating ? 0 : 1 }}
   173→        >
   174→          {advice.profile}
   175→        </div>
   176→        <div
   177→          className="text-lg font-semibold text-gray-100 leading-tight transition-opacity duration-200"
   178→          style={{ opacity: isAnimating ? 0 : 1 }}
   179→        >
