   186→
   187→// ============================================================================
   188→// FLOATING SCORE CARD COMPONENT
   189→// ============================================================================
   190→// Score values to cycle through for continuous animation
   191→const scoreValues = [4.27, 6.42, 7.85, 5.91, 8.12, 3.56];
   192→
   193→function FloatingScoreCard() {
   194→  const [yOffset, setYOffset] = useState(0);
   195→  const [displayScore, setDisplayScore] = useState(0);
   196→  const scoreIndexRef = useRef(0);
   197→
   198→  // Animate score cycling continuously
   199→  useEffect(() => {
   200→    let animationFrame: number;
   201→    let timeoutId: NodeJS.Timeout;
   202→
   203→    const animateToScore = (targetScore: number, startScore: number, onComplete: () => void) => {
   204→      const duration = 1500;
   205→      const startTime = Date.now();
   206→
   207→      const animate = () => {
   208→        const elapsed = Date.now() - startTime;
   209→        const progress = Math.min(elapsed / duration, 1);
   210→        const eased = 1 - Math.pow(1 - progress, 3);
   211→        setDisplayScore(startScore + (targetScore - startScore) * eased);
   212→
   213→        if (progress < 1) {
   214→          animationFrame = requestAnimationFrame(animate);
   215→        } else {
   216→          onComplete();
   217→        }
   218→      };
   219→
   220→      animationFrame = requestAnimationFrame(animate);
   221→    };
   222→
   223→    const cycleScore = () => {
   224→      const currentIndex = scoreIndexRef.current;
   225→      const nextIndex = (currentIndex + 1) % scoreValues.length;
   226→      const currentScore = scoreValues[currentIndex];
   227→      const nextScore = scoreValues[nextIndex];
   228→
   229→      scoreIndexRef.current = nextIndex;
   230→
   231→      animateToScore(nextScore, currentScore, () => {
   232→        timeoutId = setTimeout(cycleScore, 2000);
   233→      });
   234→    };
   235→
   236→    // Initial animation from 0 to first score
   237→    const initialTimeout = setTimeout(() => {
   238→      animateToScore(scoreValues[0], 0, () => {
   239→        timeoutId = setTimeout(cycleScore, 2000);
   240→      });
   241→    }, 800);
   242→
   243→    return () => {
   244→      clearTimeout(initialTimeout);
   245→      clearTimeout(timeoutId);
   246→      cancelAnimationFrame(animationFrame);
   247→    };
   248→  }, []);
   249→
   250→  // Gentle floating animation
   251→  useEffect(() => {
   252→    let animationFrame: number;
   253→    let startTime: number;
   254→
   255→    const animate = (timestamp: number) => {
   256→      if (!startTime) startTime = timestamp;
   257→      const elapsed = (timestamp - startTime) / 1000;
   258→      setYOffset(Math.sin(elapsed * 0.6 + 1) * 6);
   259→      animationFrame = requestAnimationFrame(animate);
   260→    };
   261→
   262→    animationFrame = requestAnimationFrame(animate);
   263→    return () => cancelAnimationFrame(animationFrame);
   264→  }, []);
   265→
   266→  // Color interpolation based on score
   267→  const getScoreColor = (score: number) => {
   268→    if (score < 3) return 'rgb(239, 68, 68)';
   269→    if (score < 5) return 'rgb(247, 142, 71)';
   270→    if (score < 7) return 'rgb(234, 179, 8)';
   271→    return 'rgb(34, 197, 94)';
   272→  };
   273→
   274→  return (
   275→    <motion.div
   276→      initial={{ opacity: 0, x: 30 }}
   277→      animate={{ opacity: 1, x: 0 }}
   278→      transition={{ delay: 0.8, duration: 0.5 }}
   279→      className="absolute bottom-[30%] right-[5%] lg:right-[10%] translate-y-1/2 pointer-events-none z-20"
   280→    >
   281→      <div
   282→        className="rounded-xl bg-white/5 backdrop-blur-xl px-5 py-4 shadow-2xl border border-white/20 transition-all duration-300"
   283→        style={{ transform: `translateY(${yOffset}px)` }}
   284→      >
   285→        <div className="text-[10px] uppercase tracking-wider font-semibold text-gray-300 mb-1.5">
   286→          Overall Score
   287→        </div>
   288→        <div className="flex items-baseline gap-0.5">
   289→          <span
   290→            className="text-4xl font-bold tabular-nums transition-colors duration-300"
   291→            style={{ color: getScoreColor(displayScore) }}
   292→          >
   293→            {displayScore.toFixed(2)}
   294→          </span>
   295→          <span className="text-xl font-medium text-gray-400">/10</span>
   296→        </div>
   297→      </div>
   298→    </motion.div>
   299→  );
   300→}
