"use client";

import { motion } from "framer-motion";
import { useState, useEffect, useRef } from "react";
import {
  ArrowRight,
  CloudUpload,
  Target,
  FileText,
  Gauge,
  Ruler,
  CheckCircle,
  TrendingUp,
  Pencil,
  Zap,
  Trophy,
  Check,
  X,
} from "lucide-react";
import Link from "next/link";


// Animation variants
const fadeIn = {
  hidden: { opacity: 0, y: 20 },
  visible: { opacity: 1, y: 0 },
};

// Rotating text words from original
const rotatingWords = ['proportions', 'symmetry', 'harmony', 'ratios', 'measurements', 'balance'];

// Advice data for floating card cycling
const adviceData = [
  { profile: 'Lateral Canthal Tilt', text: '3.96°' },
  { profile: 'Facial Thirds', text: '1:1:0.92' },
  { profile: 'Eye Spacing', text: 'Ideal' },
  { profile: 'Nasal Projection', text: '0.67x' },
  { profile: 'Jaw Angle', text: '128°' },
];

// ============================================================================
// NAVBAR COMPONENT
// ============================================================================
function Navbar() {
  return (
    <nav className="fixed top-0 inset-x-0 z-30 transition-all duration-300 bg-black/90 backdrop-blur-sm">
      <div className="max-w-7xl mx-auto px-6">
        <div className="flex items-center justify-between h-16">
          <Link href="/" className="flex items-center gap-2 text-sm font-semibold">
            <div className="h-5 w-5 rounded bg-blue-500/20 flex items-center justify-center">
              <span className="text-blue-400 text-xs font-bold">F</span>
            </div>
            <span>LOOKSMAXX</span>
          </Link>
          <div className="hidden md:flex items-center gap-8">
            <a href="#how-it-works" className="text-sm text-gray-300 hover:text-white transition-colors">
              How it works
            </a>
            <a href="#your-plan" className="text-sm text-gray-300 hover:text-white transition-colors">
              Your Plan
            </a>
            <a href="#pricing" className="text-sm text-gray-300 hover:text-white transition-colors">
              Pricing
            </a>
            <Link
              href="/gender"
              className="text-sm font-medium bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-500 transition-colors"
            >
              Get started
            </Link>
          </div>
          <div className="md:hidden">
            <Link
              href="/gender"
              className="text-sm font-medium bg-blue-600 text-white px-3 py-2 rounded-lg hover:bg-blue-500 transition-colors"
            >
              Get started
            </Link>
          </div>
        </div>
      </div>
    </nav>
  );
}

// ============================================================================
// ROTATING TEXT COMPONENT
// ============================================================================
function RotatingText() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    const timer = setInterval(() => {
      setIsAnimating(true);
      setTimeout(() => {
        setCurrentIndex((prev) => (prev + 1) % rotatingWords.length);
        setIsAnimating(false);
      }, 300);
    }, 2500);

    return () => clearInterval(timer);
  }, []);

  return (
    <span
      className="text-blue-400 inline-block"
      style={{
        transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
        minWidth: '280px',
        opacity: isAnimating ? 0 : 1,
        transform: isAnimating ? 'translateY(10px)' : 'translateY(0)',
      }}
    >
      {rotatingWords[currentIndex]}
    </span>
  );
}

// ============================================================================
// FLOATING ADVICE CARD COMPONENT
// ============================================================================
function FloatingAdviceCard() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);
  const [yOffset, setYOffset] = useState(0);

  // Cycle through advice data
  useEffect(() => {
    const timer = setInterval(() => {
      setIsAnimating(true);
      setTimeout(() => {
        setCurrentIndex((prev) => (prev + 1) % adviceData.length);
        setIsAnimating(false);
      }, 200);
    }, 3000);

    return () => clearInterval(timer);
  }, []);

  // Gentle floating animation
  useEffect(() => {
    let animationFrame: number;
    let startTime: number;

    const animate = (timestamp: number) => {
      if (!startTime) startTime = timestamp;
      const elapsed = (timestamp - startTime) / 1000;
      setYOffset(Math.sin(elapsed * 0.8) * 5);
      animationFrame = requestAnimationFrame(animate);
    };

    animationFrame = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(animationFrame);
  }, []);

  const advice = adviceData[currentIndex];

  return (
    <motion.div
      initial={{ opacity: 0, x: -30 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ delay: 0.6, duration: 0.5 }}
      className="absolute top-[26%] left-[15%] lg:left-[20%] -translate-x-1/2 -translate-y-1/2 pointer-events-none z-20"
    >
      <div
        className="rounded-xl bg-white/5 backdrop-blur-xl px-4 py-3 shadow-2xl border border-white/20 transition-all duration-300"
        style={{ transform: `translateY(${yOffset}px)` }}
      >
        <div
          className="text-[10px] uppercase tracking-wider font-semibold text-gray-300 mb-1 transition-opacity duration-200"
          style={{ opacity: isAnimating ? 0 : 1 }}
        >
          {advice.profile}
        </div>
        <div
          className="text-lg font-semibold text-gray-100 leading-tight transition-opacity duration-200"
          style={{ opacity: isAnimating ? 0 : 1 }}
        >
          {advice.text}
        </div>
      </div>
    </motion.div>
  );
}

// ============================================================================
// FLOATING SCORE CARD COMPONENT
// ============================================================================
// Score values to cycle through for continuous animation
const scoreValues = [4.27, 6.42, 7.85, 5.91, 8.12, 3.56];

function FloatingScoreCard() {
  const [yOffset, setYOffset] = useState(0);
  const [displayScore, setDisplayScore] = useState(0);
  const scoreIndexRef = useRef(0);

  // Animate score cycling continuously
  useEffect(() => {
    let animationFrame: number;
    let timeoutId: NodeJS.Timeout;

    const animateToScore = (targetScore: number, startScore: number, onComplete: () => void) => {
      const duration = 1500;
      const startTime = Date.now();

      const animate = () => {
        const elapsed = Date.now() - startTime;
        const progress = Math.min(elapsed / duration, 1);
        const eased = 1 - Math.pow(1 - progress, 3);
        setDisplayScore(startScore + (targetScore - startScore) * eased);

        if (progress < 1) {
          animationFrame = requestAnimationFrame(animate);
        } else {
          onComplete();
        }
      };

      animationFrame = requestAnimationFrame(animate);
    };

    const cycleScore = () => {
      const currentIndex = scoreIndexRef.current;
      const nextIndex = (currentIndex + 1) % scoreValues.length;
      const currentScore = scoreValues[currentIndex];
      const nextScore = scoreValues[nextIndex];

      scoreIndexRef.current = nextIndex;

      animateToScore(nextScore, currentScore, () => {
        timeoutId = setTimeout(cycleScore, 2000);
      });
    };

    // Initial animation from 0 to first score
    const initialTimeout = setTimeout(() => {
      animateToScore(scoreValues[0], 0, () => {
        timeoutId = setTimeout(cycleScore, 2000);
      });
    }, 800);

    return () => {
      clearTimeout(initialTimeout);
      clearTimeout(timeoutId);
      cancelAnimationFrame(animationFrame);
    };
  }, []);

  // Gentle floating animation
  useEffect(() => {
    let animationFrame: number;
    let startTime: number;

    const animate = (timestamp: number) => {
      if (!startTime) startTime = timestamp;
      const elapsed = (timestamp - startTime) / 1000;
      setYOffset(Math.sin(elapsed * 0.6 + 1) * 6);
      animationFrame = requestAnimationFrame(animate);
    };

    animationFrame = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(animationFrame);
  }, []);

  // Color interpolation based on score
  const getScoreColor = (score: number) => {
    if (score < 3) return 'rgb(239, 68, 68)';
    if (score < 5) return 'rgb(247, 142, 71)';
    if (score < 7) return 'rgb(234, 179, 8)';
    return 'rgb(34, 197, 94)';
  };

  return (
    <motion.div
      initial={{ opacity: 0, x: 30 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ delay: 0.8, duration: 0.5 }}
      className="absolute bottom-[30%] right-[5%] lg:right-[10%] translate-y-1/2 pointer-events-none z-20"
    >
      <div
        className="rounded-xl bg-white/5 backdrop-blur-xl px-5 py-4 shadow-2xl border border-white/20 transition-all duration-300"
        style={{ transform: `translateY(${yOffset}px)` }}
      >
        <div className="text-[10px] uppercase tracking-wider font-semibold text-gray-300 mb-1.5">
          Overall Score
        </div>
        <div className="flex items-baseline gap-0.5">
          <span
            className="text-4xl font-bold tabular-nums transition-colors duration-300"
            style={{ color: getScoreColor(displayScore) }}
          >
            {displayScore.toFixed(2)}
          </span>
          <span className="text-xl font-medium text-gray-400">/10</span>
        </div>
      </div>
    </motion.div>
  );
}

// ============================================================================
// YOUTUBE ICON COMPONENT
// ============================================================================
function YouTubeIcon({ className }: { className?: string }) {
  return (
    <svg className={className} viewBox="0 0 24 24" fill="currentColor">
      <path d="M23.498 6.186a3.016 3.016 0 0 0-2.122-2.136C19.505 3.545 12 3.545 12 3.545s-7.505 0-9.377.505A3.017 3.017 0 0 0 .502 6.186C0 8.07 0 12 0 12s0 3.93.502 5.814a3.016 3.016 0 0 0 2.122 2.136c1.871.505 9.376.505 9.376.505s7.505 0 9.377-.505a3.015 3.015 0 0 0 2.122-2.136C24 15.93 24 12 24 12s0-3.93-.502-5.814zM9.545 15.568V8.432L15.818 12l-6.273 3.568z" />
    </svg>
  );
}

// ============================================================================
// HERO SECTION COMPONENT
// ============================================================================
function HeroSection() {
  return (
    <>
      <section className="relative overflow-hidden flex items-stretch min-h-screen bg-black">
        {/* Top Gradient Overlay */}
        <div className="pointer-events-none absolute inset-x-0 -top-16 h-48 bg-gradient-to-b from-black via-black/80 to-transparent z-0 hidden lg:block" />

        {/* Bottom Gradient Overlay */}
        <div
          className="pointer-events-none absolute inset-x-0 bottom-0 h-[20vh] z-[15] hidden lg:block"
          style={{
            background:
              "linear-gradient(to bottom, transparent 0%, rgba(0,0,0,0.3) 20%, rgba(0,0,0,0.6) 50%, rgba(0,0,0,0.9) 80%, black 100%)",
          }}
        />

        <div className="max-w-7xl mx-auto w-full px-6 flex flex-col lg:flex-row items-stretch relative z-10">
          {/* Hero Text */}
          <div className="w-full lg:w-1/2 relative z-10 flex items-center py-12 sm:py-16 md:py-24 order-2 lg:order-1">
            <div className="max-w-xl w-full mx-auto lg:max-w-none lg:mx-0 text-center lg:text-left">
              {/* Social Proof - Stars and YouTube */}
              <motion.div
                variants={fadeIn}
                initial="hidden"
                animate="visible"
                transition={{ duration: 0.5 }}
                className="inline-flex flex-wrap items-center justify-center lg:justify-start gap-2 sm:gap-3 mb-4 sm:mb-6"
              >
                {/* Star Rating */}
                <div className="flex items-center gap-1.5 sm:gap-2 bg-blue-500/10 border border-blue-500/20 rounded-full px-2.5 py-1 sm:px-3 sm:py-1.5 flex-shrink-0">
                  <div className="flex">
                    {[...Array(5)].map((_, i) => (
                      <svg
                        key={i}
                        className={`w-3 h-3 sm:w-4 sm:h-4 text-yellow-400 fill-current ${i > 0 ? "-ml-0.5" : ""}`}
                        viewBox="0 0 20 20"
                      >
                        <path d="M10 15l-5.878 3.09 1.123-6.545L.489 6.91l6.572-.955L10 0l2.939 5.955 6.572.955-4.756 4.635 1.123 6.545z" />
                      </svg>
                    ))}
                  </div>
                  <span className="text-xs sm:text-sm font-medium text-blue-100">4.9/5</span>
                </div>

                <div className="h-4 w-px bg-gray-600 hidden sm:block flex-shrink-0" />

                {/* YouTube Subscribers */}
                <a
                  href="#"
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-xs sm:text-sm text-gray-400 hover:text-gray-200 transition-colors flex items-center gap-1 sm:gap-1.5 flex-shrink-0"
                >
                  <YouTubeIcon className="w-3 h-3 sm:w-4 sm:h-4 text-red-500 flex-shrink-0" />
                  <span className="font-semibold text-gray-200 whitespace-nowrap">
                    <span className="hidden sm:inline">120,000+</span>
                    <span className="sm:hidden">120K+</span> subscribers
                  </span>
                </a>
              </motion.div>

              {/* Headline with Rotating Text */}
              <motion.h1
                variants={fadeIn}
                initial="hidden"
                animate="visible"
                transition={{ duration: 0.6, delay: 0.1 }}
                className="text-4xl sm:text-5xl md:text-6xl font-bold tracking-tight leading-tight sm:leading-[1.12] md:leading-[1.08] mb-8"
              >
                Discover your facial <RotatingText />
              </motion.h1>

              {/* Subheadline */}
              <motion.p
                variants={fadeIn}
                initial="hidden"
                animate="visible"
                transition={{ duration: 0.6, delay: 0.2 }}
                className="text-base sm:text-lg md:text-xl text-gray-300 mb-10"
              >
                Get <span className="text-blue-400 font-semibold">60+</span> precise facial
                measurements and a{" "}
                <span className="text-emerald-400 font-semibold">personalized action plan</span> to
                improve your harmony score.
              </motion.p>

              {/* CTAs */}
              <motion.div
                variants={fadeIn}
                initial="hidden"
                animate="visible"
                transition={{ duration: 0.6, delay: 0.3 }}
                className="flex flex-col sm:flex-row items-stretch sm:items-center gap-3 sm:gap-5 md:gap-6"
              >
                <Link
                  href="/gender"
                  className="inline-flex items-center justify-center gap-2 font-medium bg-blue-600 text-white px-7 py-3.5 rounded-lg shadow-sm hover:bg-blue-500 focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-blue-500/40 transition-colors"
                >
                  Get your analysis
                  <ArrowRight className="h-4 w-4" />
                </Link>
                <a
                  href="#your-plan"
                  className="inline-flex items-center justify-center font-medium text-gray-100 hover:text-gray-300 transition-colors"
                >
                  See your plan
