              </motion.div>
            </div>
          </div>

          {/* Hero Right Area - Pure Black */}
          <div className="w-full lg:w-1/2 relative h-screen order-1 lg:order-2 bg-black">
          </div>
        </div>
      </section>

      {/* Branding Section */}
      <section className="py-20 px-6 bg-black relative">
        <div className="max-w-5xl mx-auto relative z-20">
          <motion.div
            variants={fadeIn}
            initial="hidden"
            whileInView="visible"
            viewport={{ once: true }}
