     3→import { motion } from "framer-motion";
     4→import { useState, useEffect, useRef } from "react";
     5→import dynamic from "next/dynamic";
     6→import {
     7→  ArrowRight,
     8→  CloudUpload,
     9→  Target,
    10→  FileText,
    11→  Gauge,
    12→  Ruler,
    13→  CheckCircle,
    14→  TrendingUp,
    15→  Pencil,
