    23→// Dynamic import for Three.js component (client-side only)
    24→const HeadScene = dynamic(() => import("@/components/HeadScene"), {
    25→  ssr: false,
    26→  loading: () => (
    27→    <div className="absolute inset-0 flex items-center justify-center">
    28→      <div className="w-8 h-8 border-2 border-blue-400 border-t-transparent rounded-full animate-spin" />
