    32→
    33→// Animation variants
    34→const fadeIn = {
    35→  hidden: { opacity: 0, y: 20 },
    36→  visible: { opacity: 1, y: 0 },
    37→};
    38→
    39→// Rotating text words from original
    40→const rotatingWords = ['proportions', 'symmetry', 'harmony', 'ratios', 'measurements', 'balance'];
    41→
    42→// Advice data for floating card cycling
    43→const adviceData = [
    44→  { profile: 'Lateral Canthal Tilt', text: '3.96°' },
    45→  { profile: 'Facial Thirds', text: '1:1:0.92' },
    46→  { profile: 'Eye Spacing', text: 'Ideal' },
    47→  { profile: 'Nasal Projection', text: '0.67x' },
    48→  { profile: 'Jaw Angle', text: '128°' },
    49→];
    50→
    51→// ============================================================================
    52→// NAVBAR COMPONENT
    53→// ============================================================================
    54→function Navbar() {
    55→  return (
    56→    <nav className="fixed top-0 inset-x-0 z-30 transition-all duration-300 bg-black/90 backdrop-blur-sm">
    57→      <div className="max-w-7xl mx-auto px-6">
    58→        <div className="flex items-center justify-between h-16">
    59→          <Link href="/" className="flex items-center gap-2 text-sm font-semibold">
    60→            <div className="h-5 w-5 rounded bg-blue-500/20 flex items-center justify-center">
    61→              <span className="text-blue-400 text-xs font-bold">F</span>
    62→            </div>
    63→            <span>LOOKSMAXX</span>
    64→          </Link>
    65→          <div className="hidden md:flex items-center gap-8">
    66→            <a href="#how-it-works" className="text-sm text-gray-300 hover:text-white transition-colors">
    67→              How it works
    68→            </a>
    69→            <a href="#your-plan" className="text-sm text-gray-300 hover:text-white transition-colors">
    70→              Your Plan
    71→            </a>
    72→            <a href="#pricing" className="text-sm text-gray-300 hover:text-white transition-colors">
    73→              Pricing
    74→            </a>
    75→            <Link
    76→              href="/gender"
    77→              className="text-sm font-medium bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-500 transition-colors"
    78→            >
    79→              Get started
    80→            </Link>
    81→          </div>
    82→          <div className="md:hidden">
    83→            <Link
    84→              href="/gender"
    85→              className="text-sm font-medium bg-blue-600 text-white px-3 py-2 rounded-lg hover:bg-blue-500 transition-colors"
    86→            >
    87→              Get started
    88→            </Link>
    89→          </div>
    90→        </div>
    91→      </div>
    92→    </nav>
    93→  );
    94→}
    95→
    96→// ============================================================================
    97→// ROTATING TEXT COMPONENT
    98→// ============================================================================
    99→function RotatingText() {
   100→  const [currentIndex, setCurrentIndex] = useState(0);
   101→  const [isAnimating, setIsAnimating] = useState(false);
   102→
   103→  useEffect(() => {
   104→    const timer = setInterval(() => {
   105→      setIsAnimating(true);
   106→      setTimeout(() => {
   107→        setCurrentIndex((prev) => (prev + 1) % rotatingWords.length);
   108→        setIsAnimating(false);
   109→      }, 300);
   110→    }, 2500);
   111→
   112→    return () => clearInterval(timer);
   113→  }, []);
   114→
   115→  return (
   116→    <span
   117→      className="text-blue-400 inline-block"
   118→      style={{
   119→        transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
   120→        minWidth: '280px',
   121→        opacity: isAnimating ? 0 : 1,
   122→        transform: isAnimating ? 'translateY(10px)' : 'translateY(0)',
   123→      }}
   124→    >
   125→      {rotatingWords[currentIndex]}
   126→    </span>
   127→  );
   128→}
   129→
   130→// ============================================================================
   131→// FLOATING ADVICE CARD COMPONENT
   132→// ============================================================================
   133→function FloatingAdviceCard() {
   134→  const [currentIndex, setCurrentIndex] = useState(0);
   135→  const [isAnimating, setIsAnimating] = useState(false);
   136→  const [yOffset, setYOffset] = useState(0);
   137→
   138→  // Cycle through advice data
   139→  useEffect(() => {
   140→    const timer = setInterval(() => {
   141→      setIsAnimating(true);
   142→      setTimeout(() => {
   143→        setCurrentIndex((prev) => (prev + 1) % adviceData.length);
   144→        setIsAnimating(false);
   145→      }, 200);
   146→    }, 3000);
   147→
   148→    return () => clearInterval(timer);
   149→  }, []);
   150→
   151→  // Gentle floating animation
   152→  useEffect(() => {
   153→    let animationFrame: number;
   154→    let startTime: number;
   155→
   156→    const animate = (timestamp: number) => {
   157→      if (!startTime) startTime = timestamp;
   158→      const elapsed = (timestamp - startTime) / 1000;
   159→      setYOffset(Math.sin(elapsed * 0.8) * 5);
   160→      animationFrame = requestAnimationFrame(animate);
   161→    };
   162→
   163→    animationFrame = requestAnimationFrame(animate);
   164→    return () => cancelAnimationFrame(animationFrame);
   165→  }, []);
   166→
   167→  const advice = adviceData[currentIndex];
   168→
   169→  return (
   170→    <motion.div
   171→      initial={{ opacity: 0, x: -30 }}
   172→      animate={{ opacity: 1, x: 0 }}
   173→      transition={{ delay: 0.6, duration: 0.5 }}
   174→      className="absolute top-[26%] left-[15%] lg:left-[20%] -translate-x-1/2 -translate-y-1/2 pointer-events-none z-20"
   175→    >
   176→      <div
   177→        className="rounded-xl bg-white/5 backdrop-blur-xl px-4 py-3 shadow-2xl border border-white/20 transition-all duration-300"
   178→        style={{ transform: `translateY(${yOffset}px)` }}
   179→      >
   180→        <div
   181→          className="text-[10px] uppercase tracking-wider font-semibold text-gray-300 mb-1 transition-opacity duration-200"
   182→          style={{ opacity: isAnimating ? 0 : 1 }}
   183→        >
   184→          {advice.profile}
   185→        </div>
   186→        <div
   187→          className="text-lg font-semibold text-gray-100 leading-tight transition-opacity duration-200"
   188→          style={{ opacity: isAnimating ? 0 : 1 }}
   189→        >
   190→          {advice.text}
   191→        </div>
   192→      </div>
   193→    </motion.div>
   194→  );
   195→}
   196→
   197→// ============================================================================
   198→// FLOATING SCORE CARD COMPONENT
   199→// ============================================================================
   200→// Score values to cycle through for continuous animation
   201→const scoreValues = [4.27, 6.42, 7.85, 5.91, 8.12, 3.56];
   202→
   203→function FloatingScoreCard() {
   204→  const [yOffset, setYOffset] = useState(0);
   205→  const [displayScore, setDisplayScore] = useState(0);
   206→  const scoreIndexRef = useRef(0);
   207→
   208→  // Animate score cycling continuously
   209→  useEffect(() => {
   210→    let animationFrame: number;
   211→    let timeoutId: NodeJS.Timeout;
   212→
   213→    const animateToScore = (targetScore: number, startScore: number, onComplete: () => void) => {
   214→      const duration = 1500;
   215→      const startTime = Date.now();
   216→
   217→      const animate = () => {
   218→        const elapsed = Date.now() - startTime;
   219→        const progress = Math.min(elapsed / duration, 1);
   220→        const eased = 1 - Math.pow(1 - progress, 3);
   221→        setDisplayScore(startScore + (targetScore - startScore) * eased);
   222→
   223→        if (progress < 1) {
   224→          animationFrame = requestAnimationFrame(animate);
   225→        } else {
   226→          onComplete();
   227→        }
   228→      };
   229→
   230→      animationFrame = requestAnimationFrame(animate);
   231→    };
   232→
   233→    const cycleScore = () => {
   234→      const currentIndex = scoreIndexRef.current;
   235→      const nextIndex = (currentIndex + 1) % scoreValues.length;
   236→      const currentScore = scoreValues[currentIndex];
   237→      const nextScore = scoreValues[nextIndex];
   238→
   239→      scoreIndexRef.current = nextIndex;
   240→
   241→      animateToScore(nextScore, currentScore, () => {
   242→        timeoutId = setTimeout(cycleScore, 2000);
   243→      });
   244→    };
   245→
   246→    // Initial animation from 0 to first score
   247→    const initialTimeout = setTimeout(() => {
   248→      animateToScore(scoreValues[0], 0, () => {
   249→        timeoutId = setTimeout(cycleScore, 2000);
   250→      });
   251→    }, 800);
   252→
   253→    return () => {
   254→      clearTimeout(initialTimeout);
   255→      clearTimeout(timeoutId);
   256→      cancelAnimationFrame(animationFrame);
   257→    };
   258→  }, []);
   259→
   260→  // Gentle floating animation
   261→  useEffect(() => {
   262→    let animationFrame: number;
   263→    let startTime: number;
   264→
   265→    const animate = (timestamp: number) => {
   266→      if (!startTime) startTime = timestamp;
   267→      const elapsed = (timestamp - startTime) / 1000;
   268→      setYOffset(Math.sin(elapsed * 0.6 + 1) * 6);
   269→      animationFrame = requestAnimationFrame(animate);
   270→    };
   271→
   272→    animationFrame = requestAnimationFrame(animate);
   273→    return () => cancelAnimationFrame(animationFrame);
   274→  }, []);
   275→
   276→  // Color interpolation based on score
   277→  const getScoreColor = (score: number) => {
   278→    if (score < 3) return 'rgb(239, 68, 68)';
   279→    if (score < 5) return 'rgb(247, 142, 71)';
   280→    if (score < 7) return 'rgb(234, 179, 8)';
   281→    return 'rgb(34, 197, 94)';
   282→  };
   283→
   284→  return (
   285→    <motion.div
   286→      initial={{ opacity: 0, x: 30 }}
   287→      animate={{ opacity: 1, x: 0 }}
   288→      transition={{ delay: 0.8, duration: 0.5 }}
   289→      className="absolute bottom-[30%] right-[5%] lg:right-[10%] translate-y-1/2 pointer-events-none z-20"
   290→    >
   291→      <div
   292→        className="rounded-xl bg-white/5 backdrop-blur-xl px-5 py-4 shadow-2xl border border-white/20 transition-all duration-300"
   293→        style={{ transform: `translateY(${yOffset}px)` }}
   294→      >
   295→        <div className="text-[10px] uppercase tracking-wider font-semibold text-gray-300 mb-1.5">
   296→          Overall Score
   297→        </div>
   298→        <div className="flex items-baseline gap-0.5">
   299→          <span
   300→            className="text-4xl font-bold tabular-nums transition-colors duration-300"
   301→            style={{ color: getScoreColor(displayScore) }}
   302→          >
   303→            {displayScore.toFixed(2)}
   304→          </span>
   305→          <span className="text-xl font-medium text-gray-400">/10</span>
   306→        </div>
   307→      </div>
   308→    </motion.div>
   309→  );
   310→}
