  return (
    <section id="how-it-works" className="py-20 md:py-28 px-6 scroll-mt-16 bg-black">
      <div className="max-w-6xl mx-auto">
        <motion.div
          variants={fadeIn}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true }}
