import { query } from '../../config/database.js'
import type {
  AudioJob,
  AudioJobPublic,
  AudioJobStatus,
  CreateAudioJobInput,
} from '../../types/audio.js'

export function toPublicJob(job: AudioJob): AudioJobPublic {
  return {
    id: job.id,
    jobType: job.job_type,
    status: job.status,
    audioDurationMinutes: job.audio_duration_minutes,
    progress: job.progress,
    resultUrl: job.result_url,
    resultData: job.result_data,
    errorMessage: job.error_message,
    createdAt: job.created_at,
    completedAt: job.completed_at,
  }
}

export async function create(input: CreateAudioJobInput): Promise<AudioJob> {
  const result = await query<AudioJob>(
    `INSERT INTO audio_jobs (user_id, job_type, status, audio_url, audio_duration_minutes)
     VALUES ($1, $2::audio_job_type, 'pending'::audio_job_status, $3, $4)
     RETURNING *`,
    [input.userId, input.jobType, input.audioUrl, input.audioDurationMinutes]
  )
  const job = result.rows[0]
  if (!job) {
    throw new Error('Failed to create audio job')
  }
  return job
}

export async function findById(id: string): Promise<AudioJob | null> {
  const result = await query<AudioJob>('SELECT * FROM audio_jobs WHERE id = $1', [id])
  return result.rows[0] ?? null
}

export async function findByIdAndUser(id: string, userId: string): Promise<AudioJob | null> {
  const result = await query<AudioJob>('SELECT * FROM audio_jobs WHERE id = $1 AND user_id = $2', [
    id,
    userId,
  ])
  return result.rows[0] ?? null
}

export async function findByUser(
  userId: string,
  limit: number = 20,
  offset: number = 0
): Promise<AudioJob[]> {
  const result = await query<AudioJob>(
    `SELECT * FROM audio_jobs
     WHERE user_id = $1
     ORDER BY created_at DESC
     LIMIT $2 OFFSET $3`,
    [userId, limit, offset]
  )
  return result.rows
}

export async function updateStatus(
  id: string,
  status: AudioJobStatus,
  errorMessage?: string
): Promise<AudioJob | null> {
  const shouldComplete = status === 'completed' || status === 'failed'
  const result = await query<AudioJob>(
    `UPDATE audio_jobs
     SET status = $2::audio_job_status,
         error_message = $3,
         completed_at = CASE WHEN $4 THEN NOW() ELSE NULL END,
         updated_at = NOW()
     WHERE id = $1
     RETURNING *`,
    [id, status, errorMessage ?? null, shouldComplete]
  )
  return result.rows[0] ?? null
}

export async function updateResult(
  id: string,
  resultUrl: string | null,
  resultData: Record<string, unknown> | null
): Promise<AudioJob | null> {
  const result = await query<AudioJob>(
    `UPDATE audio_jobs
     SET status = 'completed',
         progress = 100,
         result_url = $2,
         result_data = $3,
         completed_at = NOW(),
         updated_at = NOW()
     WHERE id = $1
     RETURNING *`,
    [id, resultUrl, resultData ? JSON.stringify(resultData) : null]
  )
  return result.rows[0] ?? null
}

export async function updateProgress(id: string, progress: number): Promise<AudioJob | null> {
  const clampedProgress = Math.max(0, Math.min(100, Math.round(progress)))
  const result = await query<AudioJob>(
    `UPDATE audio_jobs
     SET progress = $2,
         updated_at = NOW()
     WHERE id = $1
     RETURNING *`,
    [id, clampedProgress]
  )
  return result.rows[0] ?? null
}

export async function countPendingByUser(userId: string): Promise<number> {
  const result = await query<{ count: string }>(
    `SELECT COUNT(*) as count FROM audio_jobs
     WHERE user_id = $1 AND status IN ('pending', 'processing')`,
    [userId]
  )
  return parseInt(result.rows[0]?.count ?? '0', 10)
}

import { Queue } from 'bullmq'
import { z } from 'zod'
import { redis } from '../config/redis.js'
import * as audioRepository from '../db/repositories/audio-repository.js'
import * as userRepository from '../db/repositories/user-repository.js'
import { recordUsage } from '../middleware/usage-limit.js'
import type { AudioJobPublic, AudioJobQueueData, AudioJobType } from '../types/audio.js'
import type { UserPublic } from '../types/user.js'
import { TIER_LIMITS } from '../types/user.js'

const QUEUE_NAMES = {
  transcription: 'transcription-jobs',
  vocal_isolation: 'vocal-isolation-jobs',
} as const

const transcriptionQueue = new Queue<AudioJobQueueData>(QUEUE_NAMES.transcription, {
  connection: redis,
  defaultJobOptions: {
    attempts: 3,
    backoff: {
      type: 'exponential',
      delay: 5000,
    },
    removeOnComplete: 100,
    removeOnFail: 50,
  },
})

const vocalIsolationQueue = new Queue<AudioJobQueueData>(QUEUE_NAMES.vocal_isolation, {
  connection: redis,
  defaultJobOptions: {
    attempts: 3,
    backoff: {
      type: 'exponential',
      delay: 5000,
    },
    removeOnComplete: 100,
    removeOnFail: 50,
  },
})

export const submitJobSchema = z.object({
  audioUrl: z.string().url(),
  audioDurationMinutes: z.number().positive().max(60),
})

export class AudioServiceError extends Error {
  constructor(
    message: string,
    public statusCode: number = 400
  ) {
    super(message)
    this.name = 'AudioServiceError'
  }
}

export async function canSubmitJob(user: UserPublic, durationMinutes: number): Promise<boolean> {
  const limit = TIER_LIMITS[user.subscription_tier]
  const remaining = limit - user.usage_minutes_used

  if (durationMinutes > remaining) {
    return false
  }

  const pendingJobs = await audioRepository.countPendingByUser(user.id)
  const maxPendingJobs = user.subscription_tier === 'enterprise' ? 10 : 5

  if (pendingJobs >= maxPendingJobs) {
    return false
  }

  return true
}

export async function submitTranscriptionJob(
  user: UserPublic,
  audioUrl: string,
  audioDurationMinutes: number
): Promise<AudioJobPublic> {
  return submitJob(user, 'transcription', audioUrl, audioDurationMinutes)
}

export async function submitVocalIsolationJob(
  user: UserPublic,
  audioUrl: string,
  audioDurationMinutes: number
): Promise<AudioJobPublic> {
  return submitJob(user, 'vocal_isolation', audioUrl, audioDurationMinutes)
}

async function submitJob(
  user: UserPublic,
  jobType: AudioJobType,
  audioUrl: string,
  audioDurationMinutes: number
): Promise<AudioJobPublic> {
  const canSubmit = await canSubmitJob(user, audioDurationMinutes)
  if (!canSubmit) {
    throw new AudioServiceError(
      'Cannot submit job. Either usage limit exceeded or too many pending jobs.',
      402
    )
  }

  const job = await audioRepository.create({
    userId: user.id,
    jobType,
    audioUrl,
    audioDurationMinutes,
  })

  const queueData: AudioJobQueueData = {
    jobId: job.id,
    userId: user.id,
    audioUrl,
    audioDurationMinutes,
  }

  const queue = jobType === 'transcription' ? transcriptionQueue : vocalIsolationQueue

  await queue.add(job.id, queueData, {
    jobId: job.id,
  })

  return audioRepository.toPublicJob(job)
}

export async function getJobStatus(user: UserPublic, jobId: string): Promise<AudioJobPublic> {
  const job = await audioRepository.findByIdAndUser(jobId, user.id)
  if (!job) {
    throw new AudioServiceError('Job not found', 404)
  }
  return audioRepository.toPublicJob(job)
}

export async function getUserJobs(
  user: UserPublic,
  limit: number = 20,
  offset: number = 0
): Promise<AudioJobPublic[]> {
  const jobs = await audioRepository.findByUser(user.id, limit, offset)
  return jobs.map(audioRepository.toPublicJob)
}

export async function markJobCompleted(
  jobId: string,
  resultUrl: string | null,
  resultData: Record<string, unknown> | null
): Promise<void> {
  const job = await audioRepository.findById(jobId)
  if (!job) {
    throw new AudioServiceError('Job not found', 404)
  }

  await audioRepository.updateResult(jobId, resultUrl, resultData)

  await recordUsage(job.user_id, job.audio_duration_minutes)
}

export async function markJobFailed(jobId: string, errorMessage: string): Promise<void> {
  await audioRepository.updateStatus(jobId, 'failed', errorMessage)
}

export async function markJobProcessing(jobId: string): Promise<void> {
  await audioRepository.updateStatus(jobId, 'processing')
}

export async function updateJobProgress(jobId: string, progress: number): Promise<void> {
  await audioRepository.updateProgress(jobId, progress)
}

export async function resetUserUsage(userId: string): Promise<void> {
  await userRepository.resetUsageIfNeeded(userId)
}

export { QUEUE_NAMES }

import { Worker, Job } from 'bullmq'
import { redis } from '../config/redis.js'
import { env } from '../config/env.js'
import * as audioService from '../services/audio-service.js'
import type { AudioJobQueueData, TranscriptionResult } from '../types/audio.js'

const QUEUE_NAME = audioService.QUEUE_NAMES.transcription

async function processTranscriptionJob(job: Job<AudioJobQueueData>): Promise<void> {
  const { jobId, audioUrl } = job.data

  console.log(`Processing transcription job ${jobId}`)

  await audioService.markJobProcessing(jobId)
  await audioService.updateJobProgress(jobId, 10)

  try {
    // Update progress before API call
    await audioService.updateJobProgress(jobId, 30)

    const result = await transcribeAudio(audioUrl)

    // Update progress after API call completes
    await audioService.updateJobProgress(jobId, 90)

    await audioService.markJobCompleted(jobId, null, result as unknown as Record<string, unknown>)

    console.log(`Transcription job ${jobId} completed successfully`)
  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : 'Unknown error'
    console.error(`Transcription job ${jobId} failed:`, errorMessage)
    throw error
  }
}

async function transcribeAudio(audioUrl: string): Promise<TranscriptionResult> {
  if (env.DEEPGRAM_API_KEY) {
    return transcribeWithDeepgram(audioUrl, env.DEEPGRAM_API_KEY)
  }

  if (env.WHISPER_API_URL) {
    return transcribeWithWhisper(audioUrl, env.WHISPER_API_URL)
  }

  throw new Error('No transcription service configured. Set DEEPGRAM_API_KEY or WHISPER_API_URL.')
}

async function transcribeWithDeepgram(
  audioUrl: string,
  apiKey: string
): Promise<TranscriptionResult> {
  const response = await fetch(
    'https://api.deepgram.com/v1/listen?model=nova-2&smart_format=true',
    {
      method: 'POST',
      headers: {
        Authorization: `Token ${apiKey}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ url: audioUrl }),
    }
  )

  if (!response.ok) {
    const errorText = await response.text()
    throw new Error(`Deepgram API error: ${String(response.status)} - ${errorText}`)
  }

  const data = (await response.json()) as DeepgramResponse

  const channel = data.results?.channels?.[0]
  const alternative = channel?.alternatives?.[0]

  if (!alternative) {
    throw new Error('No transcription result from Deepgram')
  }

  return {
    text: alternative.transcript,
    segments: (alternative.words ?? []).map((word) => ({
      start: word.start,
      end: word.end,
      text: word.word,
      confidence: word.confidence,
    })),
    language: data.results?.channels?.[0]?.detected_language ?? 'en',
    confidence: alternative.confidence,
  }
}

async function transcribeWithWhisper(
  audioUrl: string,
  apiUrl: string
): Promise<TranscriptionResult> {
  const response = await fetch(apiUrl, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ audio_url: audioUrl }),
  })

  if (!response.ok) {
    const errorText = await response.text()
    throw new Error(`Whisper API error: ${String(response.status)} - ${errorText}`)
  }

  const data = (await response.json()) as WhisperResponse

  return {
    text: data.text,
    segments: (data.segments ?? []).map((segment) => ({
      start: segment.start,
      end: segment.end,
      text: segment.text,
      confidence: segment.confidence ?? 1.0,
    })),
    language: data.language ?? 'en',
    confidence: data.confidence ?? 1.0,
  }
}

interface DeepgramResponse {
  results?: {
    channels?: Array<{
      detected_language?: string
      alternatives?: Array<{
        transcript: string
        confidence: number
        words?: Array<{
          word: string
          start: number
          end: number
          confidence: number
        }>
      }>
    }>
  }
}

interface WhisperResponse {
  text: string
  language?: string
  confidence?: number
  segments?: Array<{
    start: number
    end: number
    text: string
    confidence?: number
  }>
}

export function startTranscriptionWorker(): Worker<AudioJobQueueData> {
  const worker = new Worker<AudioJobQueueData>(QUEUE_NAME, processTranscriptionJob, {
    connection: redis,
    concurrency: 3,
  })

  worker.on('completed', (job) => {
    console.log(`Job ${String(job.id)} completed`)
  })

  worker.on('failed', (job, err) => {
    if (job) {
      console.error(`Job ${String(job.id)} failed with error: ${err.message}`)
      audioService.markJobFailed(job.data.jobId, err.message).catch((e: unknown) => {
        console.error('Failed to mark job as failed:', e)
      })
    }
  })

  worker.on('error', (err) => {
    console.error('Worker error:', err)
  })

  console.log(`Transcription worker started, listening on queue: ${QUEUE_NAME}`)

  return worker
}

import { Worker, Job } from 'bullmq'
import { redis } from '../config/redis.js'
import { env } from '../config/env.js'
import * as audioService from '../services/audio-service.js'
import type { AudioJobQueueData, VocalIsolationResult } from '../types/audio.js'

const QUEUE_NAME = audioService.QUEUE_NAMES.vocal_isolation

async function processVocalIsolationJob(job: Job<AudioJobQueueData>): Promise<void> {
  const { jobId, audioUrl } = job.data

  console.log(`Processing vocal isolation job ${jobId}`)

  await audioService.markJobProcessing(jobId)
  await audioService.updateJobProgress(jobId, 10)

  try {
    const result = await isolateVocals(audioUrl, jobId)

    await audioService.markJobCompleted(
      jobId,
      result.vocalsUrl,
      result as unknown as Record<string, unknown>
    )

    console.log(`Vocal isolation job ${jobId} completed successfully`)
  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : 'Unknown error'
    console.error(`Vocal isolation job ${jobId} failed:`, errorMessage)
    throw error
  }
}

async function isolateVocals(audioUrl: string, jobId: string): Promise<VocalIsolationResult> {
  if (env.DOLBY_API_KEY) {
    return isolateWithDolby(audioUrl, env.DOLBY_API_KEY, jobId)
  }

  if (env.DEMUCS_API_URL) {
    return isolateWithDemucs(audioUrl, env.DEMUCS_API_URL, jobId)
  }

  throw new Error('No vocal isolation service configured. Set DOLBY_API_KEY or DEMUCS_API_URL.')
}

async function isolateWithDolby(
  audioUrl: string,
  apiKey: string,
  jobId: string
): Promise<VocalIsolationResult> {
  await audioService.updateJobProgress(jobId, 20)

  const startResponse = await fetch('https://api.dolby.com/media/enhance', {
    method: 'POST',
    headers: {
      Authorization: `Bearer ${apiKey}`,
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({
      input: audioUrl,
      output: 'dlb://out/vocals.wav',
      content: {
        type: 'voice_recording',
      },
    }),
  })

  if (!startResponse.ok) {
    const errorText = await startResponse.text()
    throw new Error(`Dolby API error: ${String(startResponse.status)} - ${errorText}`)
  }

  const startData = (await startResponse.json()) as DolbyStartResponse
  const dolbyJobId = startData.job_id

  await audioService.updateJobProgress(jobId, 30)

  let result: DolbyStatusResponse | null = null
  const maxAttempts = 60
  let attempts = 0

  while (attempts < maxAttempts) {
    await sleep(5000)

    const statusResponse = await fetch(`https://api.dolby.com/media/enhance?job_id=${dolbyJobId}`, {
      headers: {
        Authorization: `Bearer ${apiKey}`,
      },
    })

    if (!statusResponse.ok) {
      const errorText = await statusResponse.text()
      throw new Error(`Dolby status check error: ${String(statusResponse.status)} - ${errorText}`)
    }

    result = (await statusResponse.json()) as DolbyStatusResponse

    if (result.status === 'Success') {
      break
    }

    if (result.status === 'Failed') {
      throw new Error(`Dolby processing failed: ${result.error?.message ?? 'Unknown error'}`)
    }

    // Update progress based on poll iteration (30% to 85%)
    const progress = 30 + Math.min(55, Math.floor((attempts / maxAttempts) * 55))
    await audioService.updateJobProgress(jobId, progress)

    attempts++
  }

  if (!result || result.status !== 'Success') {
    throw new Error('Dolby processing timed out')
  }

  await audioService.updateJobProgress(jobId, 90)

  return {
    vocalsUrl: result.output ?? '',
    instrumentalUrl: '',
  }
}

async function isolateWithDemucs(
  audioUrl: string,
  apiUrl: string,
  jobId: string
): Promise<VocalIsolationResult> {
  await audioService.updateJobProgress(jobId, 30)

  const response = await fetch(apiUrl, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ audio_url: audioUrl }),
  })

  if (!response.ok) {
    const errorText = await response.text()
    throw new Error(`Demucs API error: ${String(response.status)} - ${errorText}`)
  }

  const data = (await response.json()) as DemucsResponse

  await audioService.updateJobProgress(jobId, 90)

  return {
    vocalsUrl: data.vocals_url,
    instrumentalUrl: data.instrumental_url ?? data.accompaniment_url ?? '',
  }
}

function sleep(ms: number): Promise<void> {
  return new Promise((resolve) => setTimeout(resolve, ms))
}

interface DolbyStartResponse {
  job_id: string
}

interface DolbyStatusResponse {
  status: 'Pending' | 'Running' | 'Success' | 'Failed'
  output?: string
  error?: {
    message: string
  }
}

interface DemucsResponse {
  vocals_url: string
  instrumental_url?: string
  accompaniment_url?: string
}

export function startVocalIsolationWorker(): Worker<AudioJobQueueData> {
  const worker = new Worker<AudioJobQueueData>(QUEUE_NAME, processVocalIsolationJob, {
    connection: redis,
    concurrency: 2,
  })

  worker.on('completed', (job) => {
    console.log(`Job ${String(job.id)} completed`)
  })

  worker.on('failed', (job, err) => {
    if (job) {
      console.error(`Job ${String(job.id)} failed with error: ${err.message}`)
      audioService.markJobFailed(job.data.jobId, err.message).catch((e: unknown) => {
        console.error('Failed to mark job as failed:', e)
      })
    }
  })

  worker.on('error', (err) => {
    console.error('Worker error:', err)
  })

  console.log(`Vocal isolation worker started, listening on queue: ${QUEUE_NAME}`)

  return worker
}

