   119→
   120→type AuthChangeCallback = (user: User | null) => void
   121→
   122→class BackendClient {
   123→  private accessToken: string | null = null
   124→  private refreshToken: string | null = null
   125→  private currentUser: User | null = null
   126→  private authChangeCallbacks: AuthChangeCallback[] = []
   127→  private initPromise: Promise<void>
   128→
   129→  // Mutex for token refresh to prevent concurrent refresh attempts
   130→  private refreshPromise: Promise<boolean> | null = null
   131→
   132→  constructor() {
   133→    this.initPromise = this.loadTokensFromStorage()
   134→  }
   135→
   136→  // Wait for initialization to complete (token loading)
   137→  async waitForInit(): Promise<void> {
   138→    return this.initPromise
