   140→
   141→  private async loadTokensFromStorage(): Promise<void> {
   142→    // Check if we're in a UXP environment (Adobe plugin)
   143→    // Use globalThis to check without triggering Vite's import analysis
   144→    const isUXP = typeof globalThis !== 'undefined' && 'uxp' in globalThis
   145→
   146→    if (!isUXP) {
   147→      // Browser dev mode - try localStorage fallback
   148→      try {
   149→        const stored = localStorage.getItem('splice-tokens')
   150→        if (stored) {
   151→          const data = JSON.parse(stored)
   152→          this.accessToken = data.accessToken || null
   153→          this.refreshToken = data.refreshToken || null
   154→        }
   155→      } catch {
   156→        console.log('[BackendClient] Running in browser dev mode, using localStorage')
   157→      }
   158→      return
   159→    }
   160→
   161→    try {
   162→      // UXP storage API - dynamic import to avoid Vite analysis
   163→      const uxpModule = 'uxp'
   164→      const uxp = await (Function('m', 'return import(m)')(uxpModule) as Promise<
   165→        typeof import('uxp')
   166→      >)
   167→      const storage = uxp.storage
   168→      const fs = storage.localFileSystem
   169→      const dataFolder = await fs.getDataFolder()
   170→
   171→      try {
   172→        const tokenFile = await dataFolder.getEntry('splice-tokens.json')
   173→        if (tokenFile) {
   174→          const contents = await tokenFile.read()
   175→          try {
   176→            const data = JSON.parse(contents)
   177→            this.accessToken = data.accessToken || null
   178→            this.refreshToken = data.refreshToken || null
   179→          } catch {
   180→            // Invalid JSON - delete corrupted token file
   181→            console.warn('[BackendClient] Corrupted token file, deleting')
   182→            await tokenFile.delete()
   183→          }
   184→        }
   185→      } catch {
   186→        // File doesn't exist yet, that's fine
   187→      }
   188→    } catch {
   189→      // Not in UXP environment (dev mode), use memory only
   190→      console.log('[BackendClient] Running outside UXP, tokens will not persist')
   191→    }
   192→  }
   193→
   194→  private async saveTokensToStorage(): Promise<void> {
   195→    const isUXP = typeof globalThis !== 'undefined' && 'uxp' in globalThis
   196→
   197→    if (!isUXP) {
   198→      // Browser dev mode - use localStorage
   199→      try {
   200→        localStorage.setItem(
   201→          'splice-tokens',
   202→          JSON.stringify({
   203→            accessToken: this.accessToken,
   204→            refreshToken: this.refreshToken,
   205→          })
   206→        )
   207→      } catch {
   208→        // localStorage not available
   209→      }
   210→      return
   211→    }
   212→
   213→    try {
   214→      const uxpModule = 'uxp'
   215→      const uxp = await (Function('m', 'return import(m)')(uxpModule) as Promise<
   216→        typeof import('uxp')
   217→      >)
   218→      const storage = uxp.storage
   219→      const fs = storage.localFileSystem
   220→      const dataFolder = await fs.getDataFolder()
   221→
   222→      const tokenFile = await dataFolder.createFile('splice-tokens.json', { overwrite: true })
   223→      await tokenFile.write(
   224→        JSON.stringify({
   225→          accessToken: this.accessToken,
   226→          refreshToken: this.refreshToken,
   227→        })
   228→      )
   229→    } catch {
   230→      // Not in UXP environment
   231→    }
   232→  }
   233→
   234→  private async clearTokensFromStorage(): Promise<void> {
   235→    const isUXP = typeof globalThis !== 'undefined' && 'uxp' in globalThis
   236→
   237→    if (!isUXP) {
   238→      // Browser dev mode - use localStorage
   239→      try {
   240→        localStorage.removeItem('splice-tokens')
   241→      } catch {
   242→        // localStorage not available
   243→      }
   244→      return
   245→    }
   246→
   247→    try {
   248→      const uxpModule = 'uxp'
   249→      const uxp = await (Function('m', 'return import(m)')(uxpModule) as Promise<
   250→        typeof import('uxp')
   251→      >)
   252→      const storage = uxp.storage
   253→      const fs = storage.localFileSystem
   254→      const dataFolder = await fs.getDataFolder()
   255→
   256→      try {
   257→        const tokenFile = await dataFolder.getEntry('splice-tokens.json')
   258→        if (tokenFile) {
   259→          await tokenFile.delete()
   260→        }
   261→      } catch {
   262→        // File doesn't exist
   263→      }
   264→    } catch {
   265→      // Not in UXP environment
   266→    }
   267→  }
   268→
   269→  async setTokens(tokens: AuthTokens): Promise<void> {
   270→    this.accessToken = tokens.accessToken
   271→    this.refreshToken = tokens.refreshToken
   272→    await this.saveTokensToStorage()
   273→  }
   274→
   275→  async clearTokens(): Promise<void> {
   276→    this.accessToken = null
   277→    this.refreshToken = null
   278→    this.currentUser = null
   279→    await this.clearTokensFromStorage()
   280→    this.notifyAuthChange(null)
   281→  }
   282→
   283→  onAuthChange(callback: AuthChangeCallback): () => void {
   284→    this.authChangeCallbacks.push(callback)
   285→    return () => {
   286→      this.authChangeCallbacks = this.authChangeCallbacks.filter((cb) => cb !== callback)
   287→    }
   288→  }
   289→
   290→  private notifyAuthChange(user: User | null): void {
   291→    this.currentUser = user
   292→    this.authChangeCallbacks.forEach((cb) => cb(user))
   293→  }
   294→
   295→  /**
   296→   * Fetch with timeout support
   297→   */
   298→  private async fetchWithTimeout(
   299→    url: string,
   300→    options: RequestInit = {},
   301→    timeoutMs: number = DEFAULT_TIMEOUT_MS
   302→  ): Promise<Response> {
   303→    const controller = new AbortController()
   304→    const timeoutId = setTimeout(() => controller.abort(), timeoutMs)
   305→
   306→    try {
   307→      const response = await fetch(url, {
   308→        ...options,
   309→        signal: controller.signal,
   310→      })
   311→      return response
   312→    } catch (error) {
   313→      if (error instanceof Error && error.name === 'AbortError') {
   314→        throw new Error(`Request timed out after ${timeoutMs}ms`)
   315→      }
   316→      throw error
   317→    } finally {
   318→      clearTimeout(timeoutId)
   319→    }
   320→  }
   321→
   322→  private async request<T>(
   323→    endpoint: string,
   324→    options: RequestInit = {},
   325→    timeoutMs: number = DEFAULT_TIMEOUT_MS
   326→  ): Promise<T> {
   327→    const apiBase = getApiBase()
   328→    const headers: Record<string, string> = {
   329→      'Content-Type': 'application/json',
   330→      ...(options.headers as Record<string, string>),
   331→    }
   332→
   333→    if (this.accessToken) {
   334→      headers['Authorization'] = `Bearer ${this.accessToken}`
   335→    }
   336→
   337→    const response = await this.fetchWithTimeout(
   338→      `${apiBase}${endpoint}`,
   339→      {
   340→        ...options,
   341→        headers,
   342→      },
   343→      timeoutMs
   344→    )
   345→
   346→    // Handle 401 with token refresh
   347→    if (response.status === 401 && this.refreshToken) {
   348→      const refreshed = await this.refreshTokens()
   349→      if (refreshed) {
   350→        headers['Authorization'] = `Bearer ${this.accessToken}`
   351→        const retryResponse = await this.fetchWithTimeout(
   352→          `${apiBase}${endpoint}`,
   353→          {
   354→            ...options,
   355→            headers,
   356→          },
   357→          timeoutMs
   358→        )
   359→        return this.handleResponse<T>(retryResponse)
   360→      }
   361→    }
   362→
   363→    return this.handleResponse<T>(response)
   364→  }
   365→
   366→  private async handleResponse<T>(response: Response): Promise<T> {
   367→    if (!response.ok) {
   368→      const error = await response.json().catch(() => ({ message: response.statusText }))
   369→      throw new Error(error.error || error.message || 'Request failed')
   370→    }
   371→
   372→    const result: ApiResponse<T> = await response.json()
   373→    if (!result.success || result.data === undefined) {
   374→      throw new Error(result.error || result.message || 'Request failed')
   375→    }
   376→    return result.data
   377→  }
   378→
   379→  /**
   380→   * Refresh tokens with mutex to prevent concurrent refresh attempts.
   381→   * If a refresh is already in progress, wait for it instead of starting a new one.
   382→   */
   383→  private async refreshTokens(): Promise<boolean> {
   384→    // If a refresh is already in progress, wait for it
   385→    if (this.refreshPromise) {
   386→      return this.refreshPromise
   387→    }
   388→
   389→    // Start new refresh and store the promise
   390→    this.refreshPromise = this.doRefreshTokens()
   391→
   392→    try {
   393→      return await this.refreshPromise
   394→    } finally {
   395→      // Clear the mutex when done
   396→      this.refreshPromise = null
   397→    }
   398→  }
   399→
   400→  private async doRefreshTokens(): Promise<boolean> {
   401→    try {
   402→      const apiBase = getApiBase()
   403→      const response = await this.fetchWithTimeout(
   404→        `${apiBase}/auth/refresh`,
   405→        {
   406→          method: 'POST',
   407→          headers: { 'Content-Type': 'application/json' },
   408→          body: JSON.stringify({ refreshToken: this.refreshToken }),
   409→        },
   410→        DEFAULT_TIMEOUT_MS
   411→      )
   412→
   413→      if (!response.ok) {
   414→        await this.clearTokens()
   415→        return false
   416→      }
   417→
   418→      const result: ApiResponse<AuthTokens> = await response.json()
   419→      if (!result.success || !result.data) {
   420→        await this.clearTokens()
   421→        return false
   422→      }
   423→      await this.setTokens(result.data)
   424→      return true
   425→    } catch {
   426→      await this.clearTokens()
   427→      return false
   428→    }
   429→  }
   430→
   431→  // ==================== Auth ====================
   432→
   433→  async login(email: string, password: string): Promise<{ user: User; tokens: AuthTokens }> {
   434→    const data = await this.request<{ user: User; tokens: AuthTokens }>('/auth/login', {
   435→      method: 'POST',
   436→      body: JSON.stringify({ email, password }),
   437→    })
   438→    await this.setTokens(data.tokens)
   439→    this.notifyAuthChange(data.user)
   440→    return data
   441→  }
   442→
   443→  async register(
   444→    email: string,
   445→    password: string,
   446→    name?: string
   447→  ): Promise<{ user: User; tokens: AuthTokens }> {
   448→    const data = await this.request<{ user: User; tokens: AuthTokens }>('/auth/register', {
   449→      method: 'POST',
   450→      body: JSON.stringify({ email, password, name }),
   451→    })
   452→    await this.setTokens(data.tokens)
   453→    this.notifyAuthChange(data.user)
   454→    return data
   455→  }
   456→
   457→  async logout(): Promise<void> {
   458→    try {
   459→      const apiBase = getApiBase()
   460→      await this.fetchWithTimeout(
   461→        `${apiBase}/auth/logout`,
   462→        {
   463→          method: 'POST',
   464→          headers: {
   465→            'Content-Type': 'application/json',
   466→            ...(this.accessToken ? { Authorization: `Bearer ${this.accessToken}` } : {}),
   467→          },
   468→          body: JSON.stringify({ refreshToken: this.refreshToken }),
   469→        },
   470→        DEFAULT_TIMEOUT_MS
   471→      )
   472→    } finally {
   473→      await this.clearTokens()
   474→    }
   475→  }
   476→
   477→  async getMe(): Promise<User> {
   478→    const user = await this.request<User>('/auth/me')
   479→    this.notifyAuthChange(user)
   480→    return user
   481→  }
   482→
   483→  isAuthenticated(): boolean {
   484→    return Boolean(this.accessToken)
   485→  }
   486→
   487→  getUser(): User | null {
   488→    return this.currentUser
   489→  }
   490→
   491→  // ==================== Usage ====================
   492→
   493→  async getUsage(): Promise<UsageStats> {
   494→    return this.request<UsageStats>('/usage')
   495→  }
   496→
   497→  // Check if user can submit a job with given duration
   498→  // Uses /usage/check which returns remaining minutes
   499→  async checkUsageLimit(minutes: number): Promise<{ allowed: boolean; remaining: number }> {
   500→    return this.request<{ allowed: boolean; remaining: number }>(`/usage/check?minutes=${minutes}`)
   501→  }
   502→
   503→  // Check if user can submit audio job (considers pending jobs too)
   504→  // Uses /audio/check which returns reason if not allowed
   505→  async canSubmitAudioJob(minutes: number): Promise<{ allowed: boolean; reason?: string }> {
   506→    return this.request<{ allowed: boolean; reason?: string }>(`/audio/check?minutes=${minutes}`)
   507→  }
   508→
   509→  // ==================== File Upload ====================
   510→
   511→  // Get a presigned URL for uploading a file to S3
   512→  async getPresignedUploadUrl(
   513→    filename: string,
   514→    contentType: string,
   515→    fileSize: number
   516→  ): Promise<PresignedUrlResponse> {
   517→    return this.request<PresignedUrlResponse>('/audio/presigned-url', {
   518→      method: 'POST',
   519→      body: JSON.stringify({ filename, contentType, fileSize }),
   520→    })
   521→  }
   522→
   523→  // Upload a file to S3 using a presigned URL
   524→  // Returns the public URL of the uploaded file
   525→  async uploadFileToS3(file: File, presignedUrl: string): Promise<void> {
   526→    const response = await fetch(presignedUrl, {
   527→      method: 'PUT',
   528→      body: file,
   529→      headers: {
   530→        'Content-Type': file.type,
   531→      },
   532→    })
   533→
   534→    if (!response.ok) {
   535→      throw new Error(`Upload failed: ${response.statusText}`)
   536→    }
   537→  }
   538→
   539→  // Convenience method: get presigned URL and upload file in one call
   540→  async uploadFile(file: File): Promise<UploadResult> {
   541→    const presigned = await this.getPresignedUploadUrl(file.name, file.type, file.size)
   542→
   543→    await this.uploadFileToS3(file, presigned.uploadUrl)
   544→
   545→    return {
   546→      publicUrl: presigned.publicUrl,
   547→      fileKey: presigned.fileKey,
   548→    }
   549→  }
   550→
   551→  // ==================== Audio Jobs ====================
   552→
   553→  // Submit a transcription job
   554→  // audioUrl: URL to the audio file (must be pre-uploaded)
   555→  // audioDurationMinutes: duration of the audio in minutes
   556→  async submitTranscription(audioUrl: string, audioDurationMinutes: number): Promise<AudioJob> {
   557→    return this.request<AudioJob>('/audio/transcribe', {
   558→      method: 'POST',
   559→      body: JSON.stringify({ audioUrl, audioDurationMinutes }),
   560→    })
   561→  }
   562→
   563→  // Submit a vocal isolation job
   564→  // audioUrl: URL to the audio file (must be pre-uploaded)
   565→  // audioDurationMinutes: duration of the audio in minutes
   566→  async submitVocalIsolation(audioUrl: string, audioDurationMinutes: number): Promise<AudioJob> {
   567→    return this.request<AudioJob>('/audio/isolate-vocals', {
   568→      method: 'POST',
   569→      body: JSON.stringify({ audioUrl, audioDurationMinutes }),
   570→    })
   571→  }
   572→
   573→  async getJobs(limit: number = 20, offset: number = 0): Promise<AudioJob[]> {
   574→    return this.request<AudioJob[]>(`/audio/jobs?limit=${limit}&offset=${offset}`)
   575→  }
   576→
   577→  async getJob(jobId: string): Promise<AudioJob> {
   578→    return this.request<AudioJob>(`/audio/jobs/${jobId}`)
   579→  }
   580→
   581→  /**
   582→   * Poll a job until it completes or fails.
   583→   * Supports cancellation via AbortSignal.
   584→   *
   585→   * @param jobId - The job ID to poll
   586→   * @param onProgress - Optional callback for progress updates
   587→   * @param options - Polling options including intervalMs, maxAttempts, and signal for cancellation
   588→   * @returns The completed job
   589→   * @throws Error if job fails, times out, or is cancelled
   590→   */
   591→  async pollJobUntilComplete(
   592→    jobId: string,
   593→    onProgress?: (job: AudioJob) => void,
   594→    options: PollOptions = {}
   595→  ): Promise<AudioJob> {
   596→    const { intervalMs = 2000, maxAttempts = 300, signal } = options // Default 10 min timeout
   597→
   598→    return new Promise((resolve, reject) => {
   599→      let attempts = 0
   600→      let timeoutId: ReturnType<typeof setTimeout> | null = null
   601→
   602→      // Handle abort signal
   603→      const abortHandler = () => {
   604→        if (timeoutId) {
   605→          clearTimeout(timeoutId)
   606→        }
   607→        reject(new Error('Polling cancelled'))
   608→      }
   609→
   610→      if (signal) {
   611→        if (signal.aborted) {
   612→          reject(new Error('Polling cancelled'))
   613→          return
   614→        }
   615→        signal.addEventListener('abort', abortHandler)
   616→      }
   617→
   618→      const cleanup = () => {
   619→        if (signal) {
   620→          signal.removeEventListener('abort', abortHandler)
   621→        }
   622→      }
   623→
   624→      const poll = async () => {
   625→        // Check if aborted before making request
   626→        if (signal?.aborted) {
   627→          cleanup()
   628→          reject(new Error('Polling cancelled'))
   629→          return
   630→        }
   631→
   632→        try {
   633→          attempts++
   634→          const job = await this.getJob(jobId)
   635→          onProgress?.(job)
   636→
   637→          if (job.status === 'completed') {
   638→            cleanup()
   639→            resolve(job)
   640→          } else if (job.status === 'failed') {
   641→            cleanup()
   642→            reject(new Error(job.errorMessage || 'Job failed'))
   643→          } else if (attempts >= maxAttempts) {
   644→            cleanup()
   645→            reject(new Error('Job polling timed out'))
   646→          } else {
   647→            // Schedule next poll
   648→            timeoutId = setTimeout(poll, intervalMs)
   649→          }
   650→        } catch (error) {
   651→          cleanup()
   652→          reject(error)
   653→        }
   654→      }
   655→
   656→      poll()
   657→    })
   658→  }
   659→
