   321→      name: 'Scene Detection',
   322→      description: 'Automatically detect scene changes and suggest cut points',
   323→      premium: false,
   324→    },
   325→    {
   326→      id: 'smart-cut' as AnalysisType,
   327→      name: 'Smart Cut Suggestions',
   328→      description: 'AI-powered suggestions for optimal edit points',
   329→      premium: false,
   330→    },
   331→    {
   332→      id: 'audio-analysis' as AnalysisType,
   333→      name: 'Audio Analysis',
   334→      description: 'Analyze audio for beats, silence, and speech patterns',
   335→      premium: true,
   336→    },
   337→    {
   338→      id: 'content' as AnalysisType,
   339→      name: 'Content Analysis',
   340→      description: 'Deep analysis of clip content and themes',
   341→      premium: true,
   342→    },
   343→  ]
   344→
   345→  private async handleToolClick(toolId: AnalysisType, isPremiumTool: boolean): Promise<void> {
   346→    // Check if service is configured
   347→    if (!isAIServiceConfigured()) {
   348→      logger.warn('AI service not configured')
   349→      return
   350→    }
   351→
   352→    // Check if clips are selected
   353→    if (this.selectedClips.length === 0) {
   354→      logger.warn('No clips selected for analysis')
   355→      return
   356→    }
   357→
   358→    // Check premium requirement
   359→    if (isPremiumTool && !this.isPremium) {
   360→      this.dispatchEvent(new CustomEvent('upgrade-needed', { bubbles: true, composed: true }))
   361→      return
   362→    }
   363→
   364→    // Check operation limit for free tier
   365→    if (!this.isPremium && !canPerformOperation()) {
   366→      this.dispatchEvent(new CustomEvent('upgrade-needed', { bubbles: true, composed: true }))
   367→      return
   368→    }
   369→
   370→    this.isAnalyzing = true
   371→    this.analysisResult = null
   372→
   373→    try {
   374→      // Use first selected clip for analysis
   375→      const clip = this.selectedClips[0]
   376→
   377→      const result = await analyzeClip({
   378→        clipData: {
   379→          duration: clip.endTime - clip.startTime,
   380→          frameRate: 24, // Would come from actual project settings
   381→          audioChannels: 2,
   382→          metadata: { name: clip.name },
   383→        },
   384→        analysisType: toolId,
   385→      })
   386→
   387→      this.analysisResult = result
   388→
   389→      // Decrement operations for free tier
   390→      if (!this.isPremium && result.success) {
   391→        incrementOperationsUsed()
   392→        this.dispatchEvent(new CustomEvent('operation-used', { bubbles: true, composed: true }))
   393→      }
   394→    } catch (error) {
   395→      logger.error('Analysis failed', error)
   396→    } finally {
   397→      this.isAnalyzing = false
   398→    }
   399→  }
   400→
   401→  private canUseTool(isPremiumTool: boolean): boolean {
   402→    if (!isAIServiceConfigured()) return false
   403→    if (this.selectedClips.length === 0) return false
   404→    if (isPremiumTool && !this.isPremium) return false
   405→    if (!this.isPremium && !canPerformOperation()) return false
   406→    return true
   407→  }
   408→
   409→  // ==================== File Upload Methods ====================
   410→
   411→  private handleDragOver(e: DragEvent): void {
   412→    e.preventDefault()
   413→    e.stopPropagation()
   414→    if (!this.user) return
   415→    this.isDragOver = true
   416→  }
   417→
   418→  private handleDragLeave(e: DragEvent): void {
   419→    e.preventDefault()
   420→    e.stopPropagation()
   421→    this.isDragOver = false
   422→  }
   423→
   424→  private handleDrop(e: DragEvent): void {
   425→    e.preventDefault()
   426→    e.stopPropagation()
   427→    this.isDragOver = false
   428→
   429→    if (!this.user) return
   430→
   431→    const files = e.dataTransfer?.files
   432→    if (files && files.length > 0) {
   433→      this.handleFileSelect(files[0])
   434→    }
   435→  }
   436→
   437→  private handleFileInputChange(e: Event): void {
   438→    const input = e.target as HTMLInputElement
   439→    if (input.files && input.files.length > 0) {
   440→      this.handleFileSelect(input.files[0])
   441→    }
   442→  }
   443→
   444→  private handleFileSelect(file: File): void {
   445→    // Validate file type
   446→    const validTypes = [
   447→      'audio/mpeg',
   448→      'audio/mp3',
   449→      'audio/wav',
   450→      'audio/wave',
   451→      'audio/x-wav',
   452→      'audio/aac',
   453→      'audio/ogg',
   454→      'audio/flac',
   455→      'audio/m4a',
   456→      'audio/x-m4a',
   457→      'video/mp4',
   458→      'video/quicktime',
   459→    ]
   460→
   461→    if (!validTypes.includes(file.type)) {
   462→      this.errorMessage = 'Invalid file type. Please select an audio or video file.'
   463→      this.uploadState = 'error'
   464→      return
   465→    }
   466→
   467→    // Validate file size (500MB max)
   468→    const maxSize = 500 * 1024 * 1024
   469→    if (file.size > maxSize) {
   470→      this.errorMessage = 'File too large. Maximum size is 500MB.'
   471→      this.uploadState = 'error'
   472→      return
   473→    }
   474→
   475→    this.selectedFile = file
   476→    this.uploadState = 'idle'
   477→    this.errorMessage = ''
   478→    this.currentJob = null
   479→  }
   480→
   481→  private clearSelectedFile(): void {
   482→    this.selectedFile = null
   483→    this.uploadState = 'idle'
   484→    this.errorMessage = ''
   485→    this.currentJob = null
   486→
   487→    // Reset file input
   488→    const fileInput = this.shadowRoot?.querySelector('.file-input') as HTMLInputElement
   489→    if (fileInput) {
   490→      fileInput.value = ''
   491→    }
   492→  }
   493→
   494→  private async handleCloudTool(tool: CloudTool): Promise<void> {
   495→    if (!this.selectedFile || !this.user) return
   496→
   497→    // Estimate duration (rough: 1 min per 10MB for now)
   498→    // In production, you'd get actual duration from audio metadata
   499→    const estimatedMinutes = Math.max(1, Math.ceil(this.selectedFile.size / (10 * 1024 * 1024)))
   500→
   501→    // Check if user can submit job
   502→    try {
   503→      const canSubmit = await backendClient.canSubmitAudioJob(estimatedMinutes)
   504→      if (!canSubmit.allowed) {
   505→        this.errorMessage = canSubmit.reason || 'Cannot submit job at this time.'
   506→        this.uploadState = 'error'
   507→        return
   508→      }
   509→    } catch (error) {
   510→      this.errorMessage = error instanceof Error ? error.message : 'Failed to check usage limits.'
   511→      this.uploadState = 'error'
   512→      return
   513→    }
   514→
   515→    this.uploadState = 'uploading'
   516→    this.uploadProgress = 0
   517→    this.errorMessage = ''
   518→
   519→    try {
   520→      // Upload file to S3
   521→      logger.info('Uploading file to S3', { filename: this.selectedFile.name })
   522→      const uploadResult = await backendClient.uploadFile(this.selectedFile)
   523→      this.uploadProgress = 50
   524→      logger.info('File uploaded', { publicUrl: uploadResult.publicUrl })
   525→
   526→      // Submit job
   527→      this.uploadState = 'processing'
   528→      logger.info('Submitting job', { tool, audioUrl: uploadResult.publicUrl })
   529→
   530→      let job: AudioJob
   531→      if (tool === 'transcription') {
   532→        job = await backendClient.submitTranscription(uploadResult.publicUrl, estimatedMinutes)
   533→      } else {
   534→        job = await backendClient.submitVocalIsolation(uploadResult.publicUrl, estimatedMinutes)
   535→      }
   536→
   537→      this.currentJob = job
   538→      logger.info('Job submitted', { jobId: job.id })
   539→
   540→      // Poll for completion with real-time progress updates
   541→      const completedJob = await backendClient.pollJobUntilComplete(
   542→        job.id,
   543→        (updatedJob) => {
   544→          this.currentJob = updatedJob
   545→          // Use server-reported progress (scaled: upload=0-50%, processing=50-100%)
   546→          if (updatedJob.status === 'processing') {
   547→            // Scale job progress (0-100) to display progress (50-100)
   548→            this.uploadProgress = 50 + Math.floor(updatedJob.progress / 2)
   549→          }
   550→        },
   551→        { intervalMs: 2000, maxAttempts: 300 }
   552→      )
   553→
   554→      this.currentJob = completedJob
   555→      this.uploadState = 'complete'
   556→      this.uploadProgress = 100
   557→      logger.info('Job completed', { jobId: completedJob.id })
   558→    } catch (error) {
   559→      logger.error('Job failed', error)
   560→      this.errorMessage = error instanceof Error ? error.message : 'Job failed.'
   561→      this.uploadState = 'error'
   562→    }
   563→  }
   564→
   565→  private formatFileSize(bytes: number): string {
   566→    if (bytes < 1024) return `${bytes} B`
   567→    if (bytes < 1024 * 1024) return `${(bytes / 1024).toFixed(1)} KB`
   568→    return `${(bytes / (1024 * 1024)).toFixed(1)} MB`
   569→  }
   570→
   571→  private getStatusText(): string {
   572→    switch (this.uploadState) {
   573→      case 'uploading':
   574→        return 'Uploading file...'
   575→      case 'processing':
   576→        if (this.currentJob?.status === 'processing') {
   577→          const progress = this.currentJob.progress
   578→          if (progress < 30) return 'Starting...'
   579→          if (progress < 60) return 'Processing audio...'
   580→          if (progress < 90) return 'Analyzing...'
   581→          return 'Finishing up...'
   582→        }
   583→        return 'Queued...'
   584→      case 'complete':
   585→        return 'Complete!'
   586→      case 'error':
   587→        return this.errorMessage || 'Error'
   588→      default:
   589→        return ''
   590→    }
   591→  }
   592→
   593→  private renderUploadSection() {
   594→    const isDisabled = !this.user
   595→    const isBusy = this.uploadState === 'uploading' || this.uploadState === 'processing'
   596→
   597→    return html`
   598→      <div class="section">
   599→        <div class="section-title">Cloud Audio Processing</div>
   600→
   601→        ${!this.user
   602→          ? html`
   603→              <div class="warning-banner">
   604→                <h4>Sign In Required</h4>
   605→                <p>Please sign in to use cloud audio processing features.</p>
   606→              </div>
   607→            `
   608→          : ''}
   609→
   610→        ${this.selectedFile
   611→          ? html`
   612→              <div class="selected-file">
   613→                <div class="file-info">
   614→                  <div class="file-name">${this.selectedFile.name}</div>
   615→                  <div class="file-size">${this.formatFileSize(this.selectedFile.size)}</div>
   616→                </div>
   617→                ${!isBusy
   618→                  ? html`
   619→                      <button class="remove-file" @click=${this.clearSelectedFile} title="Remove file">
   620→                        ✕
   621→                      </button>
   622→                    `
   623→                  : ''}
   624→              </div>
   625→            `
   626→          : html`
   627→              <div
   628→                class="upload-zone ${isDisabled ? 'disabled' : ''} ${this.isDragOver ? 'dragover' : ''}"
   629→                @click=${() => !isDisabled && this.shadowRoot?.querySelector<HTMLInputElement>('.file-input')?.click()}
   630→                @dragover=${this.handleDragOver}
   631→                @dragleave=${this.handleDragLeave}
   632→                @drop=${this.handleDrop}
   633→              >
   634→                <div class="upload-icon">📁</div>
   635→                <div class="upload-text">
   636→                  <strong>Click to upload</strong> or drag and drop<br />
   637→                  Audio files (MP3, WAV, AAC, M4A) up to 500MB
   638→                </div>
   639→              </div>
   640→              <input
   641→                type="file"
   642→                class="file-input"
   643→                accept="audio/*,video/mp4,video/quicktime"
   644→                @change=${this.handleFileInputChange}
   645→              />
   646→            `}
   647→
   648→        ${this.selectedFile && !isBusy && this.uploadState !== 'complete'
   649→          ? html`
   650→              <div class="cloud-tools-grid">
   651→                <button
   652→                  class="cloud-tool-btn"
   653→                  @click=${() => this.handleCloudTool('transcription')}
   654→                  ?disabled=${!this.user}
   655→                >
   656→                  <span class="icon">🎤</span>
   657→                  <span>Transcribe Audio</span>
   658→                </button>
   659→                <button
   660→                  class="cloud-tool-btn"
   661→                  @click=${() => this.handleCloudTool('vocal_isolation')}
   662→                  ?disabled=${!this.user}
   663→                >
   664→                  <span class="icon">🎵</span>
   665→                  <span>Isolate Vocals</span>
   666→                </button>
   667→              </div>
   668→            `
   669→          : ''}
   670→
   671→        ${isBusy || this.uploadState === 'complete' || this.uploadState === 'error'
   672→          ? html`
   673→              <div class="progress-section">
   674→                <div class="progress-header">
   675→                  <span
   676→                    class="progress-status ${this.uploadState === 'error'
   677→                      ? 'error'
   678→                      : this.uploadState === 'complete'
   679→                        ? 'complete'
   680→                        : ''}"
   681→                  >
   682→                    ${this.getStatusText()}
   683→                  </span>
   684→                  ${this.uploadState === 'complete' || this.uploadState === 'error'
   685→                    ? html`
   686→                        <button class="remove-file" @click=${this.clearSelectedFile} title="Clear">
   687→                          ✕
   688→                        </button>
   689→                      `
   690→                    : ''}
   691→                </div>
   692→                ${isBusy
   693→                  ? html`
   694→                      <sp-progress-bar value=${this.uploadProgress} label="Progress"></sp-progress-bar>
   695→                    `
   696→                  : ''}
   697→                ${this.uploadState === 'complete' && this.currentJob?.resultData
   698→                  ? html`
   699→                      <div class="job-result">
   700→                        <pre>${JSON.stringify(this.currentJob.resultData, null, 2)}</pre>
   701→                      </div>
   702→                    `
   703→                  : ''}
   704→              </div>
   705→            `
   706→          : ''}
   707→      </div>
   708→    `
   709→  }
   710→
   711→  render() {
   712→    if (this.isAnalyzing) {
   713→      return html`
   714→        <div class="loading-state">
   715→          <sp-progress-circle indeterminate size="l" label="Analyzing clip"></sp-progress-circle>
   716→          <p>Analyzing clip...</p>
   717→        </div>
   718→      `
   719→    }
   720→
   721→    return html`
   722→      <!-- Cloud Audio Processing Section -->
   723→      ${this.renderUploadSection()}
   724→
   725→      <!-- Local AI Tools Section -->
   726→      ${isAIServiceConfigured()
   727→        ? html`
   728→            ${this.selectedClips.length === 0
   729→              ? html`
   730→                  <div class="warning-banner">
   731→                    <h4>No Clips Selected</h4>
   732→                    <p>Select clips in the Selection tab or Premiere Pro timeline to use local AI tools.</p>
   733→                  </div>
   734→                `
   735→              : ''}
   736→
   737→            <div class="section">
   738→              <div class="section-title">Local AI Tools</div>
   739→              <div class="ai-tools-grid">
   740→                ${this.aiTools.map(
   741→                  (tool) => html`
   742→                    <div
   743→                      class="ai-tool-card ${this.canUseTool(tool.premium) ? '' : 'disabled'}"
   744→                      @click=${() =>
   745→                        this.canUseTool(tool.premium) && this.handleToolClick(tool.id, tool.premium)}
   746→                    >
   747→                      <div class="tool-header">
   748→                        <span class="tool-name">${tool.name}</span>
   749→                        ${tool.premium
   750→                          ? html`<span class="tool-badge premium">PRO</span>`
   751→                          : html`<span class="tool-badge">AI</span>`}
   752→                      </div>
   753→                      <div class="tool-description">${tool.description}</div>
   754→                    </div>
   755→                  `
   756→                )}
   757→              </div>
   758→            </div>
   759→
   760→            ${this.analysisResult
   761→              ? html`
   762→                  <div class="section">
   763→                    <div class="section-title">Analysis Results</div>
   764→                    <div class="results-section">
   765→                      ${this.analysisResult.success
   766→                        ? html` <pre>${JSON.stringify(this.analysisResult.result, null, 2)}</pre> `
   767→                        : html`
   768→                            <p style="color: var(--color-negative)">
   769→                              Error: ${this.analysisResult.error?.message}
   770→                            </p>
   771→                          `}
   772→                    </div>
   773→                  </div>
   774→                `
   775→              : ''}
   776→          `
   777→        : ''}
   778→    `
   779→  }
   780→}
   781→
   782→declare global {
   783→  interface HTMLElementTagNameMap {
   784→    'ai-tools-panel': AIToolsPanel
