// AI Tools Panel - AI-powered analysis and editing suggestions
import { LitElement, html, css } from 'lit'
import { customElement, property, state } from 'lit/decorators.js'
import type { ClipSelection } from '@/types/plugin'
import type { AnalysisType, AIAnalysisResponse } from '@/types/api'
import { analyzeClip, isAIServiceConfigured } from '@/utils/api/ai-service'
import { canPerformOperation, incrementOperationsUsed } from '@/utils/api/plugin'
import { backendClient, type AudioJob, type User } from '@/utils/api/backend-client'
import { logger } from '@/utils/logger'

type UploadState = 'idle' | 'uploading' | 'processing' | 'complete' | 'error'
type CloudTool = 'transcription' | 'vocal_isolation'

@customElement('ai-tools-panel')
export class AIToolsPanel extends LitElement {
  static styles = css`
    :host {
      display: block;
    }

    .section {
      margin-bottom: var(--space-lg);
    }

    .section-title {
      font-size: var(--font-size-sm);
      font-weight: var(--font-weight-medium);
      color: var(--text-secondary);
      margin-bottom: var(--space-sm);
      text-transform: uppercase;
      letter-spacing: 0.5px;
    }

    .ai-tools-grid {
      display: grid;
      gap: var(--space-sm);
    }

    .ai-tool-card {
      background: var(--bg-layer-1);
      border-radius: var(--radius-md);
      padding: var(--space-md);
      cursor: pointer;
      transition: all var(--transition-fast);
      border: 1px solid transparent;
    }

    .ai-tool-card:hover {
      background: var(--bg-layer-2);
      border-color: var(--border-hover);
    }

    .ai-tool-card.disabled {
      opacity: 0.5;
      cursor: not-allowed;
    }

    .ai-tool-card.disabled:hover {
      background: var(--bg-layer-1);
      border-color: transparent;
    }

    .tool-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: var(--space-xs);
    }

    .tool-name {
      font-weight: var(--font-weight-medium);
    }

    .tool-badge {
      font-size: var(--font-size-xs);
      padding: 2px 6px;
      border-radius: var(--radius-sm);
      background: var(--color-accent);
      color: white;
    }

    .tool-badge.premium {
      background: linear-gradient(135deg, #ffd700, #ffaa00);
      color: #1a1a1a;
    }

    .tool-description {
      font-size: var(--font-size-sm);
      color: var(--text-secondary);
    }

    .warning-banner {
      background: var(--bg-layer-1);
      border-left: 3px solid var(--color-warning);
      padding: var(--space-md);
      border-radius: var(--radius-sm);
      margin-bottom: var(--space-md);
    }

    .warning-banner h4 {
      margin: 0 0 var(--space-xs) 0;
      color: var(--color-warning);
      font-size: var(--font-size-sm);
    }

    .warning-banner p {
      margin: 0;
      font-size: var(--font-size-sm);
      color: var(--text-secondary);
    }

    .results-section {
      background: var(--bg-layer-1);
      border-radius: var(--radius-md);
      padding: var(--space-md);
    }

    .result-item {
      padding: var(--space-sm) 0;
      border-bottom: 1px solid var(--border-default);
    }

    .result-item:last-child {
      border-bottom: none;
    }

    .loading-state {
      display: flex;
      flex-direction: column;
      align-items: center;
      padding: var(--space-xl);
      gap: var(--space-md);
    }

    /* File Upload Styles */
    .upload-zone {
      border: 2px dashed var(--border-default);
      border-radius: var(--radius-md);
      padding: var(--space-lg);
      text-align: center;
      cursor: pointer;
      transition: all var(--transition-fast);
      background: var(--bg-layer-1);
    }

    .upload-zone:hover {
      border-color: var(--color-accent);
      background: var(--bg-layer-2);
    }

    .upload-zone.disabled {
      opacity: 0.5;
      cursor: not-allowed;
    }

    .upload-zone.disabled:hover {
      border-color: var(--border-default);
      background: var(--bg-layer-1);
    }

    .upload-zone.dragover {
      border-color: var(--color-accent);
      background: var(--bg-layer-2);
    }

    .upload-icon {
      font-size: 2rem;
      margin-bottom: var(--space-sm);
    }

    .upload-text {
      color: var(--text-secondary);
      font-size: var(--font-size-sm);
    }

    .upload-text strong {
      color: var(--color-accent);
    }

    .file-input {
      display: none;
    }

    .selected-file {
      background: var(--bg-layer-1);
      border-radius: var(--radius-md);
      padding: var(--space-md);
      display: flex;
      align-items: center;
      gap: var(--space-md);
      margin-bottom: var(--space-md);
    }

    .file-info {
      flex: 1;
      min-width: 0;
    }

    .file-name {
      font-weight: var(--font-weight-medium);
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
    }

    .file-size {
      font-size: var(--font-size-sm);
      color: var(--text-secondary);
    }

    .remove-file {
      background: none;
      border: none;
      color: var(--text-secondary);
      cursor: pointer;
      padding: var(--space-xs);
      font-size: 1.2rem;
    }

    .remove-file:hover {
      color: var(--color-negative);
    }

    .cloud-tools-grid {
      display: grid;
      gap: var(--space-sm);
      margin-top: var(--space-md);
    }

    .cloud-tool-btn {
      display: flex;
      align-items: center;
      gap: var(--space-sm);
      padding: var(--space-md);
      background: var(--bg-layer-1);
      border: 1px solid var(--border-default);
      border-radius: var(--radius-md);
      cursor: pointer;
      transition: all var(--transition-fast);
      color: var(--text-primary);
