// Settings Panel - Plugin configuration and API setup
import { LitElement, html, css } from 'lit'
import { customElement, property, state } from 'lit/decorators.js'
import type { PricingPlan } from '@/types/plugin'
import type { AIProvider } from '@/types/api'
import {
  configureAIService,
  getAvailableProviders,
  isAIServiceConfigured,
} from '@/utils/api/ai-service'
import { isValidApiKey } from '@/utils/helpers/validators'
import { logger } from '@/utils/logger'

@customElement('settings-panel')
export class SettingsPanel extends LitElement {
  static styles = css`
    :host {
      display: block;
    }

    .section {
      margin-bottom: var(--space-lg);
    }

    .section-title {
      font-size: var(--font-size-sm);
      font-weight: var(--font-weight-medium);
      color: var(--text-secondary);
      margin-bottom: var(--space-sm);
      text-transform: uppercase;
      letter-spacing: 0.5px;
    }

    .setting-card {
      background: var(--bg-layer-1);
      border-radius: var(--radius-md);
      padding: var(--space-md);
      margin-bottom: var(--space-sm);
    }

    .setting-row {
      display: flex;
      flex-direction: column;
      gap: var(--space-sm);
      margin-bottom: var(--space-md);
    }

    .setting-row:last-child {
      margin-bottom: 0;
    }

    .setting-label {
      font-size: var(--font-size-sm);
      font-weight: var(--font-weight-medium);
    }

    .setting-description {
      font-size: var(--font-size-xs);
      color: var(--text-secondary);
    }

    .status-indicator {
      display: inline-flex;
      align-items: center;
      gap: var(--space-xs);
      font-size: var(--font-size-sm);
      padding: var(--space-xs) var(--space-sm);
      border-radius: var(--radius-sm);
    }

    .status-indicator.connected {
      background: rgba(15, 191, 132, 0.1);
      color: var(--color-accent);
    }

    .status-indicator.disconnected {
      background: rgba(215, 55, 63, 0.1);
      color: var(--color-negative);
    }

    .status-dot {
      width: 8px;
      height: 8px;
      border-radius: 50%;
    }

    .status-indicator.connected .status-dot {
      background: var(--color-accent);
    }

    .status-indicator.disconnected .status-dot {
      background: var(--color-negative);
    }

    .form-row {
      display: flex;
      gap: var(--space-sm);
    }

    .form-row sp-picker {
      width: 140px;
    }

    .form-row sp-textfield {
      flex: 1;
    }

    .about-section {
      text-align: center;
      padding: var(--space-md);
      color: var(--text-secondary);
      font-size: var(--font-size-sm);
    }

    .about-section a {
      color: var(--color-primary);
      text-decoration: none;
    }

    .about-section a:hover {
      text-decoration: underline;
    }

    .plan-info {
      display: flex;
      align-items: center;
      justify-content: space-between;
    }

    .plan-name {
      font-weight: var(--font-weight-bold);
      text-transform: capitalize;
    }
  `

  @property({ type: String }) currentPlan: PricingPlan = 'free'

  @state() private selectedProvider: AIProvider = 'anthropic'
  @state() private apiKey = ''
  @state() private isSaving = false
  @state() private saveMessage = ''

  private providers = getAvailableProviders()

  private async handleSaveApiKey(): Promise<void> {
    if (!this.apiKey.trim()) {
      this.saveMessage = 'Please enter an API key'
      return
    }

    if (!isValidApiKey(this.apiKey, this.selectedProvider)) {
      this.saveMessage = 'Invalid API key format'
      return
    }

    this.isSaving = true
    this.saveMessage = ''

    try {
      configureAIService({
        provider: this.selectedProvider,
        apiKey: this.apiKey.trim(),
      })

      this.saveMessage = 'API key saved successfully!'
      logger.info('AI service configured', { provider: this.selectedProvider })

      // Clear the input for security
      this.apiKey = ''
    } catch (error) {
      this.saveMessage = 'Failed to save API key'
      logger.error('Failed to configure AI service', error)
    } finally {
      this.isSaving = false
    }
  }

  private handleProviderChange(e: Event): void {
    const picker = e.target as HTMLSelectElement
    this.selectedProvider = picker.value as AIProvider
  }

  render() {
    const isConfigured = isAIServiceConfigured()

    return html`
      <div class="section">
        <div class="section-title">AI Service Configuration</div>
        <div class="setting-card">
          <div class="setting-row">
            <div>
              <span class="setting-label">Connection Status</span>
            </div>
            <div class="status-indicator ${isConfigured ? 'connected' : 'disconnected'}">
              <span class="status-dot"></span>
              ${isConfigured ? 'Connected' : 'Not Configured'}
            </div>
          </div>

          <div class="setting-row">
            <span class="setting-label">AI Provider & API Key</span>
            <span class="setting-description">
              Select your AI provider and enter your API key. Keys are stored locally.
            </span>
            <div class="form-row">
              <sp-picker
                label="Provider"
                .value=${this.selectedProvider}
                @change=${this.handleProviderChange}
              >
                ${this.providers.map(
                  (p) => html`
                    <sp-menu-item value=${p.id} ?selected=${this.selectedProvider === p.id}>
                      ${p.name}
                    </sp-menu-item>
                  `
                )}
              </sp-picker>
              <sp-textfield
                type="password"
                placeholder="Enter API key"
                .value=${this.apiKey}
                @input=${(e: Event) => (this.apiKey = (e.target as HTMLInputElement).value)}
              ></sp-textfield>
            </div>
            <sp-button variant="primary" ?disabled=${this.isSaving} @click=${this.handleSaveApiKey}>
              ${this.isSaving ? 'Saving...' : 'Save API Key'}
            </sp-button>
            ${this.saveMessage
              ? html`<span class="setting-description">${this.saveMessage}</span>`
              : ''}
          </div>
        </div>
      </div>

      <div class="section">
        <div class="section-title">Subscription</div>
        <div class="setting-card">
          <div class="plan-info">
            <div>
              <span class="setting-label">Current Plan</span>
              <div class="plan-name">${this.currentPlan}</div>
            </div>
            ${this.currentPlan === 'free'
              ? html`<sp-button variant="accent" size="s">Upgrade</sp-button>`
              : html`<sp-button variant="secondary" size="s">Manage</sp-button>`}
          </div>
        </div>
      </div>

      <div class="section">
        <div class="section-title">About</div>
        <div class="setting-card about-section">
          <p><strong>Splice v3</strong></p>
          <p>Version 1.0.0</p>
          <p>
            <a href="https://splice.com/help" target="_blank">Help & Support</a> |
            <a href="https://splice.com/privacy" target="_blank">Privacy Policy</a>
          </p>
        </div>
      </div>
    `
  }
}

declare global {
  interface HTMLElementTagNameMap {
    'settings-panel': SettingsPanel
  }
}

// Plugin-specific types

export interface PluginState {
  isInitialized: boolean
  isPremium: boolean
  currentPlan: PricingPlan
  selectedClips: ClipSelection[]
  aiServiceConnected: boolean
}

export type PricingPlan = 'free' | 'pro' | 'enterprise'

export interface ClipSelection {
  id: string
  name: string
  startTime: number
  endTime: number
  trackIndex: number
  type: 'video' | 'audio'
}

export interface PluginConfig {
  apiEndpoint: string
  maxFreeOperations: number
  features: FeatureFlags
}

export interface FeatureFlags {
  aiAnalysis: boolean
  batchProcessing: boolean
  advancedSelection: boolean
  cloudSync: boolean
}

export interface UserLicense {
  userId: string
  plan: PricingPlan
  expiresAt: Date | null
  operationsUsed: number
  operationsLimit: number
}

// API types for AI services integration

export interface AIServiceConfig {
  provider: AIProvider
  apiKey: string
  model?: string
  maxTokens?: number
}

export type AIProvider = 'openai' | 'anthropic' | 'google'

export interface AIAnalysisRequest {
  clipData: ClipAnalysisData
  analysisType: AnalysisType
  options?: AIAnalysisOptions
}

export interface ClipAnalysisData {
  duration: number
  frameRate: number
  audioChannels: number
  metadata: Record<string, unknown>
}

export type AnalysisType = 'content' | 'scene-detection' | 'audio-analysis' | 'smart-cut'

export interface AIAnalysisOptions {
  detailed?: boolean
  language?: string
  customPrompt?: string
}

export interface AIAnalysisResponse {
  success: boolean
  result?: AnalysisResult
  error?: APIError
  usage?: APIUsage
}

export interface AnalysisResult {
  scenes?: SceneMarker[]
  suggestions?: EditSuggestion[]
  transcription?: string
  metadata?: Record<string, unknown>
}

export interface SceneMarker {
  startTime: number
  endTime: number
  confidence: number
  label?: string
}

export interface EditSuggestion {
  type: 'cut' | 'trim' | 'transition' | 'effect'
  startTime: number
  endTime: number
  description: string
  priority: 'low' | 'medium' | 'high'
}

export interface APIError {
  code: string
  message: string
  details?: Record<string, unknown>
}

export interface APIUsage {
  tokensUsed: number
  requestsRemaining: number
  costEstimate?: number
}

