     1→// Plugin initialization and core API
     2→
     3→import type { PluginState, PluginConfig, UserLicense } from '@/types/plugin'
     4→import { logger } from '../logger'
     5→
     6→const DEFAULT_CONFIG: PluginConfig = {
     7→  apiEndpoint: 'https://api.splice.com/v3',
     8→  maxFreeOperations: 10,
     9→  features: {
    10→    aiAnalysis: true,
    11→    batchProcessing: false,
    12→    advancedSelection: false,
    13→    cloudSync: false,
    14→  },
    15→}
    16→
    17→let pluginState: PluginState = {
    18→  isInitialized: false,
    19→  isPremium: false,
    20→  currentPlan: 'free',
    21→  selectedClips: [],
    22→  aiServiceConnected: false,
    23→}
    24→
    25→let userLicense: UserLicense | null = null
    26→
    27→export async function initializePlugin(): Promise<void> {
    28→  try {
    29→    logger.info('Initializing plugin...')
    30→
    31→    // Load saved state from local storage
    32→    await loadPersistedState()
    33→
    34→    // Check license status
    35→    await checkLicenseStatus()
    36→
    37→    pluginState.isInitialized = true
    38→    logger.info('Plugin initialized successfully', { plan: pluginState.currentPlan })
    39→  } catch (error) {
    40→    logger.error('Failed to initialize plugin', error)
    41→    throw error
    42→  }
    43→}
    44→
    45→async function loadPersistedState(): Promise<void> {
    46→  try {
    47→    // UXP local storage access
    48→    const storage = await getLocalStorage()
    49→    const savedState = storage.getItem('splice-v3-state')
    50→    if (savedState) {
    51→      const parsed = JSON.parse(savedState)
    52→      pluginState = { ...pluginState, ...parsed }
    53→    }
    54→  } catch (error) {
    55→    logger.warn('Could not load persisted state', error)
    56→  }
    57→}
    58→
    59→async function checkLicenseStatus(): Promise<void> {
    60→  try {
    61→    // In a real implementation, this would call your licensing API
    62→    // For now, we'll use the freemium free tier
    63→    userLicense = {
    64→      userId: 'local-user',
    65→      plan: 'free',
    66→      expiresAt: null,
    67→      operationsUsed: 0,
    68→      operationsLimit: DEFAULT_CONFIG.maxFreeOperations,
    69→    }
    70→
    71→    pluginState.currentPlan = userLicense.plan
    72→    pluginState.isPremium = userLicense.plan !== 'free'
    73→
    74→    // Update features based on plan
    75→    updateFeaturesForPlan(userLicense.plan)
    76→  } catch (error) {
    77→    logger.warn('Could not check license status, defaulting to free tier', error)
    78→  }
    79→}
    80→
    81→function updateFeaturesForPlan(plan: UserLicense['plan']): void {
    82→  const config = getConfig()
    83→
    84→  switch (plan) {
    85→    case 'pro':
    86→      config.features.batchProcessing = true
    87→      config.features.advancedSelection = true
    88→      break
    89→    case 'enterprise':
    90→      config.features.batchProcessing = true
    91→      config.features.advancedSelection = true
    92→      config.features.cloudSync = true
    93→      break
    94→    default:
    95→      // Free tier - defaults are already set
    96→      break
    97→  }
    98→}
    99→
   100→export function getPluginState(): PluginState {
   101→  return { ...pluginState }
   102→}
   103→
   104→export function getConfig(): PluginConfig {
   105→  return { ...DEFAULT_CONFIG }
   106→}
   107→
   108→export function getUserLicense(): UserLicense | null {
   109→  return userLicense ? { ...userLicense } : null
   110→}
   111→
   112→export async function saveState(): Promise<void> {
   113→  try {
   114→    const storage = await getLocalStorage()
   115→    storage.setItem('splice-v3-state', JSON.stringify(pluginState))
   116→    logger.debug('State saved')
   117→  } catch (error) {
   118→    logger.error('Failed to save state', error)
   119→  }
   120→}
   121→
   122→export function incrementOperationsUsed(): boolean {
   123→  if (!userLicense) return false
   124→
   125→  if (userLicense.plan === 'free' && userLicense.operationsUsed >= userLicense.operationsLimit) {
   126→    logger.warn('Free tier operation limit reached')
   127→    return false
   128→  }
   129→
   130→  userLicense.operationsUsed++
   131→  saveState()
   132→  return true
   133→}
   134→
   135→export function canPerformOperation(): boolean {
   136→  if (!userLicense) return false
   137→  if (userLicense.plan !== 'free') return true
   138→  return userLicense.operationsUsed < userLicense.operationsLimit
   139→}
   140→
   141→export function getRemainingOperations(): number {
   142→  if (!userLicense) return 0
   143→  if (userLicense.plan !== 'free') return Infinity
   144→  return Math.max(0, userLicense.operationsLimit - userLicense.operationsUsed)
   145→}
   146→
   147→// UXP storage helper
   148→async function getLocalStorage(): Promise<Storage> {
   149→  // In UXP environment, use the localStorage API
   150→  // This is a simplified version - real UXP plugins use the storage module
   151→  return window.localStorage
   152→}
