// Logging utility for Splice v3

type LogLevel = 'debug' | 'info' | 'warn' | 'error'

interface LogEntry {
  level: LogLevel
  message: string
  timestamp: string
  data?: unknown
}

const LOG_PREFIX = '[Splice v3]'

class Logger {
  private isDevelopment: boolean
  private logHistory: LogEntry[] = []
  private maxHistorySize = 100

  constructor() {
    this.isDevelopment = import.meta.env?.DEV ?? true
  }

  private formatMessage(level: LogLevel, message: string): string {
    const timestamp = new Date().toISOString()
    return `${LOG_PREFIX} [${level.toUpperCase()}] ${timestamp}: ${message}`
  }

  private addToHistory(entry: LogEntry): void {
    this.logHistory.push(entry)
    if (this.logHistory.length > this.maxHistorySize) {
      this.logHistory.shift()
    }
  }

  debug(message: string, data?: unknown): void {
    if (this.isDevelopment) {
      console.debug(this.formatMessage('debug', message), data ?? '')
      this.addToHistory({ level: 'debug', message, timestamp: new Date().toISOString(), data })
    }
  }

  info(message: string, data?: unknown): void {
    console.info(this.formatMessage('info', message), data ?? '')
    this.addToHistory({ level: 'info', message, timestamp: new Date().toISOString(), data })
  }

  warn(message: string, data?: unknown): void {
    console.warn(this.formatMessage('warn', message), data ?? '')
    this.addToHistory({ level: 'warn', message, timestamp: new Date().toISOString(), data })
  }

  error(message: string, error?: unknown): void {
    console.error(this.formatMessage('error', message), error ?? '')
    this.addToHistory({ level: 'error', message, timestamp: new Date().toISOString(), data: error })
  }

  getHistory(): LogEntry[] {
    return [...this.logHistory]
  }

  clearHistory(): void {
    this.logHistory = []
  }
}

export const logger = new Logger()

// UI component prop types

export interface PanelProps {
  isLoading?: boolean
  error?: string | null
}

export interface ButtonProps {
  variant?: 'primary' | 'secondary' | 'negative' | 'accent'
  size?: 'small' | 'medium' | 'large'
  disabled?: boolean
  loading?: boolean
  onClick?: () => void
}

export interface DialogProps {
  open: boolean
  title: string
  onClose: () => void
  onConfirm?: () => void
  confirmLabel?: string
  cancelLabel?: string
  variant?: 'confirmation' | 'information' | 'destructive'
}

export interface SelectionListProps {
  items: SelectionItem[]
  selectedIds: string[]
  onSelectionChange: (ids: string[]) => void
  multiSelect?: boolean
}

export interface SelectionItem {
  id: string
  label: string
  description?: string
  icon?: string
  disabled?: boolean
}

export interface ToastNotification {
  id: string
  message: string
  variant: 'info' | 'positive' | 'negative' | 'warning'
  duration?: number
}

export interface ProgressIndicatorProps {
  value?: number
  indeterminate?: boolean
  label?: string
  size?: 'small' | 'medium' | 'large'
}

