{
  "id": "com.splice.v3",
  "name": "Splice v3",
  "version": "1.0.0",
  "description": "AI-powered video editing plugin for Adobe Premiere Pro with take detection, voice isolation, and dead space removal",
  "manifestVersion": 6,
  "main": "index.html",
  "host": {
    "app": "PremierePro",
    "minVersion": "25.6.0"
  },
  "entrypoints": [
    {
      "type": "panel",
      "id": "mainPanel",
      "label": "Splice v3",
      "minimumSize": { "width": 280, "height": 400 },
      "maximumSize": { "width": 600, "height": 1200 },
      "preferredDockedSize": { "width": 320, "height": 500 },
      "preferredFloatingSize": { "width": 400, "height": 600 }
    }
  ],
  "requiredPermissions": {
    "network": {
      "domains": [
        "https://api.anthropic.com",
        "https://api.openai.com",
        "https://generativelanguage.googleapis.com",
        "https://api.splice.com",
        "https://splice.com"
      ]
    },
    "clipboard": "readAndWrite",
    "localFileSystem": "plugin",
    "webview": {
      "allow": "yes",
      "domains": ["https://*.splice.com"]
    }
  },
  "icons": [
    {
      "width": 24,
      "height": 24,
      "path": "icons/icon-24.png",
      "scale": [1, 2],
      "theme": ["all"]
    },
    {
      "width": 48,
      "height": 48,
      "path": "icons/icon-48.png",
      "scale": [1, 2],
      "theme": ["all"]
    }
  ]
}

import { defineConfig } from 'vite'
import { resolve } from 'path'

export default defineConfig({
  root: 'public',
  base: './',
  build: {
    outDir: '../dist',
    emptyOutDir: true,
    rollupOptions: {
      input: {
        main: resolve(__dirname, 'public/index.html'),
      },
      output: {
        entryFileNames: 'index.js',
        chunkFileNames: '[name].js',
        assetFileNames: '[name].[ext]',
      },
    },
    target: 'es2020',
    minify: 'esbuild',
    sourcemap: true,
  },
  resolve: {
    alias: {
      '@': resolve(__dirname, 'src'),
      '@components': resolve(__dirname, 'src/components'),
      '@hooks': resolve(__dirname, 'src/hooks'),
      '@utils': resolve(__dirname, 'src/utils'),
      '@types': resolve(__dirname, 'src/types'),
      '@context': resolve(__dirname, 'src/context'),
      '@config': resolve(__dirname, 'src/config'),
    },
  },
  server: {
    port: 3000,
    open: false,
  },
})

