/* Splice v3 - Global Styles & Design Tokens */

:root {
  /* Color Tokens */
  --color-primary: #0d66d0;
  --color-primary-hover: #1473e6;
  --color-secondary: #6e6e6e;
  --color-accent: #0fbf84;
  --color-negative: #d7373f;
  --color-warning: #e68619;

  /* Background Colors */
  --bg-base: #1e1e1e;
  --bg-layer-1: #252525;
  --bg-layer-2: #2c2c2c;
  --bg-elevated: #323232;

  /* Text Colors */
  --text-primary: #ffffff;
  --text-secondary: #b3b3b3;
  --text-disabled: #6e6e6e;

  /* Border Colors */
  --border-default: #3e3e3e;
  --border-hover: #5c5c5c;
  --border-focus: var(--color-primary);

  /* Spacing */
  --space-xs: 4px;
  --space-sm: 8px;
  --space-md: 16px;
  --space-lg: 24px;
  --space-xl: 32px;

  /* Border Radius */
  --radius-sm: 4px;
  --radius-md: 8px;
  --radius-lg: 12px;

  /* Shadows */
  --shadow-sm: 0 1px 2px rgba(0, 0, 0, 0.3);
  --shadow-md: 0 4px 8px rgba(0, 0, 0, 0.4);
  --shadow-lg: 0 8px 16px rgba(0, 0, 0, 0.5);

  /* Typography */
  --font-family: 'Adobe Clean', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  --font-size-xs: 11px;
  --font-size-sm: 12px;
  --font-size-md: 14px;
  --font-size-lg: 16px;
  --font-size-xl: 20px;
  --font-weight-normal: 400;
  --font-weight-medium: 500;
  --font-weight-bold: 700;

  /* Transitions */
  --transition-fast: 100ms ease;
  --transition-normal: 200ms ease;
  --transition-slow: 300ms ease;
}

/* Reset & Base Styles */
*,
*::before,
*::after {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

html,
body {
  height: 100%;
  font-family: var(--font-family);
  font-size: var(--font-size-md);
  color: var(--text-primary);
  background-color: var(--bg-base);
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

#app {
  height: 100%;
  display: flex;
  flex-direction: column;
}

/* Spectrum Theme Overrides */
sp-theme {
  --spectrum-global-dimension-size-100: 8px;
  --spectrum-global-dimension-size-200: 16px;
  background-color: var(--bg-base);
}

/* Scrollbar Styles */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: var(--bg-layer-1);
}

::-webkit-scrollbar-thumb {
  background: var(--border-default);
  border-radius: var(--radius-sm);
}

::-webkit-scrollbar-thumb:hover {
  background: var(--border-hover);
}

/* Utility Classes */
.hidden {
  display: none !important;
}

.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

/* Freemium Badge Styles */
.premium-badge {
  background: linear-gradient(135deg, #ffd700, #ffaa00);
  color: #1a1a1a;
  padding: 2px 8px;
  border-radius: var(--radius-sm);
  font-size: var(--font-size-xs);
  font-weight: var(--font-weight-bold);
  text-transform: uppercase;
}

.free-badge {
  background: var(--bg-layer-2);
  color: var(--text-secondary);
  padding: 2px 8px;
  border-radius: var(--radius-sm);
  font-size: var(--font-size-xs);
  font-weight: var(--font-weight-medium);
}

// Splice v3 - Main Entry Point
import '@spectrum-web-components/theme/sp-theme.js'
import '@spectrum-web-components/theme/theme-dark.js'
import '@spectrum-web-components/theme/scale-medium.js'
import '@spectrum-web-components/button/sp-button.js'
import '@spectrum-web-components/action-button/sp-action-button.js'
import '@spectrum-web-components/menu/sp-menu.js'
import '@spectrum-web-components/menu/sp-menu-item.js'
import '@spectrum-web-components/picker/sp-picker.js'
import '@spectrum-web-components/textfield/sp-textfield.js'
import '@spectrum-web-components/checkbox/sp-checkbox.js'
import '@spectrum-web-components/progress-circle/sp-progress-circle.js'
import '@spectrum-web-components/toast/sp-toast.js'

import { PluginApp } from './components/panels/PluginApp'
import { initializePlugin } from './utils/api/plugin'
import { logger } from './utils/logger'

// Initialize plugin when DOM is ready
document.addEventListener('DOMContentLoaded', async () => {
  try {
    logger.info('Splice v3 initializing...')

    // Initialize plugin core
    await initializePlugin()

    // Mount the application
    const appContainer = document.getElementById('app')
    if (appContainer) {
      const app = new PluginApp()
      appContainer.appendChild(app)
      logger.info('Splice v3 mounted successfully')
    } else {
      throw new Error('App container not found')
    }
  } catch (error) {
    logger.error('Failed to initialize Splice v3:', error)
  }
})

// Plugin-specific types

export interface PluginState {
  isInitialized: boolean
  isPremium: boolean
  currentPlan: PricingPlan
  selectedClips: ClipSelection[]
  aiServiceConnected: boolean
}

export type PricingPlan = 'free' | 'pro' | 'enterprise'

export interface ClipSelection {
  id: string
  name: string
  startTime: number
  endTime: number
  trackIndex: number
  type: 'video' | 'audio'
}

export interface PluginConfig {
  apiEndpoint: string
  maxFreeOperations: number
  features: FeatureFlags
}

export interface FeatureFlags {
  aiAnalysis: boolean
  batchProcessing: boolean
  advancedSelection: boolean
  cloudSync: boolean
}

export interface UserLicense {
  userId: string
  plan: PricingPlan
  expiresAt: Date | null
  operationsUsed: number
  operationsLimit: number
}

// UI component prop types

export interface PanelProps {
  isLoading?: boolean
  error?: string | null
}

export interface ButtonProps {
  variant?: 'primary' | 'secondary' | 'negative' | 'accent'
  size?: 'small' | 'medium' | 'large'
  disabled?: boolean
  loading?: boolean
  onClick?: () => void
}

export interface DialogProps {
  open: boolean
  title: string
  onClose: () => void
  onConfirm?: () => void
  confirmLabel?: string
  cancelLabel?: string
  variant?: 'confirmation' | 'information' | 'destructive'
}

export interface SelectionListProps {
  items: SelectionItem[]
  selectedIds: string[]
  onSelectionChange: (ids: string[]) => void
  multiSelect?: boolean
}

export interface SelectionItem {
  id: string
  label: string
  description?: string
  icon?: string
  disabled?: boolean
}

export interface ToastNotification {
  id: string
  message: string
  variant: 'info' | 'positive' | 'negative' | 'warning'
  duration?: number
}

export interface ProgressIndicatorProps {
  value?: number
  indeterminate?: boolean
  label?: string
  size?: 'small' | 'medium' | 'large'
}

import { defineConfig } from 'vite'
import { resolve } from 'path'

export default defineConfig({
  root: 'public',
  base: './',
  build: {
    outDir: '../dist',
    emptyOutDir: true,
    rollupOptions: {
      input: {
        main: resolve(__dirname, 'public/index.html'),
      },
      output: {
        entryFileNames: 'index.js',
        chunkFileNames: '[name].js',
        assetFileNames: '[name].[ext]',
      },
    },
    target: 'es2020',
    minify: 'esbuild',
    sourcemap: true,
  },
  resolve: {
    alias: {
      '@': resolve(__dirname, 'src'),
      '@components': resolve(__dirname, 'src/components'),
      '@hooks': resolve(__dirname, 'src/hooks'),
      '@utils': resolve(__dirname, 'src/utils'),
      '@types': resolve(__dirname, 'src/types'),
      '@context': resolve(__dirname, 'src/context'),
      '@config': resolve(__dirname, 'src/config'),
    },
  },
  server: {
    port: 3000,
    open: false,
  },
})
