// AI Services API Integration

import type {
  AIServiceConfig,
  AIProvider,
  AIAnalysisRequest,
  AIAnalysisResponse,
  AnalysisResult,
} from '@/types/api'
import { logger } from '../logger'

let aiConfig: AIServiceConfig | null = null

export function configureAIService(config: AIServiceConfig): void {
  aiConfig = { ...config }
  logger.info('AI service configured', { provider: config.provider })
}

export function isAIServiceConfigured(): boolean {
  return aiConfig !== null && Boolean(aiConfig.apiKey)
}

export async function analyzeClip(request: AIAnalysisRequest): Promise<AIAnalysisResponse> {
  if (!aiConfig) {
    return {
      success: false,
      error: {
        code: 'NOT_CONFIGURED',
        message: 'AI service is not configured. Please add your API key in settings.',
      },
    }
  }

  try {
    logger.info('Starting clip analysis', { type: request.analysisType })

    const result = await callAIProvider(aiConfig, request)

    logger.info('Clip analysis completed', { success: true })
    return {
      success: true,
      result,
      usage: {
        tokensUsed: 0, // Would be populated from actual API response
        requestsRemaining: 100,
      },
    }
  } catch (error) {
    logger.error('AI analysis failed', error)
    return {
      success: false,
      error: {
        code: 'ANALYSIS_FAILED',
        message: error instanceof Error ? error.message : 'Unknown error occurred',
      },
    }
  }
}

async function callAIProvider(
  config: AIServiceConfig,
  request: AIAnalysisRequest
): Promise<AnalysisResult> {
  const endpoint = getProviderEndpoint(config.provider)
  const headers = getProviderHeaders(config)
  const body = buildRequestBody(config, request)

  const response = await fetch(endpoint, {
    method: 'POST',
    headers,
    body: JSON.stringify(body),
  })

  if (!response.ok) {
    const errorData = await response.json().catch(() => ({}))
    throw new Error(errorData.message || `API request failed: ${response.status}`)
  }

  const data = await response.json()
  return parseProviderResponse(config.provider, data, request.analysisType)
}

function getProviderEndpoint(provider: AIProvider): string {
  switch (provider) {
    case 'anthropic':
      return 'https://api.anthropic.com/v1/messages'
    case 'openai':
      return 'https://api.openai.com/v1/chat/completions'
    case 'google':
      return 'https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent'
    default:
      throw new Error(`Unsupported AI provider: ${provider}`)
  }
}

function getProviderHeaders(config: AIServiceConfig): HeadersInit {
  const headers: HeadersInit = {
    'Content-Type': 'application/json',
  }

  switch (config.provider) {
    case 'anthropic':
      headers['x-api-key'] = config.apiKey
      headers['anthropic-version'] = '2024-01-01'
      break
    case 'openai':
      headers['Authorization'] = `Bearer ${config.apiKey}`
      break
    case 'google':
      headers['x-goog-api-key'] = config.apiKey
      break
  }

  return headers
}

function buildRequestBody(
  config: AIServiceConfig,
  request: AIAnalysisRequest
): Record<string, unknown> {
  const prompt = buildAnalysisPrompt(request)

  switch (config.provider) {
    case 'anthropic':
      return {
        model: config.model || 'claude-3-5-sonnet-20241022',
        max_tokens: config.maxTokens || 1024,
        messages: [{ role: 'user', content: prompt }],
      }
    case 'openai':
      return {
        model: config.model || 'gpt-4o',
        max_tokens: config.maxTokens || 1024,
        messages: [{ role: 'user', content: prompt }],
      }
    case 'google':
      return {
        contents: [{ parts: [{ text: prompt }] }],
        generationConfig: {
          maxOutputTokens: config.maxTokens || 1024,
        },
      }
    default:
      throw new Error(`Unsupported provider: ${config.provider}`)
  }
}

function buildAnalysisPrompt(request: AIAnalysisRequest): string {
  const { clipData, analysisType, options } = request

  if (options?.customPrompt) {
    return options.customPrompt
  }

  const baseContext = `Analyzing video clip with duration: ${clipData.duration}s, frame rate: ${clipData.frameRate}fps, audio channels: ${clipData.audioChannels}.`

  switch (analysisType) {
    case 'scene-detection':
      return `${baseContext}\n\nAnalyze this clip and identify distinct scenes. For each scene, provide start time, end time, and a brief description. Return as JSON array.`
    case 'audio-analysis':
      return `${baseContext}\n\nAnalyze the audio characteristics of this clip. Identify key moments, volume changes, and suggest edit points based on audio. Return as JSON.`
    case 'smart-cut':
      return `${baseContext}\n\nSuggest optimal cut points for this clip based on pacing and content flow. Provide timestamps and reasoning. Return as JSON.`
    case 'content':
    default:
      return `${baseContext}\n\nProvide a detailed analysis of this clip's content, themes, and suggested improvements. Return as JSON.`
  }
}

function parseProviderResponse(
  provider: AIProvider,
  response: Record<string, unknown>,
  analysisType: string
): AnalysisResult {
  let content: string

  switch (provider) {
    case 'anthropic':
      content = (response.content as Array<{ text: string }>)?.[0]?.text || ''
      break
    case 'openai':
      content =
        (response.choices as Array<{ message: { content: string } }>)?.[0]?.message?.content || ''
      break
    case 'google':
      content =
        (response.candidates as Array<{ content: { parts: Array<{ text: string }> } }>)?.[0]
          ?.content?.parts?.[0]?.text || ''
      break
    default:
      content = ''
  }

  try {
    // Try to parse JSON from the response
    const jsonMatch = content.match(/\{[\s\S]*\}|\[[\s\S]*\]/)
    if (jsonMatch) {
      return JSON.parse(jsonMatch[0])
    }
  } catch {
    logger.warn('Could not parse AI response as JSON')
  }

  // Return as metadata if not parseable
  return {
    metadata: {
      rawResponse: content,
      analysisType,
    },
  }
}

export function getAvailableProviders(): { id: AIProvider; name: string }[] {
  return [
    { id: 'anthropic', name: 'Anthropic Claude' },
    { id: 'openai', name: 'OpenAI GPT' },
    { id: 'google', name: 'Google Gemini' },
  ]
}

