// Upgrade Prompt - Freemium conversion modal
import { LitElement, html, css } from 'lit'
import { customElement, property } from 'lit/decorators.js'
import type { PricingPlan } from '@/types/plugin'

@customElement('upgrade-prompt')
export class UpgradePrompt extends LitElement {
  static styles = css`
    :host {
      position: fixed;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      display: flex;
      align-items: center;
      justify-content: center;
      background: rgba(0, 0, 0, 0.7);
      z-index: 1000;
    }

    .modal {
      background: var(--bg-layer-1);
      border-radius: var(--radius-lg);
      padding: var(--space-lg);
      max-width: 400px;
      width: 90%;
      box-shadow: var(--shadow-lg);
    }

    .modal-header {
      text-align: center;
      margin-bottom: var(--space-lg);
    }

    .modal-title {
      font-size: var(--font-size-xl);
      font-weight: var(--font-weight-bold);
      margin-bottom: var(--space-xs);
    }

    .modal-subtitle {
      color: var(--text-secondary);
      font-size: var(--font-size-sm);
    }

    .plans {
      display: flex;
      flex-direction: column;
      gap: var(--space-md);
      margin-bottom: var(--space-lg);
    }

    .plan-card {
      background: var(--bg-layer-2);
      border: 2px solid var(--border-default);
      border-radius: var(--radius-md);
      padding: var(--space-md);
      cursor: pointer;
      transition: all var(--transition-fast);
    }

    .plan-card:hover {
      border-color: var(--color-primary);
    }

    .plan-card.recommended {
      border-color: var(--color-accent);
      background: rgba(15, 191, 132, 0.05);
    }

    .plan-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: var(--space-sm);
    }

    .plan-name {
      font-weight: var(--font-weight-bold);
    }

    .plan-badge {
      font-size: var(--font-size-xs);
      padding: 2px 8px;
      border-radius: var(--radius-sm);
      background: var(--color-accent);
      color: white;
    }

    .plan-price {
      font-size: var(--font-size-lg);
      font-weight: var(--font-weight-bold);
      margin-bottom: var(--space-sm);
    }

    .plan-price span {
      font-size: var(--font-size-sm);
      font-weight: var(--font-weight-normal);
      color: var(--text-secondary);
    }

    .plan-features {
      list-style: none;
      padding: 0;
      margin: 0;
    }

    .plan-features li {
      font-size: var(--font-size-sm);
      color: var(--text-secondary);
      padding: var(--space-xs) 0;
      display: flex;
      align-items: center;
      gap: var(--space-xs);
    }

    .plan-features li::before {
      content: '✓';
      color: var(--color-accent);
    }

    .modal-actions {
      display: flex;
      gap: var(--space-sm);
    }

    .modal-actions sp-button {
      flex: 1;
    }

    .close-button {
      position: absolute;
      top: var(--space-sm);
      right: var(--space-sm);
      background: none;
      border: none;
      color: var(--text-secondary);
      cursor: pointer;
      font-size: 20px;
      padding: var(--space-xs);
    }

    .close-button:hover {
      color: var(--text-primary);
    }
  `

  @property({ type: String }) currentPlan: PricingPlan = 'free'

  private plans = [
    {
      id: 'pro' as PricingPlan,
      name: 'Pro',
      price: 9.99,
      period: 'month',
      recommended: true,
      features: [
        'Unlimited AI operations',
        'Batch processing',
        'Advanced selection tools',
        'Priority support',
      ],
    },
    {
      id: 'enterprise' as PricingPlan,
      name: 'Enterprise',
      price: 29.99,
      period: 'month',
      recommended: false,
      features: [
        'Everything in Pro',
        'Cloud sync & backup',
        'Team collaboration',
        'Custom AI models',
        'Dedicated support',
      ],
    },
  ]

  private handleClose(): void {
    this.dispatchEvent(new CustomEvent('close', { bubbles: true, composed: true }))
  }

  private handleUpgrade(plan: PricingPlan): void {
    // In a real implementation, this would redirect to a payment page
    // or open Adobe Exchange purchase flow
    window.open(`https://splice.com/upgrade?plan=${plan}`, '_blank')
  }

  render() {
    return html`
      <div class="modal">
        <button class="close-button" @click=${this.handleClose}>×</button>

        <div class="modal-header">
          <div class="modal-title">Upgrade to Pro</div>
          <div class="modal-subtitle">Unlock the full power of Splice v3</div>
        </div>

        <div class="plans">
          ${this.plans.map(
            (plan) => html`
              <div
                class="plan-card ${plan.recommended ? 'recommended' : ''}"
                @click=${() => this.handleUpgrade(plan.id)}
              >
                <div class="plan-header">
                  <span class="plan-name">${plan.name}</span>
                  ${plan.recommended ? html`<span class="plan-badge">Most Popular</span>` : ''}
                </div>
                <div class="plan-price">$${plan.price} <span>/ ${plan.period}</span></div>
                <ul class="plan-features">
                  ${plan.features.map((feature) => html`<li>${feature}</li>`)}
                </ul>
              </div>
            `
          )}
        </div>

        <div class="modal-actions">
          <sp-button variant="secondary" @click=${this.handleClose}> Maybe Later </sp-button>
          <sp-button variant="accent" @click=${() => this.handleUpgrade('pro')}>
            Get Pro
          </sp-button>
        </div>
      </div>
    `
  }
}

declare global {
  interface HTMLElementTagNameMap {
    'upgrade-prompt': UpgradePrompt
  }
}

// Validation utilities

import type { AIProvider } from '@/types/api'

/**
 * Validate time range
 */
export function isValidTimeRange(startTime: number, endTime: number): boolean {
  return startTime >= 0 && endTime > startTime
}

/**
 * Validate API key format based on provider
 */
export function isValidApiKey(apiKey: string, provider: AIProvider): boolean {
  if (!apiKey || typeof apiKey !== 'string') return false

  const trimmed = apiKey.trim()

  switch (provider) {
    case 'anthropic':
      // Anthropic keys start with 'sk-ant-'
      return trimmed.startsWith('sk-ant-') && trimmed.length > 20
    case 'openai':
      // OpenAI keys start with 'sk-'
      return trimmed.startsWith('sk-') && trimmed.length > 20
    case 'google':
      // Google API keys are typically 39 characters
      return trimmed.length >= 30
    default:
      return trimmed.length > 10
  }
}

/**
 * Validate email format
 */
export function isValidEmail(email: string): boolean {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
  return emailRegex.test(email)
}

/**
 * Validate that a value is a positive number
 */
export function isPositiveNumber(value: unknown): value is number {
  return typeof value === 'number' && !isNaN(value) && value > 0
}

/**
 * Validate frame rate
 */
export function isValidFrameRate(frameRate: number): boolean {
  const validRates = [23.976, 24, 25, 29.97, 30, 50, 59.94, 60]
  return validRates.some((rate) => Math.abs(rate - frameRate) < 0.01)
}

/**
 * Validate clip selection
 */
export function isValidClipSelection(clip: {
  id: string
  startTime: number
  endTime: number
}): boolean {
  return (
    typeof clip.id === 'string' &&
    clip.id.length > 0 &&
    isValidTimeRange(clip.startTime, clip.endTime)
  )
}

/**
 * Sanitize string input
 */
export function sanitizeInput(input: string): string {
  return input
    .trim()
    .replace(/[<>]/g, '') // Remove potential HTML tags
    .slice(0, 1000) // Limit length
}

/**
 * Validate URL
 */
export function isValidUrl(url: string): boolean {
  try {
    new URL(url)
    return true
  } catch {
    return false
  }
}

