   185→
   186→export async function selectClipsByTimeRange(
   187→  startTime: number,
   188→  endTime: number,
   189→  trackIndices?: number[]
   190→): Promise<boolean> {
   191→  try {
   192→    const sequence = app?.project?.activeSequence
   193→    if (!sequence) {
   194→      logger.warn('No active sequence for selection')
   195→      return false
   196→    }
   197→
   198→    logger.info('Selecting clips by time range', { startTime, endTime, trackIndices })
   199→
   200→    let selectedCount = 0
   201→
   202→    // Helper to check if clip overlaps with time range
   203→    const clipOverlapsRange = (clip: { start?: number; end?: number; startTime?: number; endTime?: number }) => {
   204→      const clipStart = clip.start ?? clip.startTime ?? 0
   205→      const clipEnd = clip.end ?? clip.endTime ?? 0
   206→      // Clip overlaps if it's not completely before or after the range
   207→      return clipEnd > startTime && clipStart < endTime
   208→    }
   209→
   210→    // Process video tracks
   211→    if (sequence.videoTracks) {
   212→      for (let trackIdx = 0; trackIdx < sequence.videoTracks.length; trackIdx++) {
   213→        // Skip if trackIndices specified and this track isn't included
   214→        if (trackIndices && !trackIndices.includes(trackIdx)) continue
   215→
   216→        const track = sequence.videoTracks[trackIdx]
   217→        if (track.clips) {
   218→          for (const clip of track.clips) {
   219→            if (clipOverlapsRange(clip)) {
   220→              // Try to select the clip using UXP API
   221→              if (typeof clip.setSelected === 'function') {
   222→                clip.setSelected(true)
   223→                selectedCount++
   224→              } else if ('selected' in clip) {
   225→                // Direct property assignment fallback
   226→                ;(clip as { selected: boolean }).selected = true
   227→                selectedCount++
   228→              }
   229→            }
   230→          }
   231→        }
   232→      }
   233→    }
   234→
   235→    // Process audio tracks
   236→    if (sequence.audioTracks) {
   237→      for (let trackIdx = 0; trackIdx < sequence.audioTracks.length; trackIdx++) {
   238→        // Skip if trackIndices specified and this track isn't included
   239→        if (trackIndices && !trackIndices.includes(trackIdx)) continue
   240→
   241→        const track = sequence.audioTracks[trackIdx]
   242→        if (track.clips) {
   243→          for (const clip of track.clips) {
   244→            if (clipOverlapsRange(clip)) {
   245→              if (typeof clip.setSelected === 'function') {
   246→                clip.setSelected(true)
   247→                selectedCount++
   248→              } else if ('selected' in clip) {
   249→                ;(clip as { selected: boolean }).selected = true
   250→                selectedCount++
   251→              }
   252→            }
   253→          }
   254→        }
   255→      }
   256→    }
   257→
   258→    logger.info('Selected clips by time range', { selectedCount })
   259→    return selectedCount > 0
   260→  } catch (error) {
   261→    logger.error('Failed to select clips by time range', error)
   262→    return false
   263→  }
