   269→): Promise<boolean> {
   270→  try {
   271→    const sequence = app?.project?.activeSequence
   272→    if (!sequence) {
   273→      logger.warn('No active sequence for track selection')
   274→      return false
   275→    }
   276→
   277→    const tracks = trackType === 'video' ? sequence.videoTracks : sequence.audioTracks
   278→    if (!tracks || trackIndex >= tracks.length) {
   279→      logger.warn('Track not found', { trackIndex, trackType, availableTracks: tracks?.length ?? 0 })
   280→      return false
   281→    }
   282→
   283→    const track = tracks[trackIndex]
   284→    if (!track.clips || track.clips.length === 0) {
   285→      logger.warn('No clips in track', { trackIndex, trackType })
   286→      return false
   287→    }
   288→
   289→    let selectedCount = 0
   290→
   291→    // Select all clips in the track
   292→    for (const clip of track.clips) {
   293→      try {
   294→        if (typeof clip.setSelected === 'function') {
   295→          clip.setSelected(true)
   296→          selectedCount++
   297→        } else if ('selected' in clip) {
   298→          ;(clip as { selected: boolean }).selected = true
   299→          selectedCount++
   300→        }
   301→      } catch (clipError) {
   302→        logger.warn('Failed to select clip', clipError)
   303→      }
   304→    }
   305→
   306→    logger.info('Selected all clips in track', {
   307→      trackIndex,
   308→      trackType,
   309→      clipCount: track.clips.length,
   310→      selectedCount,
   311→    })
   312→
   313→    return selectedCount > 0
   314→  } catch (error) {
   315→    logger.error('Failed to select clips in track', error)
   316→    return false
   317→  }
   318→}
   319→
   320→export async function getSelectionState(): Promise<SelectionState> {
   321→  try {
   322→    const sequence = await getActiveSequence()
   323→    if (!sequence) {
   324→      return { clips: [], markers: [], tracks: [] }
   325→    }
   326→
   327→    const selectedClips: PremiereClip[] = []
   328→
   329→    for (const track of [...sequence.videoTracks, ...sequence.audioTracks]) {
   330→      selectedClips.push(...track.clips.filter((c) => c.isSelected))
   331→    }
   332→
   333→    return {
   334→      clips: selectedClips,
   335→      markers: [], // Would be populated from actual API
   336→      tracks: [],
   337→    }
   338→  } catch (error) {
   339→    logger.error('Failed to get selection state', error)
   340→    return { clips: [], markers: [], tracks: [] }
   341→  }
   342→}
   343→
   344→// Helper function to map a single clip from UXP API to our type
   345→function mapClip(clip: unknown, isSelected: boolean): PremiereClip {
   346→  const c = clip as Record<string, unknown>
   347→  const startTime = Number(c.start ?? c.startTime ?? 0)
   348→  const endTime = Number(c.end ?? c.endTime ?? 0)
   349→
   350→  // Try to get media path from projectItem if available
   351→  let mediaPath = ''
   352→  try {
   353→    const projectItem = c.projectItem as { getMediaPath?: () => string } | undefined
   354→    if (projectItem?.getMediaPath) {
   355→      mediaPath = projectItem.getMediaPath()
   356→    } else if (c.mediaPath) {
   357→      mediaPath = String(c.mediaPath)
   358→    }
   359→  } catch {
   360→    // Ignore errors getting media path
   361→  }
   362→
   363→  return {
   364→    id: String(c.nodeId ?? c.id ?? `clip-${Math.random().toString(36).substr(2, 9)}`),
   365→    name: String(c.name ?? 'Untitled Clip'),
   366→    startTime,
   367→    endTime,
   368→    duration: endTime - startTime,
   369→    inPoint: Number(c.inPoint ?? 0),
   370→    outPoint: Number(c.outPoint ?? endTime - startTime),
   371→    mediaPath,
   372→    isSelected,
   373→  }
   374→}
   375→
   376→// Helper function to map a track from UXP API to our type
   377→function mapTrack(track: unknown, index: number): PremiereTrack {
   378→  const t = track as Record<string, unknown>
   379→  const clips: PremiereClip[] = []
   380→
   381→  // Try to access clips from the track
   382→  // UXP API uses 'clips' property on tracks
   383→  const trackClips = t.clips as unknown[] | undefined
   384→
   385→  if (trackClips && Array.isArray(trackClips)) {
   386→    for (let i = 0; i < trackClips.length; i++) {
   387→      try {
   388→        const clip = trackClips[i] as Record<string, unknown>
   389→        // Check if clip is selected - UXP uses isSelected() method or selected property
