     1→// Main Plugin Application Component
     2→import { LitElement, html, css } from 'lit'
     3→import { customElement, state } from 'lit/decorators.js'
     4→import type { ClipSelection, PricingPlan } from '@/types/plugin'
     5→import { getPluginState, getRemainingOperations } from '@/utils/api/plugin'
     6→import { getSelectedClips } from '@/utils/api/premiere'
     7→import { logger } from '@/utils/logger'
     8→
     9→import '../common/Header'
    10→import '../common/SelectionPanel'
    11→import '../common/AIToolsPanel'
    12→import '../common/SettingsPanel'
    13→import '../common/UpgradePrompt'
    14→
    15→type TabId = 'selection' | 'ai-tools' | 'settings'
    16→
    17→@customElement('plugin-app')
    18→export class PluginApp extends LitElement {
    19→  static styles = css`
    20→    :host {
    21→      display: flex;
