    50→    }
    51→
    52→    .operations-count {
    53→      display: flex;
    54→      align-items: center;
    55→      gap: var(--space-xs);
    56→    }
    57→
    58→    .operations-count.low {
    59→      color: var(--color-warning);
    60→    }
    61→
    62→    .operations-count.depleted {
    63→      color: var(--color-negative);
    64→    }
    65→  `
    66→
    67→  @state() private activeTab: TabId = 'selection'
    68→  @state() private selectedClips: ClipSelection[] = []
    69→  @state() private currentPlan: PricingPlan = 'free'
    70→  @state() private remainingOperations = 0
    71→  @state() private isLoading = false
    72→  @state() private showUpgradePrompt = false
    73→
    74→  connectedCallback(): void {
    75→    super.connectedCallback()
    76→    this.initializeState()
    77→  }
    78→
    79→  private async initializeState(): Promise<void> {
    80→    try {
    81→      const state = getPluginState()
    82→      // License info available via getUserLicense() for future premium features
    83→
    84→      this.currentPlan = state.currentPlan
    85→      this.remainingOperations = getRemainingOperations()
    86→
    87→      // Get initial selection
    88→      await this.refreshSelection()
    89→    } catch (error) {
    90→      logger.error('Failed to initialize app state', error)
    91→    }
    92→  }
    93→
    94→  private async refreshSelection(): Promise<void> {
    95→    this.isLoading = true
    96→    try {
    97→      this.selectedClips = await getSelectedClips()
    98→    } finally {
    99→      this.isLoading = false
   100→    }
   101→  }
   102→
   103→  private handleTabChange(tab: TabId): void {
   104→    this.activeTab = tab
   105→  }
   106→
   107→  private handleUpgradeClick(): void {
   108→    this.showUpgradePrompt = true
   109→  }
   110→
   111→  private handleUpgradeClose(): void {
   112→    this.showUpgradePrompt = false
   113→  }
   114→
   115→  private getOperationsClass(): string {
   116→    if (this.remainingOperations === 0) return 'operations-count depleted'
   117→    if (this.remainingOperations <= 3) return 'operations-count low'
   118→    return 'operations-count'
   119→  }
   120→
   121→  render() {
