   156→        ${this.currentPlan === 'free'
   157→          ? html`
   158→              <span class=${this.getOperationsClass()}>
   159→                ${this.remainingOperations} operations remaining
   160→              </span>
   161→            `
   162→          : html`<span class="premium-badge">PRO</span>`}
   163→      </div>
   164→
   165→      ${this.showUpgradePrompt
   166→        ? html`
   167→            <upgrade-prompt
   168→              .currentPlan=${this.currentPlan}
   169→              @close=${this.handleUpgradeClose}
