"use client";

import React, {
  createContext,
  useContext,
  useState,
  useEffect,
  useCallback,
  useRef,
  ReactNode,
} from "react";
import { getCsrfToken, clearCsrfToken, CSRF_HEADER_NAME } from "@/lib/csrf";

// Constants for token refresh timing
const MIN_REFRESH_INTERVAL_MS = 60 * 1000; // Minimum 1 minute between refresh attempts
const AUTH_CHECK_INTERVAL_MS = 60 * 1000; // Check auth status every minute

// Cookie name for checking auth status (non-HttpOnly cookie set by API)
const AUTH_STATUS_COOKIE = "splice_auth_status";
const USER_DATA_COOKIE = "splice_user";

// Types
interface UserData {
  customerId: string;
  tier: string;
  hoursRemaining: number;
  musicCredits?: number;
  variationsCredits?: number;
  sceneAwareCredits?: number;
}

interface AuthState {
  isAuthenticated: boolean;
  isLoading: boolean;
  user: UserData | null;
}

interface LoginResult {
  success: boolean;
  error?: string;
  message?: string;
}

interface AuthContextType extends AuthState {
  login: (licenseKey: string) => Promise<LoginResult>;
  logout: () => Promise<void>;
  refreshAuth: () => Promise<boolean>;
  checkAuth: () => Promise<boolean>;
}

interface MeResponse {
  authenticated: boolean;
  user?: UserData;
  error?: string;
  message?: string;
}

// Create context with undefined default
const AuthContext = createContext<AuthContextType | undefined>(undefined);

/**
 * Get cookie value by name
 */
function getCookie(name: string): string | null {
  if (typeof document === "undefined") return null;

  const value = `; ${document.cookie}`;
  const parts = value.split(`; ${name}=`);
  if (parts.length === 2) {
    return parts.pop()?.split(";").shift() || null;
  }
  return null;
}

/**
 * Check if auth status cookie exists
 */
function hasAuthStatusCookie(): boolean {
  return getCookie(AUTH_STATUS_COOKIE) === "authenticated";
}

/**
 * Get user data from cookie
 */
function getUserDataFromCookie(): UserData | null {
  const userCookie = getCookie(USER_DATA_COOKIE);
  if (!userCookie) return null;

  try {
    return JSON.parse(decodeURIComponent(userCookie));
  } catch {
    return null;
  }
}

/**
 * AuthProvider component
 * Uses HttpOnly cookies for secure token storage
 */
export function AuthProvider({ children }: { children: ReactNode }) {
  const [authState, setAuthState] = useState<AuthState>({
    isAuthenticated: false,
    isLoading: true,
    user: null,
  });

  // Refs for managing refresh timer
  const checkIntervalRef = useRef<NodeJS.Timeout | null>(null);
  const lastCheckAttemptRef = useRef<number>(0);
  const isCheckingRef = useRef<boolean>(false);

  /**
   * Clear all timers
   */
  const clearTimers = useCallback(() => {
    if (checkIntervalRef.current) {
      clearInterval(checkIntervalRef.current);
      checkIntervalRef.current = null;
    }
  }, []);

  /**
   * Check authentication status with the server
   * This verifies the HttpOnly cookie is valid and refreshes if needed
   */
  const checkAuth = useCallback(async (): Promise<boolean> => {
    // Prevent concurrent check attempts
    if (isCheckingRef.current) {
      console.log("[AuthContext] Auth check already in progress, skipping");
      return authState.isAuthenticated;
    }

    // Rate limit check attempts
    const now = Date.now();
    if (now - lastCheckAttemptRef.current < MIN_REFRESH_INTERVAL_MS) {
      console.log("[AuthContext] Auth check rate limited");
      return authState.isAuthenticated;
    }

    isCheckingRef.current = true;
    lastCheckAttemptRef.current = now;

    try {
      const response = await fetch("/api/auth/me", {
        method: "GET",
        credentials: "include", // Important for cookie handling
      });

      const data: MeResponse = await response.json();

      if (data.authenticated && data.user) {
        setAuthState({
          isAuthenticated: true,
          isLoading: false,
          user: data.user,
        });
        return true;
      } else {
        setAuthState({
          isAuthenticated: false,
          isLoading: false,
          user: null,
        });
        return false;
      }
    } catch (error) {
      console.error("[AuthContext] Auth check error:", error);
      return false;
    } finally {
      isCheckingRef.current = false;
    }
  }, [authState.isAuthenticated]);

  /**
   * Refresh authentication (alias for checkAuth)
   * The /api/auth/me endpoint handles token refresh internally
   */
  const refreshAuth = useCallback(async (): Promise<boolean> => {
    return checkAuth();
  }, [checkAuth]);

  /**
   * Handle user login
   * Includes CSRF token for protection against cross-site login attacks
   */
  const login = useCallback(async (licenseKey: string): Promise<LoginResult> => {
    try {
      // Get CSRF token before making login request
      const csrfToken = await getCsrfToken();

      const response = await fetch("/api/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          [CSRF_HEADER_NAME]: csrfToken,
        },
        credentials: "include", // Important for cookie handling
        body: JSON.stringify({ licenseKey }),
      });

      const data = await response.json();

      if (!response.ok || !data.success) {
        return {
          success: false,
          error: data.error,
          message: data.message || data.error || "Authentication failed",
        };
      }

      // Update state with user data from response
      // Tokens are stored in HttpOnly cookies automatically
      setAuthState({
        isAuthenticated: true,
        isLoading: false,
        user: {
          customerId: data.customerId,
          tier: data.tier,
          hoursRemaining: data.hoursRemaining,
        },
      });

      console.log("[AuthContext] Login successful");

      return { success: true };
    } catch (error) {
      console.error("[AuthContext] Login error:", error);
      return {
        success: false,
        error: "Login failed",
        message: "Unable to connect to the server. Please try again.",
      };
    }
  }, []);

  /**
   * Handle user logout
   * Includes CSRF token for protection against forced logout attacks
   */
  const logout = useCallback(async () => {
    // Clear timers first
    clearTimers();

    try {
      // Get CSRF token before making logout request
      const csrfToken = await getCsrfToken();

      await fetch("/api/auth/logout", {
        method: "POST",
        headers: {
          [CSRF_HEADER_NAME]: csrfToken,
        },
        credentials: "include", // Important for cookie handling
      });
    } catch (error) {
      // Ignore logout errors - we'll clear local state anyway
      console.warn("[AuthContext] Logout API call failed:", error);
    }

    // Clear CSRF token cache on logout
    clearCsrfToken();

    // Clear state
    setAuthState({
      isAuthenticated: false,
      isLoading: false,
      user: null,
    });

    console.log("[AuthContext] Logged out");
  }, [clearTimers]);

  /**
   * Initialize auth state on mount
   */
  useEffect(() => {
    const initAuth = async () => {
      // Quick check using non-HttpOnly cookie first
      if (hasAuthStatusCookie()) {
        // Cookie exists, try to get user data from cookie for immediate UI
        const userData = getUserDataFromCookie();
        if (userData) {
          setAuthState({
            isAuthenticated: true,
            isLoading: false,
            user: userData,
          });
        }

        // Then verify with server (this will update if needed)
        await checkAuth();
      } else {
        // No auth cookie, definitely not authenticated
        setAuthState({
          isAuthenticated: false,
          isLoading: false,
          user: null,
        });
      }
    };

    initAuth();

    // Set up periodic check for auth validity (handles cookie expiry, etc.)
    checkIntervalRef.current = setInterval(() => {
      // Only check if we think we're authenticated
      if (hasAuthStatusCookie()) {
        checkAuth();
      } else if (authState.isAuthenticated) {
        // Cookie was cleared externally
        setAuthState({
          isAuthenticated: false,
          isLoading: false,
          user: null,
        });
      }
    }, AUTH_CHECK_INTERVAL_MS);

    // Cleanup on unmount
    return () => {
      clearTimers();
    };
  }, [checkAuth, clearTimers, authState.isAuthenticated]);

  // Handle visibility change (tab focus)
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (document.visibilityState === "visible") {
        // When tab becomes visible, verify auth status
        if (hasAuthStatusCookie()) {
          checkAuth();
        } else if (authState.isAuthenticated) {
          // Cookie was cleared while tab was hidden
          setAuthState({
            isAuthenticated: false,
            isLoading: false,
            user: null,
          });
        }
      }
    };

    document.addEventListener("visibilitychange", handleVisibilityChange);

    return () => {
      document.removeEventListener("visibilitychange", handleVisibilityChange);
    };
  }, [checkAuth, authState.isAuthenticated]);

  const contextValue: AuthContextType = {
    ...authState,
    login,
    logout,
    refreshAuth,
    checkAuth,
  };

  return (
    <AuthContext.Provider value={contextValue}>{children}</AuthContext.Provider>
  );
}

/**
 * Hook to use auth context
 */
export function useAuth(): AuthContextType {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
}

/**
 * Hook to check if user is authenticated
 * Simplified version that just returns auth status
 */
export function useIsAuthenticated(): {
  isAuthenticated: boolean;
  isLoading: boolean;
  user: UserData | null;
} {
  const { isAuthenticated, isLoading, user } = useAuth();
  return { isAuthenticated, isLoading, user };
}

export default AuthContext;

