"use client";

import { useState, Suspense } from "react";
import { useRouter, useSearchParams } from "next/navigation";
import Link from "next/link";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Header } from "@/components/landing/Header";
import { Footer } from "@/components/landing/Footer";
import { motion } from "framer-motion";
import { AlertCircle, CheckCircle } from "lucide-react";
import { useAuth } from "@/context/AuthContext";

function LoginForm() {
    const router = useRouter();
    const searchParams = useSearchParams();
    const redirectTo = searchParams.get("redirect") || "/dashboard";
    const { login } = useAuth();
    const [email, setEmail] = useState("");
    const [licenseKey, setLicenseKey] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const [success, setSuccess] = useState(false);

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();
        setIsLoading(true);
        setError(null);
        setSuccess(false);

        try {
            // Use AuthContext login which handles the API call and cookie management
            // The API sets HttpOnly cookies for tokens and non-HttpOnly cookies for auth status
            const result = await login(licenseKey);

            if (!result.success) {
                setError(result.message || result.error || "Authentication failed. Please try again.");
                setIsLoading(false);
                return;
            }

            // Show success state briefly before redirect
            setSuccess(true);
            setIsLoading(false);

            // Redirect to dashboard after a short delay
            setTimeout(() => {
                router.push(redirectTo);
            }, 1000);
        } catch (err) {
            console.error("Login error:", err);
            setError("Unable to connect to the server. Please check your connection and try again.");
            setIsLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-black text-white flex flex-col font-sans">
            <Header />

            <main className="flex-grow flex items-center justify-center pt-24 pb-12 px-4 relative overflow-hidden">
                {/* Background Gradients */}
                <div className="absolute top-0 left-1/4 w-[500px] h-[500px] bg-primary/20 rounded-full blur-[120px] pointer-events-none" />
                <div className="absolute bottom-0 right-1/4 w-[500px] h-[500px] bg-purple-500/10 rounded-full blur-[120px] pointer-events-none" />

                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="w-full max-w-md space-y-8 relative z-10"
                >
                    <div className="text-center space-y-2">
                        <h1 className="text-3xl font-bold tracking-tight">Welcome Back</h1>
                        <p className="text-muted-foreground">Enter your license key to manage your subscription.</p>
                    </div>

                    <div className="glass p-8 rounded-2xl border border-white/10 shadow-2xl backdrop-blur-xl">
                        <form onSubmit={handleLogin} className="space-y-6">
                            {/* Error Message */}
                            {error && (
                                <motion.div
                                    initial={{ opacity: 0, y: -10 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    className="flex items-start gap-3 p-4 rounded-lg bg-red-500/10 border border-red-500/20"
                                >
                                    <AlertCircle className="w-5 h-5 text-red-500 shrink-0 mt-0.5" />
                                    <p className="text-sm text-red-400">{error}</p>
                                </motion.div>
                            )}

                            {/* Success Message */}
                            {success && (
                                <motion.div
                                    initial={{ opacity: 0, y: -10 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    className="flex items-start gap-3 p-4 rounded-lg bg-green-500/10 border border-green-500/20"
                                >
                                    <CheckCircle className="w-5 h-5 text-green-500 shrink-0 mt-0.5" />
                                    <p className="text-sm text-green-400">Login successful! Redirecting...</p>
                                </motion.div>
                            )}

                            <div className="space-y-2">
                                <label htmlFor="email" className="text-sm font-medium text-gray-300">Email</label>
                                <Input
                                    id="email"
                                    type="email"
                                    placeholder="you@example.com"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    className="bg-black/40 border-white/10 text-white placeholder:text-gray-500 focus:border-primary/50"
                                    required
                                    disabled={isLoading || success}
                                />
                            </div>

                            <div className="space-y-2">
                                <label htmlFor="license" className="text-sm font-medium text-gray-300">License Key</label>
                                <Input
                                    id="license"
                                    type="text"
                                    placeholder="SPLICE-XXXX-XXXX"
                                    value={licenseKey}
                                    onChange={(e) => setLicenseKey(e.target.value)}
                                    className="bg-black/40 border-white/10 text-white placeholder:text-gray-500 focus:border-primary/50"
                                    required
                                    disabled={isLoading || success}
                                />
                            </div>

                            <Button
                                type="submit"
                                className="w-full bg-primary hover:bg-primary/90 text-black font-semibold h-11"
                                disabled={isLoading || success}
                            >
                                {isLoading ? "Verifying..." : success ? "Success!" : "Access Dashboard"}
                            </Button>
                        </form>

                        <div className="mt-6 text-center text-sm text-gray-400">
                            <p>Don&apos;t have a license? <Link href="/#pricing" className="text-primary hover:underline">Get SPLICE</Link></p>
                        </div>
                    </div>
                </motion.div>
            </main>

            <Footer />
        </div>
    );
}

export default function LoginPage() {
    return (
        <Suspense fallback={
            <div className="min-h-screen bg-black text-white flex items-center justify-center">
                <div className="animate-pulse">Loading...</div>
            </div>
        }>
            <LoginForm />
        </Suspense>
    );
}

