// Music credits per tier
// Matches new pricing: Starter $15/mo, Pro $39/mo, Team $129/mo
const TIER_MUSIC_CREDITS = {
  starter: 2,    // 2 songs/month
  pro: 10,       // 10 songs/month
  team: 50,      // 50 songs/month
  cancelled: 0
};

// Feature access by tier - defines which features each tier can use
const TIER_FEATURES = {
  starter: ['silence_removal', 'take_detection', 'basic_captions', 'filler_word', 'caption_export'],
  pro: ['silence_removal', 'take_detection', 'basic_captions', 'filler_word', 'caption_export',
    'vocal_isolation', 'social_reframe', 'text_editing', 'all_captions',
    'profanity_detection', 'chapter_detection'],
  team: ['silence_removal', 'take_detection', 'basic_captions', 'filler_word', 'caption_export',
    'vocal_isolation', 'social_reframe', 'text_editing', 'all_captions',
    'profanity_detection', 'chapter_detection', 'multitrack',
    'music_to_cut', 'mood_timeline', 'youtube_metadata', 'profanity_bleeping'],
  cancelled: []
};

/**
 * Check if a tier has access to a specific feature
 * @param {string} tier - User's tier (starter, pro, team)
 * @param {string} feature - Feature name to check
 * @returns {boolean}
 */
function hasFeatureAccess(tier, feature) {
  const tierFeatures = TIER_FEATURES[tier] || TIER_FEATURES.starter;
  return tierFeatures.includes(feature);
}

// Flat overage rate for music (simplified pricing)
const MUSIC_OVERAGE_RATE = 1.00; // $1/song for all tiers

// Legacy overage rates (kept for backwards compatibility)
const MUSIC_OVERAGE_RATES = {
  starter: 1.00,  // $1.00/song
  pro: 1.00,      // $1.00/song
  team: 1.00      // $1.00/song
};

/**
 * Get music credits balance for a customer
 * @param {string} stripeCustomerId - Stripe customer ID
 * @returns {Promise<{remaining: number, total: number, tier: string}>}
 */
async function getMusicCredits(stripeCustomerId) {
  const result = await pool.query(
    `SELECT music_credits_remaining, music_credits_total, tier
     FROM users
     WHERE stripe_customer_id = $1`,
    [stripeCustomerId]
  );

  if (result.rows.length === 0) {
    return { remaining: 0, total: 0, tier: 'starter' };
  }

  const row = result.rows[0];
  return {
    remaining: row.music_credits_remaining || 0,
    total: row.music_credits_total || TIER_MUSIC_CREDITS[row.tier] || 0,
    tier: row.tier || 'starter'
  };
}

/**
 * Check if customer has music credits available
 * @param {string} stripeCustomerId - Stripe customer ID
 * @returns {Promise<boolean>}
 */
async function hasMusicCredits(stripeCustomerId) {
  const credits = await getMusicCredits(stripeCustomerId);
  return credits.remaining > 0;
}

/**
 * Deduct multiple music credits atomically from customer
 * Uses SELECT...FOR UPDATE to prevent race conditions
 * @param {string} stripeCustomerId - Stripe customer ID
 * @param {number} count - Number of credits to deduct (default: 1)
 * @returns {Promise<{success: boolean, remaining: number, charged: number, creditsDeducted: number}>}
 */
async function deductMusicCredits(stripeCustomerId, count = 1) {
  if (count < 1) {
    return { success: false, remaining: 0, charged: 0, creditsDeducted: 0, error: 'Invalid credit count' };
  }

  const client = await pool.connect();
  try {
    await client.query('BEGIN');

    // Lock row for update - prevents concurrent modifications
    const result = await client.query(
      `SELECT id, music_credits_remaining, tier
       FROM users
       WHERE stripe_customer_id = $1
       FOR UPDATE`,
      [stripeCustomerId]
    );

    if (result.rows.length === 0) {
      await client.query('ROLLBACK');
      return { success: false, remaining: 0, charged: 0, creditsDeducted: 0, error: 'User not found' };
    }

    const { id: _userId, music_credits_remaining, tier: _tier } = result.rows[0];
    const currentCredits = music_credits_remaining || 0;

    let charged = 0;
    let creditsDeducted = 0;
    let overageCount = 0;

    if (currentCredits >= count) {
      // Have enough credits - deduct all from included
      creditsDeducted = count;
      await client.query(
        `UPDATE users
         SET music_credits_remaining = music_credits_remaining - $2,
             updated_at = CURRENT_TIMESTAMP
         WHERE stripe_customer_id = $1`,
        [stripeCustomerId, count]
      );
    } else if (currentCredits > 0) {
      // Partial credits - deduct what they have, charge overage for the rest
      creditsDeducted = currentCredits;
      overageCount = count - currentCredits;
      charged = overageCount * MUSIC_OVERAGE_RATE;

      await client.query(
        `UPDATE users
         SET music_credits_remaining = 0,
             updated_at = CURRENT_TIMESTAMP
         WHERE stripe_customer_id = $1`,
        [stripeCustomerId]
      );

      // Report overage to Stripe meter for billing
      if (overageCount > 0) {
        const usageReport = await reportMusicOverage(stripeCustomerId, overageCount);
        if (usageReport.success) {
          console.log(`[SPLICE] Music overage reported to Stripe: ${stripeCustomerId} - ${overageCount} songs ($${charged.toFixed(2)})`);
        } else {
          console.warn(`[SPLICE] Music overage billing failed: ${usageReport.error}`);
        }
      }

      console.log(`[SPLICE] Partial credit deduction: ${stripeCustomerId} used ${creditsDeducted} credits, ${overageCount} overage ($${charged.toFixed(2)})`);
    } else {
      // No credits - full overage
      overageCount = count;
      charged = count * MUSIC_OVERAGE_RATE;

      // Report overage to Stripe meter for billing
      const usageReport = await reportMusicOverage(stripeCustomerId, count);
      if (usageReport.success) {
        console.log(`[SPLICE] Music overage reported to Stripe: ${stripeCustomerId} - ${count} songs ($${charged.toFixed(2)})`);
      } else {
        console.warn(`[SPLICE] Music overage billing failed: ${usageReport.error}`);
      }

      console.log(`[SPLICE] Full overage: ${stripeCustomerId} charged $${charged.toFixed(2)} for ${count} songs`);
    }

    // Log the usage - single atomic log entry for the batch
    await client.query(
      `INSERT INTO music_usage_log (stripe_customer_id, credits_used, overage_charged)
       VALUES ($1, $2, $3)`,
      [stripeCustomerId, creditsDeducted, charged]
    );

    await client.query('COMMIT');

    // Get final credit balance
    const newCredits = await getMusicCredits(stripeCustomerId);
    return {
      success: true,
      remaining: newCredits.remaining,
      creditsDeducted,
      overageCount,
      charged
    };

  } catch (err) {
    await client.query('ROLLBACK');
    console.error('[SPLICE] Music credits deduction error:', err.message);
    return { success: false, remaining: 0, charged: 0, creditsDeducted: 0, error: err.message };
  } finally {
    client.release();
  }
}

/**
 * Deduct one music credit from customer
 * @param {string} stripeCustomerId - Stripe customer ID
 * @returns {Promise<{success: boolean, remaining: number, charged: number}>}
 */
async function deductMusicCredit(stripeCustomerId) {
  const client = await pool.connect();
  try {
    await client.query('BEGIN');

    // Lock row for update
    const result = await client.query(
      `SELECT music_credits_remaining, tier
       FROM users
       WHERE stripe_customer_id = $1
       FOR UPDATE`,
      [stripeCustomerId]
    );

    if (result.rows.length === 0) {
      await client.query('ROLLBACK');
      return { success: false, remaining: 0, charged: 0, error: 'User not found' };
    }

    const { music_credits_remaining } = result.rows[0];
    let charged = 0;

    if (music_credits_remaining > 0) {
      // Deduct from included credits
      await client.query(
        `UPDATE users
         SET music_credits_remaining = music_credits_remaining - 1,
             updated_at = CURRENT_TIMESTAMP
         WHERE stripe_customer_id = $1`,
        [stripeCustomerId]
      );
    } else {
      // Overage - charge flat rate ($1/song)
      charged = MUSIC_OVERAGE_RATE;

      // Report overage to Stripe meter for billing
      const usageReport = await reportMusicOverage(stripeCustomerId, 1);
      if (usageReport.success) {
        console.log(`[SPLICE] Music overage reported to Stripe: ${stripeCustomerId} - $${charged}`);
      } else {
        console.warn(`[SPLICE] Music overage billing failed: ${usageReport.error}`);
      }
    }

    // Log the usage
    await client.query(
      `INSERT INTO music_usage_log (stripe_customer_id, credits_used, overage_charged)
       VALUES ($1, 1, $2)
       ON CONFLICT DO NOTHING`,
      [stripeCustomerId, charged]
    );

    await client.query('COMMIT');

    const newCredits = await getMusicCredits(stripeCustomerId);
    return {
      success: true,
      remaining: newCredits.remaining,
      charged
    };

  } catch (err) {
    await client.query('ROLLBACK');
    console.error('[SPLICE] Music credit deduction error:', err.message);
    return { success: false, remaining: 0, charged: 0, error: err.message };
  } finally {
    client.release();
  }
}

/**
 * Reset music credits based on tier (called on subscription renewal)
 * @param {string} stripeCustomerId - Stripe customer ID
 * @param {string} tier - Subscription tier
 */
async function resetMusicCredits(stripeCustomerId, tier) {
  const credits = TIER_MUSIC_CREDITS[tier] || TIER_MUSIC_CREDITS.starter;
  const client = await pool.connect();

  try {
    await client.query('BEGIN');

    // Lock row for update to prevent race conditions
    const result = await client.query(
      `SELECT id FROM users
       WHERE stripe_customer_id = $1
       FOR UPDATE`,
      [stripeCustomerId]
    );

    if (result.rows.length === 0) {
      await client.query('ROLLBACK');
      console.warn(`[SPLICE] resetMusicCredits: User not found for ${stripeCustomerId}`);
      return;
    }

    await client.query(
      `UPDATE users
       SET music_credits_remaining = $2,
           music_credits_total = $2,
           music_credits_reset_at = CURRENT_TIMESTAMP,
           updated_at = CURRENT_TIMESTAMP
       WHERE stripe_customer_id = $1`,
      [stripeCustomerId, credits]
    );

    await client.query('COMMIT');
  } catch (err) {
    await client.query('ROLLBACK');
    console.error('[SPLICE] Music credit reset error:', err.message);
    throw err;
  } finally {
    client.release();
  }
}

/**
 * Add music credits (for promotional or overage purchases)
 * @param {string} stripeCustomerId - Stripe customer ID
 * @param {number} credits - Number of credits to add
 * @returns {Promise<{success: boolean, newBalance: number}>}
 */
async function addMusicCredits(stripeCustomerId, credits) {
  const client = await pool.connect();
  try {
    await client.query('BEGIN');

    // Lock row for update to prevent concurrent modification
    const lockResult = await client.query(
      `SELECT music_credits_remaining
       FROM users
       WHERE stripe_customer_id = $1
       FOR UPDATE`,
      [stripeCustomerId]
    );

    if (lockResult.rows.length === 0) {
      await client.query('ROLLBACK');
      return { success: false, newBalance: 0, error: 'User not found' };
    }

    // Update the credits
    const result = await client.query(
      `UPDATE users
       SET music_credits_remaining = music_credits_remaining + $2,
           updated_at = CURRENT_TIMESTAMP
       WHERE stripe_customer_id = $1
       RETURNING music_credits_remaining`,
      [stripeCustomerId, credits]
    );

    await client.query('COMMIT');

    return {
      success: true,
      newBalance: result.rows[0].music_credits_remaining
    };

  } catch (err) {
    await client.query('ROLLBACK');
    console.error('[SPLICE] Music credit addition error:', err.message);
    return { success: false, newBalance: 0, error: err.message };
  } finally {
    client.release();
  }
}

/**
 * Get music usage history for a customer
 * @param {string} stripeCustomerId - Stripe customer ID
 * @param {number} limit - Max records to return
 * @returns {Promise<Object[]>}
 */
async function getMusicUsageHistory(stripeCustomerId, limit = 50) {
  const result = await pool.query(
    `SELECT * FROM music_usage_log
     WHERE stripe_customer_id = $1
     ORDER BY created_at DESC
     LIMIT $2`,
    [stripeCustomerId, limit]
  );
  return result.rows;
}

/**
 * Initialize music credits columns (migration helper)
 */
async function initMusicCreditsColumns() {
  const client = await pool.connect();
  try {
    // Add music credits columns if they don't exist
    // CRITICAL-003: Default music credits should be 2 (Starter tier), not 5
    await client.query(`
      DO $$
      BEGIN
        IF NOT EXISTS (SELECT 1 FROM information_schema.columns WHERE table_name = 'users' AND column_name = 'music_credits_remaining') THEN
          ALTER TABLE users ADD COLUMN music_credits_remaining INTEGER DEFAULT 2;
        END IF;
        IF NOT EXISTS (SELECT 1 FROM information_schema.columns WHERE table_name = 'users' AND column_name = 'music_credits_total') THEN
          ALTER TABLE users ADD COLUMN music_credits_total INTEGER DEFAULT 2;
        END IF;
        IF NOT EXISTS (SELECT 1 FROM information_schema.columns WHERE table_name = 'users' AND column_name = 'music_credits_reset_at') THEN
          ALTER TABLE users ADD COLUMN music_credits_reset_at TIMESTAMP;
        END IF;
      END $$;
    `);

    // Create music usage log table
    await client.query(`
      CREATE TABLE IF NOT EXISTS music_usage_log (
        id SERIAL PRIMARY KEY,
        stripe_customer_id VARCHAR(255) NOT NULL,
        credits_used INTEGER DEFAULT 1,
        overage_charged DECIMAL(10,2) DEFAULT 0,
        created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
      )
    `);

    await client.query(`
      CREATE INDEX IF NOT EXISTS idx_music_usage_customer
      ON music_usage_log(stripe_customer_id)
    `);

    console.log('[SPLICE] Music credits columns initialized');
  } finally {
    client.release();
  }
}

// Variations credits cost (equivalent to 2.5 music credits)
const VARIATIONS_CREDIT_COST = 2.5;

// Overage rates for variations (higher than single music due to 3 parallel generations)
const VARIATIONS_OVERAGE_RATES = {
  starter: 1.25,  // $1.25/variations (2.5x $0.50)
  pro: 0.75,      // $0.75/variations (2.5x $0.30)
  team: 0.50      // $0.50/variations (2.5x $0.20)
};

// Scene-aware music credits cost (1.5 credits for transcript analysis + generation)
const SCENE_AWARE_CREDIT_COST = 1.5;

// Overage rates for scene-aware music (75% margin)
const SCENE_AWARE_OVERAGE_RATES = {
  starter: 0.75,  // $0.75/scene-aware (1.5x $0.50)
  pro: 0.45,      // $0.45/scene-aware (1.5x $0.30)
  team: 0.30      // $0.30/scene-aware (1.5x $0.20)
};

/**
 * Check if customer has enough credits for variations (2.5 credits)
 * @param {string} stripeCustomerId - Stripe customer ID
 * @returns {Promise<{canGenerate: boolean, creditsAvailable: number, creditsRequired: number}>}
 */
async function checkVariationsCredits(stripeCustomerId) {
  const credits = await getMusicCredits(stripeCustomerId);
  const creditsRequired = VARIATIONS_CREDIT_COST;

  // Allow if they have at least 1 credit (we'll charge overage for the rest)
  // Or if they're on a paid tier (overage allowed)
  const canGenerate = credits.remaining > 0 || credits.tier !== 'cancelled';

  return {
    canGenerate,
    creditsAvailable: credits.remaining,
    creditsRequired,
    tier: credits.tier
  };
}

/**
 * Deduct variations credit from customer (2.5 credits worth)
 * @param {string} stripeCustomerId - Stripe customer ID
 * @returns {Promise<{success: boolean, remaining: number, charged: number}>}
 */
async function deductVariationsCredit(stripeCustomerId) {
  const client = await pool.connect();
  try {
    await client.query('BEGIN');

    // Lock row for update
    const result = await client.query(
      `SELECT music_credits_remaining, tier
       FROM users
       WHERE stripe_customer_id = $1
       FOR UPDATE`,
      [stripeCustomerId]
    );

    if (result.rows.length === 0) {
      await client.query('ROLLBACK');
      return { success: false, remaining: 0, charged: 0, error: 'User not found' };
    }

    const { music_credits_remaining, tier } = result.rows[0];
    let charged = 0;
    let creditsDeducted = 0;

    // Determine how many credits to deduct vs overage to charge
    if (music_credits_remaining >= VARIATIONS_CREDIT_COST) {
      // Have enough credits - deduct 2.5 (rounded up to 3 for integer credits)
      creditsDeducted = Math.ceil(VARIATIONS_CREDIT_COST);
      await client.query(
        `UPDATE users
         SET music_credits_remaining = music_credits_remaining - $2,
             updated_at = CURRENT_TIMESTAMP
         WHERE stripe_customer_id = $1`,
        [stripeCustomerId, creditsDeducted]
      );
    } else if (music_credits_remaining > 0) {
      // Partial credits - deduct what they have and charge overage for the rest
      const creditsPortion = music_credits_remaining;
      const overagePortion = VARIATIONS_CREDIT_COST - creditsPortion;
      const overageRate = MUSIC_OVERAGE_RATES[tier] || MUSIC_OVERAGE_RATES.starter;
      charged = overagePortion * overageRate;

      creditsDeducted = creditsPortion;
      await client.query(
        `UPDATE users
         SET music_credits_remaining = 0,
             updated_at = CURRENT_TIMESTAMP
         WHERE stripe_customer_id = $1`,
        [stripeCustomerId]
      );

      console.log(`[SPLICE] Variations partial overage: ${stripeCustomerId} used ${creditsPortion} credits, charged $${charged.toFixed(2)}`);
    } else {
      // No credits - full overage
      charged = VARIATIONS_OVERAGE_RATES[tier] || VARIATIONS_OVERAGE_RATES.starter;
      console.log(`[SPLICE] Variations full overage: ${stripeCustomerId} charged $${charged.toFixed(2)}`);
    }

    // Log the usage
    await client.query(
      `INSERT INTO music_usage_log (stripe_customer_id, credits_used, overage_charged)
       VALUES ($1, $2, $3)
       ON CONFLICT DO NOTHING`,
      [stripeCustomerId, creditsDeducted, charged]
    );

    await client.query('COMMIT');

    const newCredits = await getMusicCredits(stripeCustomerId);
    return {
      success: true,
      remaining: newCredits.remaining,
      creditsDeducted,
      charged
    };

  } catch (err) {
    await client.query('ROLLBACK');
    console.error('[SPLICE] Variations credit deduction error:', err.message);
    return { success: false, remaining: 0, charged: 0, error: err.message };
  } finally {
    client.release();
  }
}

