/**
 * Close the database connection pool
 * Used for graceful shutdown
 */
async function closePool() {
  await pool.end();
}

/**
 * Update trial end date for a customer
 * @param {string} stripeCustomerId - Customer ID
 * @param {number|null} trialEndTimestamp - Unix timestamp of trial end (null to clear)
 */
async function updateTrialEnd(stripeCustomerId, trialEndTimestamp) {
  const trialEnd = trialEndTimestamp ? new Date(trialEndTimestamp * 1000) : null;

  await pool.query(
    `UPDATE users
     SET trial_end = $2, updated_at = CURRENT_TIMESTAMP
     WHERE stripe_customer_id = $1`,
    [stripeCustomerId, trialEnd]
  );

  console.log(`[SPLICE] Updated trial_end for ${stripeCustomerId}: ${trialEnd ? trialEnd.toISOString() : 'null'}`);
}

/**
 * Check database connectivity
 * Used for health checks
 */
async function checkDatabase() {
  const client = await pool.connect();
  try {
    const result = await client.query('SELECT 1 as health');
    return result.rows[0].health === 1;
  } finally {
    client.release();
  }
}

// =============================================================================
// Email Verification Functions
// =============================================================================

/**
 * Mark a user's email as verified
 * @param {string} email - Email address that was verified
 * @returns {Promise<{success: boolean, error?: string}>}
 */
async function markEmailVerified(email) {
  if (!email) {
    return { success: false, error: 'Email is required' };
  }

  const normalizedEmail = email.toLowerCase().trim();

  try {
    const result = await pool.query(
      `UPDATE users
       SET email_verified = TRUE,
           email_verified_at = CURRENT_TIMESTAMP,
           updated_at = CURRENT_TIMESTAMP
       WHERE LOWER(email) = $1
       RETURNING stripe_customer_id, email_verified`,
      [normalizedEmail]
    );

    if (result.rows.length === 0) {
      // No user found with this email - this is OK for new signups
      console.log(`[SPLICE] No user found for email verification: ${normalizedEmail}`);
      return { success: true, userFound: false };
    }

    console.log(`[SPLICE] Email verified for user: ${result.rows[0].stripe_customer_id}`);
    return { success: true, userFound: true, customerId: result.rows[0].stripe_customer_id };
  } catch (err) {
    console.error('[SPLICE] markEmailVerified error:', err.message);
    return { success: false, error: err.message };
  }
}

/**
 * Check if a user's email is verified
 * @param {string} stripeCustomerId - Stripe customer ID
 * @returns {Promise<{verified: boolean, verifiedAt?: Date}>}
 */
async function isEmailVerified(stripeCustomerId) {
  try {
    const result = await pool.query(
      `SELECT email_verified, email_verified_at
       FROM users
       WHERE stripe_customer_id = $1`,
      [stripeCustomerId]
    );

    if (result.rows.length === 0) {
      return { verified: false };
    }

    return {
      verified: result.rows[0].email_verified === true,
      verifiedAt: result.rows[0].email_verified_at
    };
  } catch (err) {
    console.error('[SPLICE] isEmailVerified error:', err.message);
    return { verified: false };
  }
}

/**
 * Get user by email address
 * @param {string} email - Email address
 * @returns {Promise<Object|null>} User object or null
 */
async function getUserByEmail(email) {
  if (!email) return null;

  const normalizedEmail = email.toLowerCase().trim();

  try {
    const result = await pool.query(
      `SELECT stripe_customer_id, email, tier, email_verified, email_verified_at
       FROM users
       WHERE LOWER(email) = $1`,
      [normalizedEmail]
    );

    return result.rows.length > 0 ? result.rows[0] : null;
  } catch (err) {
    console.error('[SPLICE] getUserByEmail error:', err.message);
    return null;
  }
}

