    #status {
      padding: 8px;
      background: #1e1e1e;
      border-radius: 4px;
      font-size: 14px;
      margin-bottom: 16px;
    }
    button {
      background: #0d6efd;
      color: white;
      border: none;
      padding: 8px 16px;
      border-radius: 4px;
      cursor: pointer;
      font-size: 14px;
      width: 100%;
      margin-bottom: 16px;
    }
    button:hover {
      background: #0b5ed7;
    }
    button:disabled {
      background: #6c757d;
      cursor: not-allowed;
    }
    #clips {
      background: #1e1e1e;
      border-radius: 4px;
      padding: 8px;
      font-size: 12px;
      max-height: 300px;
      overflow-y: auto;
    }
    .clip-item {
      padding: 4px 0;
      border-bottom: 1px solid #444;
    }
    .clip-item:last-child {
      border-bottom: none;
    }
    .no-clips {
      color: #888;
      font-style: italic;
    }
  </style>
</head>
<body>
  <h1>SPLICE</h1>
  <p id="status">Plugin loaded!</p>
  <button id="readClipsBtn">Read Timeline Clips</button>
  <div id="clips"></div>

  <script>
    const ppro = require('premierepro');

    document.getElementById('readClipsBtn').addEventListener('click', async () => {
      const statusEl = document.getElementById('status');
      const clipsEl = document.getElementById('clips');
      const btn = document.getElementById('readClipsBtn');

      btn.disabled = true;
      statusEl.textContent = 'Reading clips...';
      clipsEl.innerHTML = '';

      try {
        const app = await ppro.app;
        const project = await app.openProject;

        if (!project) {
          statusEl.textContent = 'No project open';
          btn.disabled = false;
          return;
        }

        const sequence = await project.activeSequence;

        if (!sequence) {
          statusEl.textContent = 'No active sequence';
          btn.disabled = false;
          return;
        }

        const clipNames = [];
        const videoTracks = await sequence.videoTracks;
        const numTracks = await videoTracks.length;

        for (let i = 0; i < numTracks; i++) {
          const track = await videoTracks.getItem(i);
          const clips = await track.clips;
          const numClips = await clips.length;

          for (let j = 0; j < numClips; j++) {
            const clip = await clips.getItem(j);
            const name = await clip.name;
            clipNames.push(name);
          }
        }

        if (clipNames.length === 0) {
          clipsEl.innerHTML = '<div class="no-clips">No clips in timeline</div>';
          statusEl.textContent = 'No clips found';
        } else {
          clipsEl.innerHTML = clipNames
            .map(name => `<div class="clip-item">${name}</div>`)
            .join('');
          statusEl.textContent = `Found ${clipNames.length} clip(s)`;
        }
      } catch (err) {
        statusEl.textContent = `Error: ${err.message}`;
        console.error('SPLICE error:', err);
      }

      btn.disabled = false;
    });
  </script>
</body>
</html>

