// Try to get the project - API may vary
let project = null;

if (ppro.Project) {
  // Try static method pattern
  project = await ppro.Project.getActiveProject();
} else if (ppro.app) {
  const app = typeof ppro.app === 'function' ? await ppro.app() : ppro.app;
  project = app.project || app.openProject || (await app.getProject?.());
}

if (!project) {
  clipsEl.innerHTML += `<div class="clip-item">Could not get project. API: ${pproKeys.join(', ')}</div>`;
  statusEl.textContent = 'Exploring API...';
  btn.disabled = false;
  return;
}

const sequence = await project.activeSequence;

input.new_string:
// Get the active project
const project = await ppro.Project.getActiveProject();

if (!project) {
  clipsEl.innerHTML += `<div class="clip-item">No project open</div>`;
  statusEl.textContent = 'No project open';
  btn.disabled = false;
  return;
}

// Debug: show Project methods
const projectKeys = Object.keys(Object.getPrototypeOf(project)).filter(k => !k.startsWith('_'));
clipsEl.innerHTML += `<div class="clip-item">Project methods: ${projectKeys.slice(0, 10).join(', ')}...</div>`;

// Try different ways to get the active sequence
let sequence = null;
if (typeof project.getActiveSequence === 'function') {
  sequence = await project.getActiveSequence();
} else if (project.activeSequence) {
  sequence = await project.activeSequence;
}

// Also try SequenceUtils if sequence is still null
if (!sequence && ppro.SequenceUtils) {
  const seqUtilKeys = Object.keys(ppro.SequenceUtils);
  clipsEl.innerHTML += `<div class="clip-item">SequenceUtils: ${seqUtilKeys.join(', ')}</div>`;
}

