await log(clipsEl, `Sequence name: ${sequence.name || await sequence.getName?.() || 'unknown'}`);

// Get video tracks
const videoTracks = sequence.videoTracks || await sequence.getVideoTracks?.();
if (!videoTracks) {
  await log(clipsEl, 'No videoTracks property found');
  const seqProto = Object.getPrototypeOf(sequence);
  const seqMethods = Object.getOwnPropertyNames(seqProto);
  await log(clipsEl, `Sequence methods: ${seqMethods.join(', ')}`);
  btn.disabled = false;
  return;
}

const numTracks = videoTracks.length ?? await videoTracks.getLength?.() ?? 0;
await log(clipsEl, `Video tracks: ${numTracks}`);

const clipNames = [];
for (let i = 0; i < numTracks; i++) {
  const track = videoTracks[i] || await videoTracks.getItem?.(i);
  if (!track) continue;

  const clips = track.clips || await track.getClips?.() || await track.getTrackItems?.();
  if (!clips) continue;

  const numClips = clips.length ?? await clips.getLength?.() ?? 0;
  for (let j = 0; j < numClips; j++) {
    const clip = clips[j] || await clips.getItem?.(j);
    if (!clip) continue;
    const name = clip.name || await clip.getName?.() || `Clip ${i}-${j}`;
    clipNames.push(name);
  }
}

